pragma solidity 0.8.3;

interface IKlayYield {
    event Stake(address from, uint256 amount);
    event Unstake(address from, uint256 amount);
    event Claim(address from, uint256 amount);

    /// @notice total staked klay amount to the yield
    function totalStaked() external view returns (uint256);

    /// @notice expected apr of this yield
    function expectedApr() external view returns (uint256);

    /// @notice add klay to cn node
    function stake() external payable;

    /// @notice remove klay from cn node
    /// @dev pending 7 days
    function unstake(uint256 amount) external;

    function transferToken(address to, uint256 amount) external;

    function transferKlay(address to, uint256 amount) external;

    /// @notice claim unstaked usr's klay, send to usr
    function claim() external;

    /// @notice unstake status
    /// @dev pending 7 days
    function pending() external view returns (uint256 completed, uint256 yet);

    /// @notice Convert the generated reward(klay) to ksd and distribute it to the beneficiaries
    function applyProfit() external returns (uint256);
}
