pragma solidity 0.8.3;

import "../interface/klaymore/IAKlay.sol";
import "../library/AccessControl.sol";
import "../library/Pausable.sol";
import "../interface/klayswap/IKlaytnExchangeImpl.sol";
import "../interface/IDistributor.sol";
import "../interface/IKlaySwapBridge.sol";
import "../interface/IAKlayYield.sol";
import "../interface/klaymore/IAKlayStakingPool.sol";
import "../interface/klayswap/IKlaytnFactoryImpl.sol";

/// @title Staking klay to klaytn CN(Consensus Node) Staking Contract (https://github.com/klaytn/klaytn/blob/dev/contracts/cnstaking/CnStakingContract.sol)
/// @notice Independent klay staking strategy unit정
/// @dev The contract communicate with cn staking contract using delegation contract. The delegation contract is an existing external service code called klaystation.
/// @dev Inherited from klaySwapBridge.
contract AKlayYield is IAKlayYield, AccessControl, Pausable {
    IAKlay public aklay;
    IKIP7 public ksd;
    address public yieldFactory;
    IKlaySwapBridge public klayswapBridge;
    address public receiver; // bklay로 설정
    uint256 public override totalStaked;

    IAKlayStakingPool public aklayStakingPool;
    IKlaytnFactory public factory; // klaytnFactory contract

    function __AKlayYield_init() public initializer {
        __Pausable_init();
    }

    // --- config ---
    /// @notice Initialize address variable.
    function config(bytes32 what, address data) external onlyOwner {
        if (what == "aklay") {
            aklay = IAKlay(data);
        } else if (what == "ksd") {
            ksd = IKIP7(data);
        } else if (what == "yieldFactory") {
            yieldFactory = data;
        } else if (what == "klayswapBridge") {
            klayswapBridge = IKlaySwapBridge(data);
        } else if (what == "receiver") {
            receiver = data;
        } else if (what == "aklayStakingPool") {
            aklayStakingPool = IAKlayStakingPool(data);

            uint256 aklayBalance = aklay.balanceOf(address(this));
            aklay.approve(address(aklayStakingPool), aklayBalance);
            aklayStakingPool.stake(address(this), aklayBalance);
        } else if (what == "factory") {
            factory = IKlaytnFactory(data);
        } else revert("AKlayYield::config:unrecognized-param");
    }

    function expectedApr() external view override returns (uint256) {
        return 1;
    }

    // --- functions ---
    /// @notice Add klay to a CN node.
    /// @dev Delegation contract used to stake klay (refer to klaystation/contract/DelegationContract.sol)
    function stake() external payable override onlyAdmin whenNotPaused {
        aklay.stakeKlay{value: msg.value}(address(this));
        aklay.approve(address(aklayStakingPool), msg.value);
        aklayStakingPool.stake(address(this), msg.value);
        totalStaked = totalStaked + msg.value;
        emit Stake(msg.sender, msg.value);
    }

    /// @notice recieve aklay.
    function stakeAKlay(uint256 amount) external override onlyAdmin whenNotPaused {
        aklay.transferFrom(msg.sender, address(this), amount);
        aklay.approve(address(aklayStakingPool), amount);
        aklayStakingPool.stake(address(this), amount);
        totalStaked = totalStaked + amount;
        emit Stake(msg.sender, amount);
    }

    /// @notice Remove klay from a CN node.
    /// @dev It takes 7 days to unstake, only yield factory should be able to call the function
    /// @param amount Amount of unstake klay for unstaking.
    function unstake(uint256 amount) public override onlyAdmin whenNotPaused {
        aklayStakingPool.unstake(address(this), amount);
        aklay.unstakeKlay(address(this), amount);
        totalStaked = totalStaked - amount;
        emit Unstake(msg.sender, amount);
    }

    /// @notice yield에서 운용중인 Token을 인출
    function transferToken(address to, uint256 amount) public override onlyAdmin whenNotPaused {
        aklayStakingPool.unstake(address(this), amount);
        aklay.transfer(to, amount);
        totalStaked = totalStaked - amount;
        emit TransferAKlay(msg.sender, amount);
    }

    /// @notice yield에서 운용중인 Token을 klay로 변환하여 인출
    function transferKlay(address to, uint256 amount) public override onlyAdmin whenNotPaused {
        aklayStakingPool.unstake(address(this), amount);

        // swap aklay to klay
        uint256 klayAmount = address(this).balance;
        aklay.approve(address(factory), amount);
        factory.exchangeKctPos(address(aklay), amount, address(0), 1, new address[](0));
        klayAmount = address(this).balance - klayAmount;

        (bool success, ) = to.call{value: klayAmount}("");
        require(success, "AKlayYield:: transferKlay: transfer overflow");

        totalStaked = totalStaked - amount;
        emit TransferKlay(msg.sender, amount);
    }

    /// @notice Claim the user's unstaked Klay
    function claim() public override {
        aklay.claimKlay(address(this));
        uint256 amount = address(this).balance;
        (bool success, ) = address(receiver).call{value: amount}("");
        require(success, "AKlayYield::claim: transfer to bklay failed");
        emit Claim(msg.sender, amount);
    }

    /// @notice Check the status of unstaking process
    /// @dev It takes 7 days to subtract the staking klay from the node.
    /// @return
    /// completed The amount of klays that can be claimed (i.e., of which unstaking process has been finished)
    /// yet The amount of klays whose unstaking periods are not expired yet
    function pending() external view override returns (uint256 completed, uint256 yet) {
        return aklay.pendingKlay(address(this));
    }

    /// @notice Convert the generated reward(klay) to ksd and distribute it to the beneficiaries.
    /// @dev There are two actions. One is to unstake the generated revenue(surplusKlayAmount), and the other is to distribute the claimable klay among previously unstaked profits to ksd beneficiaries as a reward.
    /// need onlyOwner
    function applyProfit() external override onlyAdmin returns (uint256) {
        uint256 estimatedTotalValue = aklayStakingPool.balanceOfAKlay(address(this));
        uint256 surplusAmount = estimatedTotalValue - totalStaked;

        address[] memory _path = new address[](1); // aklay => klay => ksd;
        _path[0] = address(0);

        if (surplusAmount > 0) {
            uint256 beforeKsd = ksd.balanceOf(address(this));
            aklayStakingPool.unstake(address(this), surplusAmount);
            aklay.approve(address(klayswapBridge), surplusAmount);
            klayswapBridge.exchangeKctPos(address(aklay), surplusAmount, address(ksd), 1, _path);
            uint256 ksdAmount = ksd.balanceOf(address(this)) - beforeKsd;
            ksd.approve(yieldFactory, ksdAmount);
            return ksdAmount;
        }
        return 0;
    }

    receive() external payable {}
}
