pragma solidity ^0.8.3;

import "../library/AccessControl.sol";
import "../library/Pausable.sol";
import "../interface/IAddressBook.sol";

contract AddressBook is IAddressBook, AccessControl, Pausable {
    address public override configurator;
    address public override ledger;
    address public override ksd;
    address public override ksdGateway;

    address public override yieldManager;

    address public override kokoaRewardManager;
    address public override ksdRewardManager;
    address public override ksdRefundManager;

    mapping(bytes32 => CollateralContracts) public override collateralContracts;

    address public override ksp;

    function __AddressBook_init() public initializer {
        __AccessControl_init();
    }

    function config(bytes32 what, address data) external override onlyAdmin {
        if (what == "configurator") configurator = data;
        else if (what == "ledger") ledger = data;
        else if (what == "ksd") ksd = data;
        else if (what == "ksdGateway") ksdGateway = data;
        else if (what == "yieldManager") yieldManager = data;
        else if (what == "kokoaRewardManager") kokoaRewardManager = data;
        else if (what == "ksdRewardManager") ksdRewardManager = data;
        else if (what == "ksdRefundManager") ksdRefundManager = data;
        else if (what == "ksp") ksp = data;
        else revert("AddressBook::config: unrecognized-param");
    }

    function config(
        bytes32 collateralType,
        bytes32 what,
        address data
    ) external override onlyAdmin {
        if (what == "token") collateralContracts[collateralType].token = data;
        else if (what == "bond") collateralContracts[collateralType].bond = data;
        else if (what == "bondAuctionHouse") collateralContracts[collateralType].bondAuctionHouse = data;
        else if (what == "bondFeed") collateralContracts[collateralType].bondFeed = data;
        else if (what == "bondIssuedKsdPool") collateralContracts[collateralType].bondIssuedKsdPool = data;
        else if (what == "bondSchedule") collateralContracts[collateralType].bondSchedule = data;
        else if (what == "gateway") collateralContracts[collateralType].gateway = data;
        else if (what == "yieldFactory") collateralContracts[collateralType].yieldFactory = data;
        else revert("AddressBook::config: unrecognized-param");
    }

    function getCollateralContract(bytes32 collateralType, bytes32 what) external view override returns (address) {
        if (what == "token") return collateralContracts[collateralType].token;
        else if (what == "bond") return collateralContracts[collateralType].bond;
        else if (what == "bondAuctionHouse") return collateralContracts[collateralType].bondAuctionHouse;
        else if (what == "bondFeed") return collateralContracts[collateralType].bondFeed;
        else if (what == "bondIssuedKsdPool") return collateralContracts[collateralType].bondIssuedKsdPool;
        else if (what == "bondSchedule") return collateralContracts[collateralType].bondSchedule;
        else if (what == "gateway") return collateralContracts[collateralType].gateway;
        else if (what == "yieldFactory") return collateralContracts[collateralType].yieldFactory;
        else revert("AddressBook::getCollateralContract: unrecognized-param");
    }
}
