pragma solidity 0.8.3;

import "../../interface/klayswap/IKlaytnExchangeImpl.sol";
import "../../interface/IAddressBook.sol";
import "./Feed.sol";

contract BKSMajorStableLpFeed is Feed {
    IAddressBook public addressBook;
    bytes32 public collateralType;
    address public stableToken;
    uint256 public lastUpdatedAt;
    uint256 public windowSize;

    function __BKSMajorStableLpFeed_init(
        bytes32 collateralType_,
        address addressBook_,
        address stableToken_
    ) public initializer {
        __Feed_init();
        collateralType = collateralType_;
        addressBook = IAddressBook(addressBook_);
        stableToken = stableToken_;
    }

    function config(bytes32 what, uint256 data) external onlyAdmin {
        if (what == "windowSize") windowSize = data;
        else revert("BKSMajorStableLpFeed::config:unrecognized-param");
    }

    function set() external whenNotPaused {
        address lpToken = addressBook.getCollateralContract(collateralType, bytes32("token"));
        address tokenA = IKlaytnExchange(lpToken).tokenA();
        address tokenB = IKlaytnExchange(lpToken).tokenB();
        (uint256 poolA, uint256 poolB) = IKlaytnExchange(lpToken).getCurrentPool();
        uint256 totalSupply = IKlaytnExchange(lpToken).totalSupply();
        uint256 price_ = 0;
        if (stableToken == tokenA) {
            uint256 decimals = IKIP7Detailed(tokenA).decimals();
            uint256 targetDecimals = 36 - decimals;
            price_ = (2 * poolA * (10**targetDecimals)) / totalSupply;
        } else {
            uint256 decimals = IKIP7Detailed(tokenB).decimals();
            uint256 targetDecimals = 36 - decimals;
            price_ = (2 * poolB * (10**targetDecimals)) / totalSupply;
        }

        uint256 diff = block.timestamp - lastUpdatedAt;
        lastUpdatedAt = block.timestamp;
        if (diff >= windowSize) {
            price = price_;
            valid = true;
        } else {
            price = (price * (windowSize - diff)) / windowSize + (price_ * diff) / windowSize;
            valid = true;
        }
    }
}
