pragma solidity ^0.8.3;

/**
 * @dev This is a base contract to aid in writing upgradeable contracts, or any kind of contract that will be deployed
 * behind a proxy. Since a proxied contract can't have a constructor, it's common to move constructor logic to an
 * external initializer function, usually called `initialize`. It then becomes necessary to protect this initializer
 * function so it can only be called once. The {initializer} modifier provided by this contract will have this effect.
 *
 * TIP: To avoid leaving the proxy in an uninitialized state, the initializer function should be called as early as
 * possible by providing the encoded function call as the `_data` argument to {UpgradeableProxy-constructor}.
 *
 * CAUTION: When used with inheritance, manual care must be taken to not invoke a parent initializer twice, or to ensure
 * that all initializers are idempotent. This is not verified automatically as constructors are by Solidity.
 */
abstract contract Initializable {
    /**
     * @dev Indicates that the contract has been initialized.
     */
    bool private _initialized;

    /**
     * @dev Indicates that the contract is in the process of being initialized.
     */
    bool private _initializing;

    /**
     * @dev Modifier to protect an initializer function from being invoked twice.
     */
    modifier initializer() {
        require(_initializing || _isConstructor() || !_initialized, "Initializable: contract is already initialized");

        bool isTopLevelCall = !_initializing;
        if (isTopLevelCall) {
            _initializing = true;
            _initialized = true;
        }

        _;

        if (isTopLevelCall) {
            _initializing = false;
        }
    }

    /// @dev Returns true if and only if the function is running in the constructor
    function _isConstructor() private view returns (bool) {
        // extcodesize checks the size of the code stored in an address, and
        // address returns the current address. Since the code is still not
        // deployed when running a constructor, any checks on its code size will
        // yield zero, making it an effective way to detect if a contract is
        // under construction or not.
        address self = address(this);
        uint256 cs;
        // solhint-disable-next-line no-inline-assembly
        assembly {
            cs := extcodesize(self)
        }
        return cs == 0;
    }
}
","keccak256":"0x95a413b1c4dc23bacf1a16b826a929f2ff756835e3ff3018aeff63afd497886f","license":"MIT"},"contracts/liquidation/auctionHouse/AuctionHouse.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.3;

import "../../interface/ILedger.sol";
import "../../interface/IFeed.sol";
import "../../interface/IOracle.sol";
import "../../interface/ILiquidator.sol";
import "../../interface/IAuctionHouse.sol";
import "../../interface/IMinusBank.sol";
import "../../interface/IAuctionTakerCallee.sol";
import "../../interface/ISchedule.sol";
import "../../library/AccessControl.sol";
import "../../library/Pausable.sol";
import "../../library/WadRayMath.sol";
import "../../library/openzeppelin/contracts/utils/Math.sol";
import "../../library/openzeppelin/contracts/utils/SafeCast.sol";

/// @notice Most of the codes here are a subset of MakerDAO's contract
contract AuctionHouse is IAuctionHouse, AccessControl, Pausable {
    using WadRayMath for uint256;

    // --- Data ---
    uint256 constant BLN = 10**9;
    uint256 constant RAY = 10**27;
    bytes32 public override collateralType; // Collateral type of this AuctionHouse
    ILedger public ledger; // Core CDP Engine

    ILiquidator public liquidator; // Liquidation module
    address public minusBank; // Recipient of KSD raised in auctions
    IOracle public oracle; // Collateral price module
    ISchedule public schedule; // Current price calculator (price scheduler)

    uint256 public override buf; // Multiplicative factor to increase starting price                  [ray]
    uint256 public override timeLimit; // Time elapsed before auction reset                                 [seconds]
    uint256 public override dropLimit; // Percentage drop before auction reset                              [ray]
    uint64 public override ratioIncentive; // Percentage of raiseAmount to suck from minusBank to incentivize keepers         [wad]
    uint192 public override constIncentive; // Flat fee to suck from minusBank to incentivize keepers                  [rad]
    uint256 public __legacy; // bound: Cache the collateral dust times the penalty to prevent excessive SLOADs [rad]

    uint256 public override cnt; // Total auctions
    uint256[] public override active; // Array of active auction ids

    mapping(uint256 => Sale) public override sales;

    uint256 internal locked;

    // Levels for circuit breaker
    // 0: no breaker
    // 1: no new start()
    // 2: no new start() or redo()
    // 3: no new start(), redo(), or take()
    uint256 public stopped = 0;

    // --- Init ---
    function __AuctionHouse_init(
        address ledger_,
        address oracle_,
        address liquidator_,
        bytes32 collateralType_
    ) public initializer {
        __Pausable_init();
        ledger = ILedger(ledger_);
        oracle = IOracle(oracle_);
        liquidator = ILiquidator(liquidator_);
        collateralType = collateralType_;
        buf = RAY;
    }

    // --- Synchronization ---
    modifier lock() {
        require(locked == 0, "AuctionHouse:system-locked");
        locked = 1;
        _;
        locked = 0;
    }

    modifier isStopped(uint256 level) {
        require(stopped < level, "AuctionHouse:stopped-incorrect");
        _;
    }

    // --- Configuration ---
    function config(bytes32 what, uint256 data) external onlyAdmin lock {
        if (what == "buf") buf = data;
        else if (what == "timeLimit")
            timeLimit = data; // Time elapsed before auction reset
        else if (what == "dropLimit")
            dropLimit = data; // Percentage drop before auction reset
        else if (what == "ratioIncentive")
            ratioIncentive = uint64(data); // Percentage of raiseAmount to incentivize (max: 2^64 - 1 => 18.xxx WAD = 18xx%)
        else if (what == "constIncentive")
            constIncentive = uint192(data); // Flat fee to incentivize keepers (max: 2^192 - 1 => 6.277T RAD)
        else if (what == "stopped")
            stopped = data; // Set breaker (0, 1, 2, or 3)
        else revert("AuctionHouse::config:unrecognized-param");
        emit Config(what, data);
    }

    function config(bytes32 what, address data) external onlyAdmin lock {
        if (what == "spotter") oracle = IOracle(data);
        else if (what == "liquidator") liquidator = ILiquidator(data);
        else if (what == "minusBank") minusBank = data;
        else if (what == "schedule") schedule = ISchedule(data);
        else revert("AuctionHouse::config:unrecognized-param");
        emit Config(what, data);
    }

    // --- Auction ---
    function getFeedPrice() internal view returns (uint256 feedPrice) {
        (IFeed feed, ) = oracle.collateralInfo(collateralType);
        (uint256 val, bool has) = feed.getPrice();
        require(has, "AuctionHouse::getFeedPriceinvalid-price");
        feedPrice = (val * BLN).rdiv(oracle.unit());
    }

    /// @notice starts an auction
    /// trusts the caller to transfer collateral to the contract
    /// The `startPrice` is obtained as follows:
    ///
    ///     startPrice = feedPrice * buf / par
    ///
    /// Where `feedPrice` is the collateral's unitary value in USD, `buf` is a
    /// multiplicative factor to increase the starting price, and `par` is a
    /// reference per KSD.
    /// @param raiseAmount ksd amount to raise through auction. [rad]
    /// @param sellAmount collateral amount to sell through auction. [wad]
    /// @param vaultOwner address will receive any leftover collateral
    /// @param keeper address that will receive incentives
    function start(
        uint256 raiseAmount,
        uint256 sellAmount,
        address vaultOwner,
        address keeper
    ) external override onlyAdmin lock isStopped(1) returns (uint256 id) {
        // Input validation
        require(raiseAmount > 0, "AuctionHouse::start:zero-raiseAmount");
        require(sellAmount > 0, "AuctionHouse::start:zero-sellAmount");
        require(vaultOwner != address(0), "AuctionHouse::start:zero-vaultOwner");
        id = ++cnt;
        require(id > 0, "AuctionHouse::start:overflow");

        active.push(id);

        sales[id].index = active.length - 1;
        sales[id].raiseAmount = raiseAmount;
        sales[id].sellAmount = sellAmount;
        sales[id].vaultOwner = vaultOwner;
        sales[id].startTime = uint96(block.timestamp);

        uint256 startPrice;
        startPrice = getFeedPrice().rmul(buf);
        require(startPrice > 0, "AuctionHouse::start:zero-startPrice-price");
        sales[id].startPrice = startPrice;

        // incentive to start auction
        uint256 _constIncentive = constIncentive;
        uint256 _ratioIncentive = ratioIncentive;
        uint256 coin;
        if (_constIncentive > 0 || _ratioIncentive > 0) {
            coin = _constIncentive + raiseAmount.wmul(_ratioIncentive);
            ledger.suck(minusBank, keeper, coin);
        }

        emit Start(id, startPrice, raiseAmount, sellAmount, vaultOwner, keeper, coin);
    }

    /// @notice reset an auction
    function redo(uint256 id, address keeper) external lock isStopped(2) {
        // Read auction data
        address vaultOwner = sales[id].vaultOwner;
        uint96 startTime = sales[id].startTime;
        uint256 startPrice = sales[id].startPrice;

        require(vaultOwner != address(0), "AuctionHouse::redo:not-running-auction");

        // Check that auction needs reset
        // and compute current price [ray]
        (bool done, ) = status(startTime, startPrice);
        require(done, "AuctionHouse::redo:cannot-reset");

        uint256 raiseAmount = sales[id].raiseAmount;
        uint256 sellAmount = sales[id].sellAmount;
        sales[id].startTime = uint96(block.timestamp);

        uint256 feedPrice = getFeedPrice();
        startPrice = feedPrice.rmul(buf);
        require(startPrice > 0, "AuctionHouse::redo:zero-startPrice-price");
        sales[id].startPrice = startPrice;

        // incentive to redo auction
        uint256 _constIncentive = constIncentive;
        uint256 _ratioIncentive = ratioIncentive;
        uint256 coin;
        if (_constIncentive > 0 || _ratioIncentive > 0) {
            uint256 _bound = bound();
            if (raiseAmount >= _bound && sellAmount * feedPrice >= _bound) {
                coin = _constIncentive + raiseAmount.wmul(_ratioIncentive);
                ledger.suck(minusBank, keeper, coin);
            }
        }

        emit Redo(id, startPrice, raiseAmount, sellAmount, vaultOwner, keeper, coin);
    }

    /// @notice buy up to `amt` of collateral from the auction indexed by `id`.
    ///
    /// Auctions will not collect more KSD than their assigned KSD target,`raiseAmount`;
    // thus, if `amt` would cost more KSD than `raiseAmount` at the current price, the
    // amount of collateral purchased will instead be just enough to collect `raiseAmount` KSD.
    //
    // To avoid partial purchases resulting in very small leftover auctions that will
    // never be cleared, any partial purchase must leave at least `bound`
    // remaining KSD target. `bound` is an asynchronously updated value equal to
    // (Ledger.dust * Liquidator.penalty(ilk) / WAD) where the values are understood to be determined
    // by whatever they were when updateBound() was last called. Purchase amounts
    // will be minimally decreased when necessary to respect this limit; i.e., if the
    // specified `amt` would leave `raiseAmount < bound` but `raiseAmount > 0`, the amount actually
    // purchased will be such that `raiseAmount == bound`.
    //
    // If `raiseAmount <= bound`, partial purchases are no longer possible; that is, the remaining
    // collateral can only be purchased entirely, or not at all.
    function take(
        uint256 id,
        uint256 amt,
        uint256 max,
        address to,
        bytes calldata data
    ) external override lock isStopped(3) onlyOperator {
        address vaultOwner = sales[id].vaultOwner;
        uint96 startTime = sales[id].startTime;

        require(vaultOwner != address(0), "AuctionHouse::take:not-running-auction");

        uint256 price;
        {
            bool done;
            (done, price) = status(startTime, sales[id].startPrice);

            // Check that auction doesn't need reset
            require(!done, "AuctionHouse::take:needs-reset");
        }

        // Ensure price is acceptable to buyer
        require(max >= price, "AuctionHouse::take:too-expensive");

        uint256 sellAmount = sales[id].sellAmount;
        uint256 raiseAmount = sales[id].raiseAmount;
        uint256 owe;

        {
            // Purchase as much as possible, up to amt
            uint256 slice = Math.min(sellAmount, amt); // slice <= sellAmount

            // KSD needed to buy a slice of this sale
            owe = slice * price;

            // Don't collect more than raiseAmount of KSD
            if (owe > raiseAmount) {
                // Total debt will be paid
                owe = raiseAmount; // owe' <= owe
                // Adjust slice
                slice = owe / price; // slice' = owe' / price <= owe / price == slice <= sellAmount
            } else if (owe < raiseAmount && slice < sellAmount) {
                // If slice == sellAmount => auction completed => dust doesn't matter
                uint256 _bound = bound();
                if (raiseAmount - owe < _bound) {
                    // safe as owe < raiseAmount
                    // If raiseAmount <= bound, buyers have to take the entire sellAmount.
                    require(raiseAmount > _bound, "AuctionHouse::take:no-partial-purchase");
                    // Adjust amount to pay
                    owe = raiseAmount - _bound; // owe' <= owe
                    // Adjust slice
                    slice = owe / price; // slice' = owe' / price < owe / price == slice < sellAmount
                }
            }

            // Calculate remaining raiseAmount after operation
            raiseAmount = raiseAmount - owe; // safe since owe <= raiseAmount
            // Calculate remaining sellAmount after operation
            sellAmount = sellAmount - slice;

            // Send collateral to who
            ledger.sendCollateral(collateralType, address(this), to, slice);

            // Do external call (if data is defined) but to be
            // extremely careful we don't allow to do it to the two
            // contracts which the AuctionHouse needs to be authorized
            ILiquidator liquidator_ = liquidator;
            if (data.length > 0 && to != address(ledger) && to != address(liquidator_)) {
                IAuctionTakerCallee(to).takerCall(msg.sender, owe, slice, data);
            }

            // Get KSD from caller
            ledger.sendKsd(msg.sender, minusBank, owe);

            IMinusBank(minusBank).payBackMinus(owe);

            // Removes KSD out for liquidation from accumulator
            liquidator_.endLiquidation(collateralType, sellAmount == 0 ? raiseAmount + owe : owe);
        }

        if (sellAmount == 0) {
            _remove(id);
        } else if (raiseAmount == 0) {
            ledger.sendCollateral(collateralType, address(this), vaultOwner, sellAmount);
            ledger.actionLoan(collateralType, vaultOwner, vaultOwner, vaultOwner, SafeCast.toInt256(sellAmount), 0);
            _remove(id);
        } else {
            sales[id].raiseAmount = raiseAmount;
            sales[id].sellAmount = sellAmount;
        }

        emit Take(id, max, price, owe, raiseAmount, sellAmount, vaultOwner);
    }

    function _remove(uint256 id) internal {
        uint256 _move = active[active.length - 1];
        if (id != _move) {
            uint256 _index = sales[id].index;
            active[_index] = _move;
            sales[_move].index = _index;
        }
        active.pop();
        delete sales[id];
    }

    function count() external view returns (uint256) {
        return active.length;
    }

    function list() external view returns (uint256[] memory) {
        return active;
    }

    /// @notice status of auction sale
    /// @param id id of auction sale
    function getStatus(uint256 id)
        external
        view
        override
        returns (
            bool needsRedo,
            uint256 price,
            uint256 sellAmount, // collateral amount
            uint256 raiseAmount // raise amount
        )
    {
        // Read auction data
        address vaultOwner = sales[id].vaultOwner;
        uint96 startTime = sales[id].startTime;

        bool done;
        (done, price) = status(startTime, sales[id].startPrice);

        needsRedo = vaultOwner != address(0) && done;
        sellAmount = sales[id].sellAmount;
        raiseAmount = sales[id].raiseAmount;
    }

    /// @notice internally returns boolean for if an auction needs a redo
    /// @param startTime auction start time
    /// @param startPrice auction start price
    function status(uint96 startTime, uint256 startPrice) internal view returns (bool done, uint256 price) {
        price = schedule.price(startPrice, block.timestamp - uint256(startTime));
        done = (block.timestamp - uint256(startTime) > timeLimit || price.rdiv(startPrice) < dropLimit);
    }

    function bound() public view override returns (uint256) {
        (, , , , , uint256 _dust) = ILedger(ledger).collateralInfo(collateralType);
        return _dust.wmul(liquidator.penalty(collateralType));
    }

    /// @notice cancel an auction during EmergencyStop or via governance action.
    /// @param id sale id
    function cancel(uint256 id) external override onlyAdmin lock {
        require(sales[id].vaultOwner != address(0), "AuctionHouse::cancel:not-running-auction");
        liquidator.endLiquidation(collateralType, sales[id].raiseAmount);
        ledger.sendCollateral(collateralType, address(this), msg.sender, sales[id].sellAmount);
        _remove(id);
        emit Cancel(id);
    }
}
