pragma solidity 0.8.3;

import "../../library/WadRayMath.sol";
import "../../interface/klayswap/IKlaytnExchangeImpl.sol";
import "../../oracle/feed/BSingleFeed.sol";

contract BKWBTCFeed is BSingleFeed {
    address[] public inclusivePathToKlay;

    function __BKWBTCFeed_init(address addressBook) public initializer {
        __BSingleFeed_init(bytes32("KWBTC"), addressBook);
    }

    function setPathToKlay(address[] memory pathToKlay_) external onlyAdmin {
        delete inclusivePathToKlay;

        address token = addressBook.getCollateralContract(collateralType, bytes32("token"));
        inclusivePathToKlay.push(token);

        uint256 n = pathToKlay_.length;
        for (uint256 i = 0; i < n; i++) {
            inclusivePathToKlay.push(pathToKlay_[i]);
        }
        inclusivePathToKlay.push(address(0));
    }

    function set() external override whenNotPaused {
        address klayFeed = addressBook.getCollateralContract(bytes32("klay"), bytes32("bondFeed"));
        (uint256 klayPrice, ) = IFeed(klayFeed).getPrice();

        address ksp = addressBook.ksp();
        address token = addressBook.getCollateralContract(collateralType, bytes32("token"));

        uint256 price_ = klayPrice;
        uint256 length = inclusivePathToKlay.length;
        for (uint256 i = 1; i < length; i++) {
            address lp = IKlaytnFactory(ksp).tokenToPool(inclusivePathToKlay[i], inclusivePathToKlay[i - 1]);
            uint256 poolTarget;
            uint256 poolBase;
            if (inclusivePathToKlay[i] == IKlaytnExchange(lp).tokenA()) {
                (poolTarget, poolBase) = IKlaytnExchange(lp).getCurrentPool();
            } else {
                (poolBase, poolTarget) = IKlaytnExchange(lp).getCurrentPool();
            }
            price_ = (poolTarget * price_) / poolBase;
        }

        uint256 diff = block.timestamp - lastUpdatedAt;
        lastUpdatedAt = block.timestamp;
        if (diff >= windowSize) {
            price = price_;
            valid = true;
        } else {
            price = (price * (windowSize - diff)) / windowSize + (price_ * diff) / windowSize;
            valid = true;
        }
    }
}
