pragma solidity 0.8.3;

import "../../interface/klayswap/IKlaytnExchangeImpl.sol";
import "../../interface/klayswap/IKlaytnFactoryImpl.sol";
import "../../interface/IAddressBook.sol";
import "../../interface/IFeed.sol";
import "./Feed.sol";

contract BSingleFeed is Feed {
    IAddressBook public addressBook;
    bytes32 public collateralType;
    uint256 public lastUpdatedAt;
    uint256 public windowSize;

    function __BSingleFeed_init(bytes32 collateralType_, address addressBook_) public initializer {
        __Feed_init();
        collateralType = collateralType_;
        addressBook = IAddressBook(addressBook_);
    }

    function config(bytes32 what, uint256 data) external onlyAdmin {
        if (what == "windowSize") windowSize = data;
        else revert("BSingleFeed::config:unrecognized-param");
    }

    function set(uint256 _price) external override {
        require(false, "BSingleFeed::set: disabled");
    }

    function set() external virtual whenNotPaused {
        address klayFeed = addressBook.getCollateralContract(bytes32("klay"), bytes32("bondFeed"));
        (uint256 klayPrice, ) = IFeed(klayFeed).getPrice();

        address ksp = addressBook.ksp();
        address token = addressBook.getCollateralContract(collateralType, bytes32("token"));
        address lp = IKlaytnFactory(ksp).tokenToPool(address(0), token);

        (uint256 poolKlay, uint256 poolToken) = IKlaytnExchange(lp).getCurrentPool();
        uint256 price_ = (poolKlay * klayPrice) / poolToken;

        uint256 diff = block.timestamp - lastUpdatedAt;
        lastUpdatedAt = block.timestamp;
        if (diff >= windowSize) {
            price = price_;
            valid = true;
        } else {
            price = (price * (windowSize - diff)) / windowSize + (price_ * diff) / windowSize;
            valid = true;
        }
    }
}
