pragma solidity 0.8.3;

import "../../interface/IFeed.sol";
import "../../library/AccessControl.sol";
import "../../library/Pausable.sol";

/// @notice the source of collateral price. The price is used by oracle.
/// The price will be set directly by whitelisted operator.
contract Feed is IFeed, AccessControl, Pausable {
    bool public valid;
    uint256 public price; // WAD

    function __Feed_init() public initializer {
        __Pausable_init();
    }

    /// @notice get price
    /// @return value of price, validation check
    function getPrice() public view virtual override returns (uint256, bool) {
        return (price, valid);
    }

    /// @notice set price
    /// @param _price value of price to set
    /// @dev it should be called by only whitelisted operator
    function set(uint256 _price) external virtual onlyOperator whenNotPaused {
        price = _price;
        valid = true;
    }

    /// @notice make price invalid
    /// @dev it is should be called by only governance (EOA for now).
    function clear() external onlyOwner {
        valid = false;
    }
}
