pragma solidity 0.8.3;

import "../../interface/ISchedule.sol";
import "../../library/AccessControl.sol";
import "../../library/Pausable.sol";
import "../../library/WadRayMath.sol";

/// @notice Most of the codes here are a subset of MakerDAO's contract
contract LinearDecreaseSchedule is ISchedule, AccessControl, Pausable {
    using WadRayMath for uint256;

    // --- Data ---
    uint256 constant RAY = 10**27;
    uint256 public tau; // Seconds after auction start when the price reaches zero [seconds]

    event Config(bytes32 indexed what, uint256 data);

    function __LinearDecreaseSchedule_init() public initializer {
        __Pausable_init();
    }

    // --- Configuration ---
    function config(bytes32 what, uint256 data) external onlyAdmin whenNotPaused {
        if (what == "tau") tau = data;
        else revert("LinearDecrease:config::data-unrecognized-param");
        emit Config(what, data);
    }

    // Price calculation when price is decreased linearly in proportion to time:
    // tau: The number of seconds after the start of the auction where the price will hit 0
    // top: Initial price
    // dur: current seconds since the start of the auction
    //
    // Returns y = top * ((tau - dur) / tau)
    //
    // Note the internal call to mul multiples by RAY, thereby ensuring that the rmul calculation
    // which utilizes top and tau (RAY values) is also a RAY value.
    function price(uint256 top, uint256 dur) external view override returns (uint256) {
        if (dur >= tau) return 0;
        return top.rmul(((tau - dur) * RAY) / tau);
    }
}
