pragma solidity 0.8.3;

import "../../library/kip/KIP7Extended.sol";
import "../../library/openzeppelin/contracts/math/SafeMath.sol";
import "../../library/openzeppelin/contracts/utils/Math.sol";
import "../../interface/IBond.sol";
import "../../interface/IYieldFactory.sol";

contract BaseBond is IBond, KIP7Extended {
    // --- data ---
    IYieldFactory public yieldFactory;
    IKIP7 public token;

    function __BaseBond_init(
        string memory name_,
        string memory symbol_,
        uint8 decimals_
    ) public initializer {
        __KIP7Extended_init(name_, symbol_, decimals_);
        __Pausable_init();
    }

    function config(bytes32 what, address data) external onlyAdmin whenNotPaused {
        if (what == "yieldFactory") yieldFactory = IYieldFactory(data);
        else if (what == "token") token = IKIP7(data);
        else revert("BaseBond::config: unrecognized-param");
    }

    function fromTokenAmount(uint256 tokenAmount) public view override returns (uint256 bondAmount) {
        uint256 totalAmount = yieldFactory.totalAmount();
        if (totalAmount <= 0) {
            bondAmount = tokenAmount;
        } else {
            bondAmount = (tokenAmount * _totalSupply) / totalAmount;
        }
    }

    function toTokenAmount(uint256 bondAmount) public view override returns (uint256 tokenAmount) {
        uint256 totalAmount = yieldFactory.totalAmount();
        if (totalAmount <= 0) {
            tokenAmount = bondAmount;
        } else {
            tokenAmount = (bondAmount * totalAmount) / _totalSupply;
        }
    }

    function stake(address to, uint256 tokenAmount) external override whenNotPaused returns (uint256 bondAmount) {
        if (tokenAmount <= 0) {
            return 0;
        }
        bondAmount = fromTokenAmount(tokenAmount);
        _mint(to, bondAmount);
        token.transferFrom(msg.sender, address(this), tokenAmount);
        token.approve(address(yieldFactory), tokenAmount);
        yieldFactory.stake(tokenAmount);

        emit Stake(msg.sender, to, tokenAmount, bondAmount);
    }

    function unstake(address to, uint256 bondAmount) external override whenNotPaused returns (uint256 tokenAmount) {
        if (bondAmount <= 0) {
            return 0;
        }
        tokenAmount = toTokenAmount(bondAmount);
        _burn(msg.sender, bondAmount);
        yieldFactory.unstake(tokenAmount);
        token.transfer(to, tokenAmount);

        emit Unstake(msg.sender, to, tokenAmount, bondAmount);
    }
}
