    function withdrawKlay(address to, uint256 amount) external;

    /// @notice Unstaking status for all yields.
    function pendingKlay(address usr) external view returns (uint256 completed, uint256 yet);

    /// @notice Claim on every yield, send claimed klay to user.
    function claimKlay(address usr) external;
}
