pragma solidity 0.8.3;

import "../../library/kip/KIP7Extended.sol";
import "../../library/openzeppelin/contracts/math/SafeMath.sol";
import "../../interface/IBKlay.sol";
import "../../library/openzeppelin/contracts/utils/Math.sol";
import "../../interface/IKlayYieldFactory.sol";

/// @title Bonded Klay Contract
/// @notice Mint/Burn logic of Bklay via klay
/// @dev Bklay is an abstracted token of klay. Bklay used as collateral for ledge
/// @dev 1 BKlay = 1 klay
contract BKlay is KIP7Extended, IBKlay {
    // --- data ---
    IKlayYieldFactory public yieldFactory;

    struct UnstakeRecord {
        address caller;
        uint256 amount;
        uint256 completionTime;
        bool isClaimed;
    }

    uint256 public lockUpPeriod;
    mapping(address => uint256) public override claimCount;
    mapping(address => uint256) public override unstakeRecordCount;
    mapping(address => mapping(uint256 => UnstakeRecord)) public override unstakeRecord;

    IKIP7 public aklay;

    function __BKlay_init() public initializer {
        __KIP7Extended_init("Borrowed Klay", "bKLAY", 18);
        __Pausable_init();
    }

    // --- config ---
    function config(bytes32 what, address data) external onlyAdmin whenNotPaused {
        if (what == "yieldFactory") {
            yieldFactory = IKlayYieldFactory(data);
        } else if (what == "aklay") {
            aklay = IKIP7(data);
        } else revert("BKlay::config: unrecognized-param");
    }

    function config(bytes32 what, uint256 data) external onlyAdmin whenNotPaused {
        if (what == "lockUpPeriod") {
            lockUpPeriod = data;
        } else revert("BKlay::config: unrecognized-param");
    }

    /// @notice Stake klay to yield factory, mint bklay.
    /// @dev Owner of klay is 'to'.
    /// @param to Owner of klay.
    function stakeKlay(address to) external payable override whenNotPaused {
        if (msg.value <= 0) {
            return;
        }
        _mint(to, msg.value);
        yieldFactory.stake{value: msg.value}();
    }

    /// @notice Stake aklay to yield factory, mint bklay.
    /// @dev Owner of aklay is 'to'.
    /// @param to Owner of aklay.
    function stakeAKlay(address to, uint256 amount) external override whenNotPaused {
        if (amount <= 0) {
            return;
        }
        _mint(to, amount);
        aklay.transferFrom(msg.sender, address(this), amount);
        aklay.approve(address(yieldFactory), amount);
        yieldFactory.stakeAKlay(amount);
    }

    /// @notice Unstake klay from yield factory, burn bklay.
    /// @dev The function that executes unstake.
    /// @dev Unstake takes 7 days to complete.
    /// @dev Owner of klay is 'to'.
    /// @param to Owner of klay.
    /// @param amount Amount of klay to unstake.
    function unstakeKlay(address to, uint256 amount) external override whenNotPaused {
        if (amount <= 0) {
            return;
        }
        require(balanceOf(msg.sender) >= amount, "BKlay::unstakeKlay: insufficient balance");
        _burn(msg.sender, amount);
        yieldFactory.unstake(amount);

        // for record
        uint256 completionTime = block.timestamp + lockUpPeriod;
        uint256 recordIndex = unstakeRecordCount[to] + 1;
        unstakeRecord[to][recordIndex] = UnstakeRecord(msg.sender, amount, completionTime, false);
        unstakeRecordCount[to] = recordIndex;

        emit UnstakeKlay(msg.sender, to, amount);
    }

    /// @notice bKLAY에서 aKLAY 등의 자산 인출할 수 있는 기능
    /// @dev cdp 의 bklay가 user에게 인출되어 있는 상태를 가정
    /// @param to receiver.
    /// @param amount Amount of bklay to unstake.
    function withdrawToken(address to, uint256 amount) external override whenNotPaused {
        if (amount <= 0) {
            return;
        }
        require(balanceOf(msg.sender) >= amount, "BKlay::withdrawToken: insufficient balance");
        _burn(msg.sender, amount);
        yieldFactory.withdrawToken(to, amount);

        emit WithdrawToken(msg.sender, to, amount);
    }

    function withdrawKlay(address to, uint256 amount) external override whenNotPaused {
        if (amount <= 0) {
            return;
        }
        require(balanceOf(msg.sender) >= amount, "BKlay::withdrawKlay: insufficient balance");
        _burn(msg.sender, amount);
        yieldFactory.withdrawKlay(to, amount);

        emit WithdrawKlay(msg.sender, to, amount);
    }

    /// @notice Unstaking status for all yields.
    /// @param usr Owner of klay where unstaking was executed.
    /// @return completed Amount of klay that can be claimed after 7 days of unstake.
    /// @return yet Amount of klay that cannot be claimed as 7 days have not yet passed since unstake.
    function pendingKlay(address usr) external view override returns (uint256 completed, uint256 yet) {
        completed = 0;
        yet = 0;
        for (uint256 i = claimCount[usr] + 1; i <= unstakeRecordCount[usr]; i++) {
            UnstakeRecord memory record = unstakeRecord[usr][i];
            if (record.completionTime <= block.timestamp) {
                completed = completed + record.amount;
            } else {
                yet = yet + record.amount;
            }
        }
    }

    /// @notice Claim on every yield, send claimed klay to user.
    /// @param usr Address of the user who execute the claim.
    function claimKlay(address usr) external override {
        yieldFactory.claim();

        uint256 claimedAmount = 0;
        uint256 start = claimCount[usr] + 1;
        uint256 end = Math.min(start + 9, unstakeRecordCount[usr]);
        for (uint256 i = start; i <= end; i++) {
            UnstakeRecord storage record = unstakeRecord[usr][i];
            if (record.completionTime <= block.timestamp) {
                record.isClaimed = true;
                claimCount[usr] = i;
                (bool success, ) = usr.call{value: record.amount}("");
                require(success, "BKlay::claimKlay: transfer klay failed");
                claimedAmount += record.amount;
            } else {
                break;
            }
        }

        emit ClaimKlay(usr, claimedAmount);
    }

    receive() external payable {}
}
