pragma solidity 0.8.3;

import "../library/WadRayMath.sol";
import "../library/kip/KIP7Detailed.sol";
import "../library/Pausable.sol";
import "../library/AccessControl.sol";
import "../interface/IDKSD.sol";
import "../library/openzeppelin/contracts/math/SafeMath.sol";

/// @title A delegated stable coin.
/// @notice Users can deposit ksd and in return receive dksd,
/// and also dksd holder can exchange dksd to ksd in a ratio of 1 to 1.
/// The purpose of this contract is distributing profit of yield farming to dksd holder.
/// The profit of yield framing is swapped into KSD, and the KSD distributed to dKSD holder.
/// Therefore, the balance of dKSD holder increases with the passing of time.
/// For example, if you deposit 100KSD at first, you receive 100dKSD.
/// With the passing of time, the balance of your DKSD becomes 101dKSD.
/// And then, you can withdraw 101KSD.
contract DKSD is KIP7Detailed, AccessControl, IDKSD, Pausable {
    using WadRayMath for uint256;

    IKIP7 public ksd; /// address of ksd stable coin

    /// @notice multiplicative factor for increasing balance/totalSupply
    /// dksd랑 ksd랑 1 대 1 페깅 시키게 만드는 변수로, 유저가 처음 KSD를 a 만큼 deposit하면,
    /// a / liquidityIndex 만큼 유저의 _balance를 증가시킨다.
    /// 반면 balanceOf 함수에서는 유저의 balance를 보여줄 때는 _balance * liquidityIndex 만큼 보여주기 때문에,
    /// 유저에게 보여지는 balance 양은 a 로 보여지게 된다.
    /// 유저가 withdraw 하기 전에, yield 수익이 DKSD contract로 분배되면 liquidityIndex는 증가하게 되고,
    /// 유저의 _balance는 변함 없지만, balance는 증가한 것 처럼 보이게 된다.
    uint256 public override liquidityIndex;
    uint256 public constant override precision = 1e12;

    function __DKSD_init(address KSD) public initializer {
        __Pausable_init();
        __KIP7Detailed_init("Deposited KSD", "dKSD", 18);
        ksd = IKIP7(KSD);
        liquidityIndex = WadRayMath.ray();
    }

    /// @notice deposit ksd, and receive dksd.
    /// @param to address of receiving dksd
    /// @param amount amount of depositing ksd
    function deposit(address to, uint256 amount) external override {
        uint256 amountScaled = (amount * precision).rayDiv(liquidityIndex);
        require(amountScaled != 0, "DKSD::deposit: too small amount");
        _balances[to] = _balances[to] + amountScaled;
        _totalSupply = _totalSupply + amountScaled;
        emit Transfer(address(0), to, amount);

        ksd.transferFrom(msg.sender, address(this), amount);
        emit Deposit(msg.sender, to, amount, liquidityIndex);
    }

    /// @notice burn dksd, and withdraw ksd
    /// @param to address of receiving ksd
    /// @param amount amount of withdrawing dksd
    function withdraw(address to, uint256 amount) external override {
        require(balanceOf(msg.sender) >= amount, "DKSD::withdraw: insufficient balance");

        uint256 amountScaled = (amount * precision).rayDiv(liquidityIndex);
        require(amountScaled != 0, "DKSD::withdraw: too small amount");
        _balances[msg.sender] = _balances[msg.sender] - amountScaled;
        _totalSupply = _totalSupply - amountScaled;
        emit Transfer(msg.sender, address(0), amount);

        ksd.transfer(to, amount);
        emit Withdraw(msg.sender, to, amount, liquidityIndex);
    }

    function fromRawAmount(uint256 rawAmount) external view override returns (uint256) {
        return rawAmount.rayMul(liquidityIndex) / precision;
    }

    function toRawAmount(uint256 amount) external view override returns (uint256) {
        return (amount * precision).rayDiv(liquidityIndex);
    }

    function rawBalanceOf(address usr) external view override returns (uint256) {
        return _balances[usr];
    }

    function rawTotalSupply() external view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address usr) public view virtual override(IKIP7, KIP7) returns (uint256) {
        return _balances[usr].rayMul(liquidityIndex) / precision;
    }

    function totalSupply() public view virtual override(IKIP7, KIP7) returns (uint256) {
        return _totalSupply.rayMul(liquidityIndex) / precision;
    }

    function __transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal {
        require(sender != address(0), "DKSD::_transfer: transfer from the zero address");
        require(recipient != address(0), "DKSD::_transfer: transfer to the zero address");

        uint256 amountScaled = (amount * precision).rayDiv(liquidityIndex);
        _balances[sender] = _balances[sender] - amountScaled;
        _balances[recipient] = _balances[recipient] + amountScaled;
        emit Transfer(sender, recipient, amount);
    }

    function transfer(address recipient, uint256 amount) public virtual override(IKIP7, KIP7) returns (bool success) {
        __transfer(msg.sender, recipient, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public virtual override(IKIP7, KIP7) returns (bool) {
        __transfer(sender, recipient, amount);
        _approve(sender, msg.sender, _allowances[sender][msg.sender] - amount);
        return true;
    }

    /// @notice receive ksd and distribute the ksd to dksd holder
    /// @param ksdAmount amount of receiving ksd
    /// @dev this function is called by KSDRewardManager contract
    function earn(uint256 ksdAmount) external override {
        if (ksdAmount > 0 && _totalSupply > 0) {
            ksd.transferFrom(msg.sender, address(this), ksdAmount);

            uint256 earningPerShare = (WadRayMath.ray() * ksdAmount * precision) / _totalSupply;
            liquidityIndex = liquidityIndex + earningPerShare;
            emit Earn(block.timestamp, ksdAmount, liquidityIndex);
        }
    }
}
