pragma solidity 0.8.3;

import "../library/AccessControl.sol";
import "../library/Pausable.sol";
import "../interface/ILedger.sol";
import "../interface/IAuctionHouse.sol";
import "../interface/ILiquidator.sol";
import "../interface/IMinusBank.sol";
import "../interface/IOracle.sol";
import "../interface/IFeed.sol";
import "../interface/kip/IKIP7.sol";

/// @notice stops other contract and fix some state of contracts
/// This code is a subset of MakerDAO's Emergency module.
contract Emergency is AccessControl, Pausable {
    address public kokoa;
    address public skokoa;
    address public ksd;
    address public dksd;
    address public bklay;

    address public ledger; // CDP Engine
    address public liquidator;
    address public minusBank; // Debt Engine
    address public oracle;

    mapping(bytes32 => address) auctionHouse;

    uint256 public when; // Time of cage                   [unix epoch time]

    // --- Init ---
    function __Emergency_init() public initializer {
        __Pausable_init();
    }

    function config(bytes32 what, address data) external onlyOwner whenNotPaused {
        if (what == "kokoa") kokoa = data;
        else if (what == "skokoa") skokoa = data;
        else if (what == "ksd") ksd = data;
        else if (what == "dksd") dksd = data;
        else if (what == "bklay") bklay = data;
        else if (what == "ledger") ledger = data;
        else if (what == "liquidator") liquidator = data;
        else if (what == "minusBank") minusBank = data;
        else if (what == "oracle") oracle = data;
        else revert("Emergency::config:unrecognized-param");
    }

    function config(
        bytes32 collateralType,
        bytes32 what,
        address data
    ) external onlyOwner whenNotPaused {
        if (what == "auctionHouse") auctionHouse[collateralType] = data;
        else revert("Emergency::config:unrecognized-param");
    }

    function pauseCollateral(bytes32 collateralType) public onlyOwner whenNotPaused {
        IPausable(auctionHouse[collateralType]).pause();
    }

    /// @notice pause all registered contracts
    /// Therefore, this contract should have admin permission to pause other contract
    function pauseAll() external onlyOwner whenNotPaused {
        when = block.timestamp;
        pauseCollateral(bytes32("klay"));
        IPausable(kokoa).pause();
        IPausable(skokoa).pause();
        IPausable(ksd).pause();
        IPausable(dksd).pause();
        IPausable(bklay).pause();
        IPausable(ledger).pause();
        IPausable(liquidator).pause();
        IPausable(minusBank).pause();
        IPausable(oracle).pause();
    }
}
