pragma solidity 0.8.3;

interface IAddressBook {
    struct CollateralContracts {
        address token;
        address bond;
        address bondAuctionHouse;
        address bondFeed;
        address bondIssuedKsdPool;
        address bondSchedule;
        address gateway;
        address yieldFactory;
    }

    function config(bytes32 what, address data) external;

    function config(
        bytes32 collateralType,
        bytes32 what,
        address data
    ) external;

    function configurator() external view returns (address);

    function ledger() external view returns (address);

    function ksd() external view returns (address);

    function ksdGateway() external view returns (address);

    function yieldManager() external view returns (address);

    function kokoaRewardManager() external view returns (address);

    function ksdRewardManager() external view returns (address);

    function ksdRefundManager() external view returns (address);

    function collateralContracts(bytes32 collateralType)
        external
        view
        returns (
            address,
            address,
            address,
            address,
            address,
            address,
            address,
            address
        );

    function getCollateralContract(bytes32 collateralType, bytes32 what) external view returns (address);

    function ksp() external view returns (address);
}
