import "../library/kip/KIP7Detailed.sol";

pragma solidity 0.8.3;

/// @title A delegated stable coin.
/// @notice Users can deposit ksd and in return receive dksd,
/// and also dksd holder can exchange dksd to ksd in a ratio of 1 to 1.
/// The purpose of this contract is distributing profit of yield farming to dksd holder.
/// The profit of yield framing is swapped into KSD, and the KSD distributed to dKSD holder.
/// Therefore, the balance of dKSD holder increases with the passing of time.
/// For example, if you deposit 100KSD at first, you receive 100dKSD.
/// With the passing of time, the balance of your DKSD becomes 101dKSD.
/// And then, you can withdraw 101KSD.
interface IDKSD is IKIP7Detailed {
    /// @notice value of liquidityIndex
    function liquidityIndex() external view returns (uint256);

    /// @notice precision of scaled values
    function precision() external pure returns (uint256);

    /// @notice deposit ksd
    /// @param to address of receiving dksd
    /// @param amount amount of depositing ksd
    function deposit(address to, uint256 amount) external;

    /// @notice withdraw ksd
    /// @param to address of receiving ksd
    /// @param amount amount of withdrawing dksd
    function withdraw(address to, uint256 amount) external;

    /// @notice receive ksd and distribute the ksd to dksd holder
    /// @param ksdAmount amount of receiving ksd
    function earn(uint256 ksdAmount) external;

    /// @notice calculate amount of corresponding to rawAmount
    /// @param rawAmount rawAmount
    /// @return amount of corresponding to rawAmount
    function fromRawAmount(uint256 rawAmount) external view returns (uint256);

    /// @notice calculate rawAmount of corresponding to amount
    /// @param amount amount
    function toRawAmount(uint256 amount) external view returns (uint256);

    /// @notice get raw balance of user
    /// @param usr address of user
    function rawBalanceOf(address usr) external view returns (uint256);

    /// @notice get raw total balance
    function rawTotalSupply() external view returns (uint256);

    event Deposit(address indexed user, address to, uint256 amount, uint256 liquidityIndex);
    event Withdraw(address indexed user, address to, uint256 amount, uint256 liquidityIndex);
    event Earn(uint256 now, uint256 ksdAmount, uint256 liquidityIndex);
}
