pragma solidity 0.8.3;

import "./kip/IKIP7Extended.sol";

interface ISKokoaToken {
    event Stake(address from, address to, uint256 amount);

    event Unstake(address from, address to, uint256 amount);

    event Claim(address usr, uint256 amount);

    /// @notice An event thats emitted when an account changes its delegate
    event DelegateChanged(address indexed delegator, address indexed fromDelegate, address indexed toDelegate);

    /// @notice An event thats emitted when a delegate account's vote balance changes
    event DelegateVotesChanged(address indexed delegate, uint96 previousBalance, uint96 newBalance);

    /// @notice A checkpoint for marking number of votes from a given block
    struct Checkpoint {
        uint32 fromBlock;
        uint96 votes;
    }

    /**
     * @notice Delegate votes from `msg.sender` to `delegatee`
     * @param delegator The address to get delegatee for
     */
    function delegates(address delegator) external view returns (address delegatee);

    function checkpoints(address account, uint32 index) external view returns (Checkpoint memory);

    function numCheckpoints(address account) external view returns (uint32 index);

    function nonces(address account) external view returns (uint256 nonce);

    function unstakeCount(address user) external view returns (uint256);

    function claimCount(address user) external view returns (uint256);

    function unstakeRecord(address user, uint256 index) external view returns (uint256 block, uint256 amount);

    function lockUpPeriod() external view returns (uint256);

    /**
     * @notice Delegate votes from `msg.sender` to `delegatee`
     * @param delegatee The address to delegate votes to
     */
    function delegate(address delegatee) external;

    /**
     * @notice Delegates votes from signatory to `delegatee`
     * @param delegatee The address to delegate votes to
     * @param nonce The contract state required to match the signature
     * @param expiry The time at which to expire the signature
     * @param v The recovery byte of the signature
     * @param r Half of the ECDSA signature pair
     * @param s Half of the ECDSA signature pair
     */
    function delegateBySig(
        address delegatee,
        uint256 nonce,
        uint256 expiry,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external;

    /**
     * @notice Gets the current votes balance for `account`
     * @param account The address to get votes balance
     * @return votes The number of current votes for `account`
     */
    function getCurrentVotes(address account) external view returns (uint96 votes);

    /**
     * @notice Determine the prior number of votes for an account as of a block number
     * @dev Block number must be a finalized block or else this function will revert to prevent misinformation.
     * @param account The address of the account to check
     * @param blockNumber The block number to get the vote balance at
     * @return votes The number of votes the account had as of the given block
     */
    function getPriorVotes(address account, uint256 blockNumber) external view returns (uint96 votes);
}
