        }
    }

    function mul(int256 x, uint256 y) internal pure returns (int256 z) {
        z = x * int256(y);
        require(int256(y) >= 0, "SafeMath: multiplication overflow");
        require(x == 0 || z / x == int256(y), "SafeMath: multiplication overflow");
    }

    // -- makerdao --
    // extended
    uint256 constant ONE = 10**27;
    uint256 constant RAY = 10**27;
    uint256 constant WAD = 10**18;

    function rdiv(uint256 x, uint256 y) internal pure returns (uint256 z) {
        z = x * RAY;
        z = z / y;
    }

    function rmul(uint256 x, uint256 y) internal pure returns (uint256 z) {
        z = x * y;
        require(y == 0 || z / y == x, "SafeMath:: rmul: overflow");
        z = z / RAY;
    }

    function wmul(uint256 x, uint256 y) internal pure returns (uint256 z) {
        z = x * y;
        require(y == 0 || z / y == x, "SafeMath:: wmul: overflow");
        z = z / WAD;
    }

    function min(uint256 x, uint256 y) internal pure returns (uint256 z) {
        z = x <= y ? x : y;
    }

    function max(uint256 x, uint256 y) internal pure returns (uint256 z) {
        z = x >= y ? x : y;
    }

    /**
     * @dev Returns the average of two numbers. The result is rounded towards
     * zero.
     */
