","keccak256":"0x95a413b1c4dc23bacf1a16b826a929f2ff756835e3ff3018aeff63afd497886f","license":"MIT"},"contracts/reward/pool/TokenStakingPool.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.3;

import "../.././library/openzeppelin/contracts/math/SafeMath.sol";
import "../.././interface/IStakingPool.sol";
import "../.././interface/IKokoaRewardManager.sol";
import "../.././interface/kip/IKIP7.sol";
import "../.././library/AccessControl.sol";
import "../.././library/Pausable.sol";

contract TokenStakingPool is IStakingPool, AccessControl, Pausable {
    // --- data ---
    IKIP7 public override token; // usually, lp token
    IKokoaRewardManager public kokoaRewardManager;

    uint256 public override totalSupply;
    mapping(address => uint256) public override balanceOf;

    function __TokenStakingPool_init(address token_, address kokoaRewardManager_) public initializer {
        __Pausable_init();
        token = IKIP7(token_);
        kokoaRewardManager = IKokoaRewardManager(kokoaRewardManager_);
    }

    // bypress에서 bypress stable lp -> cypress stable lp 이동을 위해서 추
    function config(bytes32 what, address data) external onlyOwner {
        if (what == "token") token = IKIP7(data);
        else revert("TokenStakingPool::config: unrecognized-param");
    }

    function stake(address to, uint256 amount) external override whenNotPaused {
        kokoaRewardManager.onDeposit(amount, to);

        token.transferFrom(msg.sender, address(this), amount);
        balanceOf[to] = balanceOf[to] + amount;
        totalSupply = totalSupply + amount;

        emit Stake(msg.sender, to, amount);
    }

    function unstake(address to, uint256 amount) public override whenNotPaused {
        kokoaRewardManager.onWithdraw(amount, msg.sender);

        token.transfer(to, amount);
        balanceOf[msg.sender] = balanceOf[msg.sender] - amount;
        totalSupply = totalSupply - amount;

        emit Unstake(msg.sender, to, amount);
    }
}
","keccak256":"0x271a104190e87a3aa308effdbab2fb2b0d446bf5763a56a697af30ed7297cdac","license":"MIT"},"contracts/utils/Helper.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.3;

import "../library/AccessControl.sol";
import "../interface/kip/IKIP7.sol";
import "../interface/IKokoaRewardManager.sol";
import "../interface/ISKokoaToken.sol";
import "../library/openzeppelin/contracts/utils/Math.sol";
import "../library/WadRayMath.sol";
import "../interface/IKSDRewardManager.sol";
import "../interface/IDKSD.sol";
import "../interface/ILedger.sol";
import "../interface/IBKlay.sol";
import "../reward/pool/TokenStakingPool.sol";
import "../interface/klayswap/IKlaytnFactoryImpl.sol";
import "../interface/klayswap/IKlaytnExchangeImpl.sol";
import "../interface/IKsdRefundManager.sol";
import "../interface/IAddressBook.sol";

contract Helper is AccessControl {
    IKokoaRewardManager public kokoaRewardManager;
    IKSDRewardManager public ksdRewardManager;
    ISKokoaToken public sKokoa;
    ILedger public ledger;
    IKlaytnFactory public factory;
    IBKlay public bklay;
    IKIP7 public dksd;
    IKIP7 public skokoa;
    IAddressBook public addressBook;

    struct AddressUint256Pair {
        address addr;
        uint256 value;
    }

    struct Bytes32Uint256Pair {
        bytes32 collateralType;
        uint256 value;
    }

    struct LpPoolInfo {
        address addr;
        uint256 totalSupply;
        address lpTokenAddress;
        uint256 lpTokenTotalSupply;
    }

    struct UnstakeRecord {
        uint256 index;
        uint256 amount;
        uint256 completionTime;
        bool isClaimed;
    }

    struct AccountCollateralInfo {
        bytes32 collateralType;
        uint256 lockedCollateral;
        uint256 loan;
    }

    function __Helpr_init(
        IKokoaRewardManager _kokoaRewardManager,
        IKSDRewardManager _ksdRewardManager,
        ISKokoaToken _sKokoa,
        ILedger _ledger
    ) public initializer {
        kokoaRewardManager = _kokoaRewardManager;
        ksdRewardManager = _ksdRewardManager;
        sKokoa = _sKokoa;
        ledger = _ledger;
        __AccessControl_init();
    }

    function config(bytes32 what, address data) external onlyOwner {
        if (what == "factory") {
            factory = IKlaytnFactory(data);
        } else if (what == "bklay") {
            bklay = IBKlay(data);
        } else if (what == "dksd") {
            dksd = IKIP7(data);
        } else if (what == "skokoa") {
            skokoa = IKIP7(data);
        } else if (what == "addressBook") {
            addressBook = IAddressBook(data);
        } else revert("Helper::config: unrecognized-param");
    }

    function userBalanceQuery(address _user, address[] calldata _tokens) external view returns (AddressUint256Pair[] memory balances) {
        balances = new AddressUint256Pair[](_tokens.length + 1);
        for (uint256 i = 0; i < _tokens.length; i++) {
            balances[i] = AddressUint256Pair(_tokens[i], IKIP7(_tokens[i]).balanceOf(_user));
        }
        balances[_tokens.length] = AddressUint256Pair(address(0), _user.balance);
    }

    function userAllowanceQuery(
        address _user,
        address[] calldata _tokens,
        address[] calldata _spenders
    ) external view returns (AddressUint256Pair[] memory allowances) {
        allowances = new AddressUint256Pair[](_tokens.length);
        for (uint256 i = 0; i < _tokens.length; i++) {
            allowances[i] = AddressUint256Pair(_tokens[i], IKIP7(_tokens[i]).allowance(_user, _spenders[i]));
        }
    }

    function userDataQuery(address _user, bytes32[] calldata _collateralTypes)
        external
        view
        returns (
            AddressUint256Pair[] memory kokoaClaimableRewards,
            UnstakeRecord[] memory kokoaUnstakeHistories,
            AddressUint256Pair[] memory ksdClaimableRewards,
            AccountCollateralInfo[] memory accountCollateralInfos,
            UnstakeRecord[] memory klayUnstakeHistories,
            AddressUint256Pair[] memory kokoaClaimableRewardsWithBonus,
            Bytes32Uint256Pair[] memory ksdClaimableRefunds
        )
    {
        kokoaClaimableRewards = getKokoaClaimableRewards(_user);
        kokoaUnstakeHistories = getLatestKokoaUnstakeHistory(_user);
        ksdClaimableRewards = getKsdClaimableRewards(_user);
        accountCollateralInfos = getAccountCollateralInfos(_user, _collateralTypes);
        klayUnstakeHistories = getLatestKlayUnstakeHistory(_user);
        kokoaClaimableRewardsWithBonus = getKokoaClaimableRewardsWithBonus(_user);
        ksdClaimableRefunds = getKsdClaimableRefunds(_user, _collateralTypes);
    }

    function statusQuery(
        address[] calldata _tokens,
        address[] calldata _lpPools,
        uint256 kokoaPrice
    )
        external
        view
        returns (
            AddressUint256Pair[] memory totalSupplies,
            LpPoolInfo[] memory lpPoolInfos,
            uint256 dksdApr,
            uint256 skokoaApr
        )
    {
        totalSupplies = new AddressUint256Pair[](_tokens.length);
        for (uint256 i = 0; i < _tokens.length; i++) {
            totalSupplies[i] = AddressUint256Pair(_tokens[i], IKIP7(_tokens[i]).totalSupply());
        }
        lpPoolInfos = getLpPoolInfos(_lpPools);
        dksdApr = getDksdApr();
        skokoaApr = getSKokoaKsdApr(kokoaPrice);
    }

    function getLpPoolInfos(address[] calldata _lpPools) public view returns (LpPoolInfo[] memory lpPoolInfos) {
        lpPoolInfos = new LpPoolInfo[](_lpPools.length);
        for (uint256 i = 0; i < _lpPools.length; i++) {
            TokenStakingPool pool = TokenStakingPool(_lpPools[i]);
            IKIP7 lpToken = IKIP7(address(pool.token()));
            lpPoolInfos[i] = LpPoolInfo(address(pool), pool.totalSupply(), address(lpToken), lpToken.totalSupply());
        }
    }

    function getKokoaClaimableRewardsWithBonus(address _user) public view returns (AddressUint256Pair[] memory claimableRewards) {
        address[] memory pools = kokoaRewardManager.rewardPools();
        claimableRewards = new AddressUint256Pair[](pools.length);
        for (uint256 i = 0; i < pools.length; i++) {
            claimableRewards[i] = AddressUint256Pair(pools[i], kokoaRewardManager.claimableReward(pools[i], _user, true));
        }
    }

    function getKokoaClaimableRewards(address _user) public view returns (AddressUint256Pair[] memory claimableRewards) {
        address[] memory pools = kokoaRewardManager.rewardPools();
        claimableRewards = new AddressUint256Pair[](pools.length);
        for (uint256 i = 0; i < pools.length; i++) {
            claimableRewards[i] = AddressUint256Pair(pools[i], kokoaRewardManager.claimableReward(pools[i], _user, false));
        }
    }

    function getKsdClaimableRewards(address _user) public view returns (AddressUint256Pair[] memory claimableRewards) {
        address[] memory pools = ksdRewardManager.rewardPools();
        claimableRewards = new AddressUint256Pair[](pools.length);
        for (uint256 i = 0; i < pools.length; i++) {
            claimableRewards[i] = AddressUint256Pair(pools[i], ksdRewardManager.claimableReward(pools[i], _user));
        }
    }

    function getKsdClaimableRefunds(address _user, bytes32[] calldata _collateralTypes) public view returns (Bytes32Uint256Pair[] memory claimableRefunds) {
        claimableRefunds = new Bytes32Uint256Pair[](_collateralTypes.length);
        IKsdRefundManager ksdRefundManager = IKsdRefundManager(addressBook.ksdRefundManager());
        for (uint256 i = 0; i < _collateralTypes.length; i++) {
            claimableRefunds[i] = Bytes32Uint256Pair(_collateralTypes[i], ksdRefundManager.claimableAmount(_collateralTypes[i], _user));
        }
    }

    function getLatestKokoaUnstakeHistory(address _user) public view returns (UnstakeRecord[] memory unstakeHistory) {
        uint256 recordCount = sKokoa.unstakeCount(_user);
        uint256 length = Math.min(recordCount, 20);

        unstakeHistory = new UnstakeRecord[](length);
        uint256 unstakeDuration = sKokoa.lockUpPeriod();

        for (uint256 i = 0; i < length; i++) {
            uint256 recordIndex = recordCount - i - 1;
            bool isClaimed;
            (uint256 blockTime, uint256 amount) = sKokoa.unstakeRecord(_user, recordIndex);
            uint256 completionTime = blockTime + unstakeDuration;
            if (recordIndex + 1 <= sKokoa.claimCount(_user)) {
                isClaimed = true;
            } else {
                isClaimed = false;
            }
            unstakeHistory[i] = UnstakeRecord(recordIndex, amount, completionTime, isClaimed);
        }
    }

    function getLatestKlayUnstakeHistory(address _user) public view returns (UnstakeRecord[] memory unstakeHistory) {
        uint256 recordCount = bklay.unstakeRecordCount(_user);
        uint256 length = Math.min(recordCount, 20);

        unstakeHistory = new UnstakeRecord[](length);

        for (uint256 i = 0; i < length; i++) {
            uint256 recordIndex = recordCount - i;
            (, uint256 amount, uint256 completionTime, bool isClaimed) = bklay.unstakeRecord(_user, recordIndex);
            unstakeHistory[i] = UnstakeRecord(recordIndex, amount, completionTime, isClaimed);
        }
    }

    function getAccountCollateralInfos(address _user, bytes32[] calldata _collateralTypes)
        public
        view
        returns (AccountCollateralInfo[] memory accountCollateralInfos)
    {
        accountCollateralInfos = new AccountCollateralInfo[](_collateralTypes.length);
        for (uint256 i = 0; i < _collateralTypes.length; i++) {
            (, , uint256 rate, , , ) = ledger.collateralInfo(_collateralTypes[i]);
            (uint256 lockedCollateral, uint256 loan) = ledger.accountInfo(_collateralTypes[i], _user);
            accountCollateralInfos[i] = AccountCollateralInfo(_collateralTypes[i], lockedCollateral, (loan * rate) / WadRayMath.RAY);
        }
    }

    function getDksdApr() public view returns (uint256 dksdApr) {
        uint256 targetDksdApr = ksdRewardManager.targetDksdApr();
        uint256 earnSumInRange = ksdRewardManager.earnSumInRange();
        uint256 earnCursor = ksdRewardManager.earnCursor();
        if (ksdRewardManager.earnHistoriesLength() <= 0) {
            return 0;
        }
        (uint256 oldestTimstamp, uint256 oldestAmount) = ksdRewardManager.earnHistories(earnCursor);
        uint256 estimatedRange = block.timestamp - oldestTimstamp;
        if (estimatedRange != 0 && dksd.totalSupply() > 0) {
            uint256 estimatedKsdPerYear = ((earnSumInRange - oldestAmount) * 365 days) / estimatedRange;
            dksdApr = Math.min(targetDksdApr * 100, (estimatedKsdPerYear * 10000) / dksd.totalSupply());
        } else {
            dksdApr = 0;
        }
    }

    function getSKokoaKsdApr(uint256 kokoaPrice) public view returns (uint256 skokoaApr) {
        uint256 targetDksdApr = ksdRewardManager.targetDksdApr();
        uint256 earnSumInRange = ksdRewardManager.earnSumInRange();
        uint256 earnCursor = ksdRewardManager.earnCursor();
        if (ksdRewardManager.earnHistoriesLength() <= 0) {
            return 0;
        }
        (uint256 oldestTimstamp, uint256 oldestAmount) = ksdRewardManager.earnHistories(earnCursor);
        uint256 estimatedRange = block.timestamp - oldestTimstamp;
        if (estimatedRange != 0 && skokoa.totalSupply() * kokoaPrice > 0) {
            uint256 estimatedKsdPerYear = ((earnSumInRange - oldestAmount) * 365 days) / estimatedRange;
            uint256 dksdInterestPerYear = Math.min((dksd.totalSupply() * targetDksdApr) / 100, estimatedKsdPerYear);
            uint256 skokoaInterestPerYear = estimatedKsdPerYear - dksdInterestPerYear;
            skokoaApr = ((skokoaInterestPerYear * 10000) * 10**18) / (skokoa.totalSupply() * kokoaPrice);
        } else {
            skokoaApr = 0;
        }
    }

    receive() external payable {}

    // -- for creating pool --
    function withdrawAllToken(address _token, address _recipient) external onlyOwner {
        bool success;
        if (_token == address(0)) {
            (success, ) = _recipient.call{value: address(this).balance}("");
        } else {
            success = IKIP7(_token).transfer(_recipient, IKIP7(_token).balanceOf(address(this)));
        }
        require(success, "Helper::withdrawAllToken: failed");
    }

    function pause(address deployed) external onlyOwner {
        Pausable(deployed).pause();
    }

    function unpause(address deployed) external onlyOwner {
        Pausable(deployed).unpause();
    }

    function createKlayPoolWithPaused(address token, uint256 amount) external payable onlyOwner {
        Pausable(token).unpause();
        createKlayPool(token, amount);
        Pausable(token).pause();
    }

    function createKlayPool(address token, uint256 amount) public payable onlyOwner {
        IKIP7(token).transferFrom(msg.sender, address(this), amount);
        IKIP7(token).approve(address(factory), amount);
        factory.createKlayPool{value: msg.value}(token, amount, 30);
    }

    function createKctPoolWithPausedA(
        address tokenA,
        uint256 amountA,
        address tokenB,
        uint256 amountB
    ) external onlyOwner {
        Pausable(tokenA).unpause();
        createKctPool(tokenA, amountA, tokenB, amountB);
        Pausable(tokenA).pause();
    }

    function createKctPoolWithPausedB(
        address tokenA,
        uint256 amountA,
        address tokenB,
        uint256 amountB
    ) external onlyOwner {
        Pausable(tokenB).unpause();
        createKctPool(tokenA, amountA, tokenB, amountB);
        Pausable(tokenB).pause();
    }

    function createKctPoolWithPausedAll(
        address tokenA,
        uint256 amountA,
        address tokenB,
        uint256 amountB
    ) external onlyOwner {
        Pausable(tokenA).unpause();
        Pausable(tokenB).unpause();
        createKctPool(tokenA, amountA, tokenB, amountB);
        Pausable(tokenA).pause();
        Pausable(tokenB).pause();
    }

    function createKctPool(
        address tokenA,
        uint256 amountA,
        address tokenB,
        uint256 amountB
    ) public onlyOwner {
        IKIP7(tokenA).transferFrom(msg.sender, address(this), amountA);
        IKIP7(tokenB).transferFrom(msg.sender, address(this), amountB);
        IKIP7(tokenA).approve(address(factory), amountA);
        IKIP7(tokenB).approve(address(factory), amountB);
        factory.createKctPool(tokenA, amountA, tokenB, amountB, 30);
    }

    function addKlayLiquidityWithPaused(address token, uint256 amount) external payable onlyOwner {
        Pausable(token).unpause();
        addKlayLiquidity(token, amount);
        Pausable(token).pause();
    }

    function addKlayLiquidity(address token, uint256 amount) public payable onlyOwner {
        IKIP7(token).transferFrom(msg.sender, address(this), amount);
        address exchange = factory.tokenToPool(address(0), token);
        IKIP7(token).approve(address(exchange), amount);
        address _tokenA = IKlaytnExchange(exchange).tokenA();
        address _tokenB = IKlaytnExchange(exchange).tokenB();
        require(address(0) == _tokenA && token == _tokenB, "Helper::addKlayLiquidity: invalid token order");
        IKlaytnExchange(exchange).addKlayLiquidity{value: msg.value}(amount);
    }

    function addKctLiquidityWithPausedA(
        address tokenA,
        uint256 amountA,
        address tokenB,
        uint256 amountB
    ) external onlyOwner {
        Pausable(tokenA).unpause();
        addKctLiquidity(tokenA, amountA, tokenB, amountB);
        Pausable(tokenA).pause();
    }

    function addKctLiquidityWithPausedB(
        address tokenA,
        uint256 amountA,
        address tokenB,
        uint256 amountB
    ) external onlyOwner {
        Pausable(tokenB).unpause();
        addKctLiquidity(tokenA, amountA, tokenB, amountB);
        Pausable(tokenB).pause();
    }

    function addKctLiquidityWithPausedAll(
        address tokenA,
        uint256 amountA,
        address tokenB,
        uint256 amountB
    ) external onlyOwner {
        Pausable(tokenA).unpause();
        Pausable(tokenB).unpause();
        addKctLiquidity(tokenA, amountA, tokenB, amountB);
        Pausable(tokenA).pause();
        Pausable(tokenB).pause();
    }

    function addKctLiquidity(
        address tokenA,
        uint256 amountA,
        address tokenB,
        uint256 amountB
    ) public onlyOwner {
        IKIP7(tokenA).transferFrom(msg.sender, address(this), amountA);
        IKIP7(tokenB).transferFrom(msg.sender, address(this), amountB);
        address exchange = factory.tokenToPool(tokenA, tokenB);
        IKIP7(tokenA).approve(address(exchange), amountA);
        IKIP7(tokenB).approve(address(exchange), amountB);
        address _tokenA = IKlaytnExchange(exchange).tokenA();
        address _tokenB = IKlaytnExchange(exchange).tokenB();
        require(tokenA == _tokenA && tokenB == _tokenB, "Helper::addKctLiquidity: invalid token order");
        IKlaytnExchange(exchange).addKctLiquidity(amountA, amountB);
    }
}
