pragma solidity 0.8.3;

import "../../interface/IKokoaRewardManager.sol";
import "../../interface/ILedger.sol";
import "../../interface/IIssuedKSDPool.sol";
import "../../library/AccessControl.sol";
import "../../library/Pausable.sol";

contract IssuedKSDPool is IIssuedKSDPool, AccessControl, Pausable {
    // --- Data ---
    ILedger public ledger;
    IKokoaRewardManager public rewardManger;

    // --- function ---
    function __IssuedKSDPool_init(address ledger_, address kokoaRewardManager_) public initializer {
        __Pausable_init();
        ledger = ILedger(ledger_);
        rewardManger = IKokoaRewardManager(kokoaRewardManager_);
    }

    function COLLATERAL_TYPE() public pure virtual returns (bytes32) {
        return bytes32("klay");
    }

    function totalSupply() external view override returns (uint256) {
        (, uint256 totalLoan, , , , ) = ledger.collateralInfo(COLLATERAL_TYPE());
        return totalLoan;
    }

    function balanceOf(address usr) external view override returns (uint256) {
        (, uint256 loan) = ledger.accountInfo(COLLATERAL_TYPE(), usr);
        return loan;
    }

    function onDeposit(uint256 amount, address to) external override {
        require(msg.sender == address(ledger), "IssuedKSDPool::onDeposit: not authorized");
        rewardManger.onDeposit(amount, to);
    }

    function onWithdraw(uint256 amount, address to) external override {
        require(msg.sender == address(ledger), "IssuedKSDPool::onWithdraw: not authorized");
        rewardManger.onWithdraw(amount, to);
    }

    function onChanged(int256 amount, address to) external override {
        require(msg.sender == address(ledger), "IssuedKSDPool::onChanged: not authorized");
        rewardManger.onChanged(amount, to);
    }
}
