pragma solidity 0.8.3;

import "../interface/IStakingPool.sol";
import "../interface/kip/IKIP7.sol";
import "../interface/klayswap/IKlaytnExchangeImpl.sol";
import "../interface/IKSPLpMigrator.sol";
import "../interface/i4i/IMetaPoolDeposit.sol";
import "../library/AccessControl.sol";

contract KSD4EYELpMigrator is IKSPLpMigrator, AccessControl {
    uint256 public constant N_ALL_COINS = 4;
    IMetaPoolDeposit public i4iKSDDeposit;
    address public ksd4EyeLpStakingPool;

    function __KSD4EYELpMigrator_init(address _i4iKSDDeposit, address _ksd4EyeLpStakingPool) external initializer {
        i4iKSDDeposit = IMetaPoolDeposit(_i4iKSDDeposit);
        ksd4EyeLpStakingPool = _ksd4EyeLpStakingPool;
    }

    function config(bytes32 what, address data) external onlyAdmin {
        if (what == "i4iKSDDeposit") {
            i4iKSDDeposit = IMetaPoolDeposit(data);
        } else if (what == "ksd4EyeLpStakingPool") {
            ksd4EyeLpStakingPool = data;
        } else revert("KSD4EYELpMigrator::config: unrecognized-param");
    }

    function calcMigrateFromKSP(address kspLpToken, uint256 amount) external view override returns (uint256) {
        if (amount == 0) {
            return 0;
        }
        address tokenA = IKlaytnExchange(kspLpToken).tokenA();
        address tokenB = IKlaytnExchange(kspLpToken).tokenB();

        (uint256 poolA, uint256 poolB) = IKlaytnExchange(kspLpToken).getCurrentPool();
        uint256 totalSupply = IKlaytnExchange(kspLpToken).totalSupply();

        require(amount <= totalSupply, "KSD4EYELpMigrator:: calcMigrateFromKSP: invalid amount");
        uint256 amountA = (poolA * amount) / totalSupply;
        uint256 amountB = (poolB * amount) / totalSupply;

        uint256[] memory amounts = new uint256[](N_ALL_COINS);
        amounts[i4iKSDDeposit.coinIndex(tokenA)] = amountA;
        amounts[i4iKSDDeposit.coinIndex(tokenB)] = amountB;

        return i4iKSDDeposit.calcTokenAmount(amounts, true);
    }

    function migrateFromKSP(
        address kspLpToken,
        address to,
        uint256 amount,
        uint256 minMintAmount
    ) external override {
        if (amount == 0) {
            return;
        }
        IKIP7(kspLpToken).transferFrom(msg.sender, address(this), amount);
        address tokenA = IKlaytnExchange(kspLpToken).tokenA();
        address tokenB = IKlaytnExchange(kspLpToken).tokenB();

        uint256 beforeBalanceA = IKIP7(tokenA).balanceOf(address(this));
        uint256 beforeBalanceB = IKIP7(tokenB).balanceOf(address(this));
        IKlaytnExchange(kspLpToken).removeLiquidity(amount);
        uint256 amountA = IKIP7(tokenA).balanceOf(address(this)) - beforeBalanceA;
        uint256 amountB = IKIP7(tokenB).balanceOf(address(this)) - beforeBalanceB;
        IKIP7(tokenA).approve(address(i4iKSDDeposit), amountA);
        IKIP7(tokenB).approve(address(i4iKSDDeposit), amountB);

        uint256[] memory amounts = new uint256[](N_ALL_COINS);
        amounts[i4iKSDDeposit.coinIndex(tokenA)] = amountA;
        amounts[i4iKSDDeposit.coinIndex(tokenB)] = amountB;
        uint256 lpAmount = i4iKSDDeposit.addLiquidity(amounts, minMintAmount);

        IKIP7(i4iKSDDeposit.token()).approve(ksd4EyeLpStakingPool, lpAmount);
        IStakingPool(ksd4EyeLpStakingPool).stake(to, lpAmount);

        emit MigrateFromKSP(kspLpToken, msg.sender, to, amount, minMintAmount);
    }
}
