pragma solidity 0.8.3;

import "../library/Pausable.sol";
import "../library/AccessControl.sol";
import "../interface/IKSDGateway.sol";
import "../interface/ILedger.sol";
import "../interface/kip/IKIP7Extended.sol";

/// @title KSD input/output contract
/// @notice A collection of functions related to KSD move
contract KSDGateway is IKSDGateway, AccessControl, Pausable {
    function __KSDGateway_init() public initializer {
        __Pausable_init();
    }

    // --- data ---
    ILedger public ledger; // ledger contract which manage lending.
    IKIP7Extended public ksd; // Stablecoin Token in kokoa system.
    uint256 constant ONE = 10**27; // constant variable.

    // --- config ---
    /// @notice Initialize interface variable.
    function config(bytes32 what, address data) external onlyAdmin whenNotPaused {
        if (what == "ledger") ledger = ILedger(data);
        else if (what == "ksd") ksd = IKIP7Extended(data);
        else revert("KSDGateway::config:unrecognized-param");
    }

    // --- function ---
    /// @notice Ksd redemption & ksd burn
    /// @param usr Target receiving ksd from ledger.
    /// @param wad Amount of ksd token.
    function join(address usr, uint256 wad) external override {
        ledger.sendKsd(address(this), usr, ONE * (wad + 1));
        ksd.burn(msg.sender, wad);
    }

    /// @notice Ksd mint & borrow ksd (loan)
    /// @param usr Who issues ksd.
    /// @param wad Amount of ksd token.
    function exit(address usr, uint256 wad) external override whenNotPaused {
        ledger.sendKsd(msg.sender, address(this), ONE * wad);
        ksd.mint(usr, wad);
    }

    /// @notice Ksd redemption & ksd burn.
    /// @dev Acting on behalf.
    /// @param ksdOwner The owner of the ksd to burn.
    /// @param usr Target receiving ksd from ledger.
    /// @param wad Amount of ksd token.
    function joinFrom(
        address ksdOwner,
        address usr,
        uint256 wad
    ) external override onlyAdmin {
        ledger.sendKsd(address(this), usr, ONE * (wad + 1));
        ksd.burn(ksdOwner, wad);
    }

    /// @notice Ksd mint & borrow ksd (loan)
    /// @dev Acting on behalf (Transaction through ksd owner)
    /// @param ksdOwner Someone who will provide ksd from ledger to issue ksd.
    /// @param usr Who issues ksd.
    /// @param wad Amount of ksd token.
    function exitFrom(
        address ksdOwner,
        address usr,
        uint256 wad
    ) external override whenNotPaused onlyAdmin {
        ledger.sendKsd(ksdOwner, address(this), ONE * wad);
        ksd.mint(usr, wad);
    }
}
