pragma solidity 0.8.3;

import "./TokenStakingPool.sol";
import "../../interface/IKSPLpMigrator.sol";

contract KSDKUsdcLpStakingPool is TokenStakingPool {
    function __KSDKUsdcLpStakingPool_init(address token, address kokoaRewardManager) public initializer {
        __TokenStakingPool_init(token, kokoaRewardManager);
    }

    function migrate(address migrator, uint256 minNewLpAmount) external {
        uint256 amount = balanceOf[msg.sender];
        unstake(address(this), amount);
        require(token.approve(migrator, amount), "approve failed");
        IKSPLpMigrator(migrator).migrateFromKSP(address(token), msg.sender, amount, minNewLpAmount);
    }
}
