pragma solidity 0.8.3;
pragma experimental ABIEncoderV2;

import "../interface/IRewardPool.sol";
import "../library/openzeppelin/contracts/utils/SafeCast.sol";
import "../library/openzeppelin/contracts/utils/Math.sol";
import "../interface/IKSDRewardManager.sol";
import "../interface/IDKSD.sol";
import "../library/AccessControl.sol";
import "../library/Pausable.sol";
import "../interface/IDistributor.sol";
import "../interface/kip/IKIP7.sol";

/// @title A manager about ksd rewards.
/// @dev It manages all ksd rewards. It's base code is sushiswap.
/// Therefore, the way to distribute reward is very similar to sushiswap.
/// The main difference between sushiswap and KSDRewardManager lies in staking.
/// Sushiswap forces to stake token, but KSDRewardManager does not own token.
/// It just expect registered pool to call onDeposit/onWithdraw/onChanged, and
/// get balance and totalSupply of pool to calculate how to distribute reward.
/// Additionally, unlike SushiSwap where SUSHI tokens for liquidity mining are generated blockwise,
/// KSDRewardManager only distributes the accumulated yield that the protocol generated.
/// Most of the code here is the same as KokoaRewardManager except the distribution function.
contract KSDRewardManager is IKSDRewardManager, AccessControl, Pausable {
    IKIP7 public ksd;
    IDKSD public dksd;
    uint256 public __legacy1; // 사용 안함

    mapping(address => PoolInfo) public override poolInfo;
    IRewardPool[] public override rewardPool;

    mapping(address => mapping(address => UserInfo)) public override userInfo;
    uint256 public override totalAllocPoint;

    uint256 private constant ACC_REWARD_PRECISION = 1e12;
    uint256 public override targetDksdApr; // 100 => 100%

    uint256 public override range;
    EarnHistory[] public override earnHistories;
    uint256 public override earnCursor;
    uint256 public override earnSumInRange;

    modifier onlyRewardPool() {
        require(poolInfo[msg.sender].init, "KSDRewardManager::onlyRewardPool: invalid reward pool");
        _;
    }

    function __KSDRewardManager_init(address ksd_, address dksd_) public initializer {
        __Pausable_init();
        ksd = IKIP7(ksd_);
        dksd = IDKSD(dksd_);
    }

    // --- config ---
    function config(bytes32 what, uint256 data) external onlyAdmin whenNotPaused {
        if (what == "targetDksdApr") {
            targetDksdApr = data;
        } else if (what == "range") {
            range = data;
        } else revert("KSDRewardManager::config: unrecognized-param");
    }

    /// @notice number of registered pools
    function poolLength() external view override returns (uint256) {
        return rewardPool.length;
    }

    /// @notice all registered pools
    function rewardPools() external view override returns (address[] memory pools) {
        pools = new address[](rewardPool.length);
        for (uint256 i = 0; i < rewardPool.length; i++) {
            pools[i] = address(rewardPool[i]);
        }
    }

    function add(uint256 allocPoint, address pool) external override onlyOwner {
        require(!poolInfo[pool].init, "KSDRewardManager::add: pool is already added");
        totalAllocPoint = totalAllocPoint + allocPoint;
        rewardPool.push(IRewardPool(pool));

        poolInfo[pool] = PoolInfo({allocPoint: allocPoint, ksdReward: 0, accRewardPerShare: 0, init: true});
        emit LogPoolAddition(rewardPool.length - 1, allocPoint, pool);
    }

    function set(address pool, uint256 allocPoint) external override onlyOwner {
        require(poolInfo[pool].init, "KSDRewardManager::set: pool is not initialized");
        totalAllocPoint = totalAllocPoint - poolInfo[pool].allocPoint + allocPoint;
        poolInfo[pool].allocPoint = allocPoint;
        emit LogSetPool(pool, allocPoint);
    }

    function claimableReward(address pool, address usr) external view override whenNotPaused returns (uint256 claimable) {
        require(poolInfo[pool].init, "KSDRewardManager::claimableReward: pool is not initialized");
        PoolInfo memory _poolInfo = poolInfo[pool];
        UserInfo storage user = userInfo[pool][usr];
        uint256 accRewardPerShare = _poolInfo.accRewardPerShare;
        uint256 totalSupply = IRewardPool(pool).totalSupply();
        if (_poolInfo.ksdReward > 0 && totalSupply != 0) {
            uint256 reward = _poolInfo.ksdReward;
            accRewardPerShare = accRewardPerShare + ((reward * ACC_REWARD_PRECISION) / totalSupply);
        }
        uint256 userBalance = IRewardPool(pool).balanceOf(usr);
        claimable = SafeCast.toUint256(SafeCast.toInt256((userBalance * accRewardPerShare) / ACC_REWARD_PRECISION) - user.rewardDebt);
    }

    function massUpdatePools(address[] calldata pools) external override whenNotPaused {
        uint256 len = pools.length;
        for (uint256 i = 0; i < len; ++i) {
            _updatePool(pools[i]);
        }
    }

    function accRewardPrecision() external pure override returns (uint256) {
        return ACC_REWARD_PRECISION;
    }

    function updatePool(address pool) external override whenNotPaused {
        _updatePool(pool);
    }

    function _updatePool(address pool) internal {
        require(poolInfo[pool].init, "KSDRewardManager::_updatePool: pool is not initialized");
        PoolInfo memory _poolInfo = poolInfo[pool];
        uint256 totalSupply = IRewardPool(pool).totalSupply();
        if (_poolInfo.ksdReward > 0 && totalSupply > 0) {
            uint256 reward = _poolInfo.ksdReward;
            _poolInfo.accRewardPerShare = _poolInfo.accRewardPerShare + ((reward * ACC_REWARD_PRECISION) / totalSupply);
            _poolInfo.ksdReward = 0;
            poolInfo[pool] = _poolInfo;
            emit LogUpdatePool(pool, block.timestamp, totalSupply, _poolInfo.accRewardPerShare);
        }
    }

    function onDeposit(uint256 amount, address to) public override onlyRewardPool whenNotPaused {
        _updatePool(msg.sender);
        PoolInfo memory pool = poolInfo[msg.sender];
        UserInfo storage user = userInfo[msg.sender][to];

        user.rewardDebt += SafeCast.toInt256((amount * pool.accRewardPerShare) / ACC_REWARD_PRECISION);
        emit Deposit(msg.sender, amount, to);
    }

    function onWithdraw(uint256 amount, address to) public override onlyRewardPool whenNotPaused {
        _updatePool(msg.sender);
        PoolInfo memory pool = poolInfo[msg.sender];
        UserInfo storage user = userInfo[msg.sender][to];

        user.rewardDebt -= SafeCast.toInt256((amount * pool.accRewardPerShare) / ACC_REWARD_PRECISION);
        emit Withdraw(msg.sender, amount, to);
    }

    function onChanged(int256 amount, address to) external override onlyRewardPool whenNotPaused {
        if (amount > 0) {
            onDeposit(SafeCast.toUint256(amount), to);
        }
        if (amount < 0) {
            onWithdraw(SafeCast.toUint256(-amount), to);
        }
    }

    function _claimReward(
        address pool,
        address from,
        address to
    ) internal returns (bool success) {
        _updatePool(pool);
        PoolInfo memory _poolInfo = poolInfo[pool];
        UserInfo storage user = userInfo[pool][from];
        uint256 userBalance = IRewardPool(pool).balanceOf(from);
        int256 accumulatedReward = SafeCast.toInt256((userBalance * _poolInfo.accRewardPerShare) / ACC_REWARD_PRECISION);
        uint256 _claimableReward = SafeCast.toUint256(accumulatedReward - user.rewardDebt);
        if (_claimableReward == 0) {
            success = false;
            return success;
        }

        // Effects
        user.rewardDebt = accumulatedReward;

        // Interactions
        ksd.transfer(to, _claimableReward);
        user.claimedReward = user.claimedReward + _claimableReward;
        emit ClaimReward(pool, from, _claimableReward);
        success = true;
    }

    function claimReward(address pool, address to) external override whenNotPaused returns (bool success) {
        return _claimReward(pool, msg.sender, to);
    }

    function claimRewardByOther(address pool, address usr) external override whenNotPaused returns (bool success) {
        success = _claimReward(pool, usr, usr);
    }

    function claimRewardAllByOther(address usr) external override whenNotPaused returns (bool success) {
        success = true;
        for (uint256 i = 0; i < rewardPool.length; i++) {
            address pool = address(rewardPool[i]);
            bool _success = _claimReward(pool, usr, usr);
            success = success && _success;
        }
    }

    function earnHistoriesLength() external view override returns (uint256) {
        return earnHistories.length;
    }

    function _insertEarnHistory(uint256 earning) internal {
        earnHistories.push(EarnHistory({timestamp: block.timestamp, amount: earning}));

        earnSumInRange += earning;
        uint256 start = block.timestamp - range;
        uint256 cursor;
        uint256 minusAmount = 0;
        for (cursor = earnCursor; (earnHistories[cursor].timestamp < start) && (cursor < earnCursor + 100); cursor++) {
            minusAmount += earnHistories[cursor].amount;
        }
        earnCursor = cursor;
        earnSumInRange -= minusAmount;
    }

    /// @notice distribute ksd reward to pool
    /// @param earning amount of ksd to distribute
    /// onlyOwner (optional)
    function distribute(uint256 earning) external override {
        if (earning <= 0) {
            return;
        }

        ksd.transferFrom(msg.sender, address(this), earning);

        _insertEarnHistory(earning);
        uint256 dksdReward = 0;
        uint256 estimatedRange = block.timestamp - earnHistories[earnCursor].timestamp;

        if (estimatedRange != 0) {
            uint256 estimatedKsdPerYear = (earnSumInRange * 365 days) / estimatedRange;
            uint256 dksdInterestPerYear = Math.min((dksd.totalSupply() * targetDksdApr) / 100, estimatedKsdPerYear);
            dksdReward = (earning * dksdInterestPerYear) / estimatedKsdPerYear;
        }

        // step 1. distribute ksd reward to dksd
        ksd.approve(address(dksd), dksdReward);
        dksd.earn(dksdReward);
        uint256 leftEarning = earning - dksdReward;

        // step 2. distribute ksd reward to rewardPool
        for (uint256 i = 0; i < rewardPool.length; ++i) {
            address pool = address(rewardPool[i]);
            PoolInfo storage _poolInfo = poolInfo[pool];
            _poolInfo.ksdReward = _poolInfo.ksdReward + ((leftEarning * _poolInfo.allocPoint) / totalAllocPoint);
        }
        emit Distribute(block.timestamp, earning);
    }
