pragma solidity 0.8.3;

import "../library/openzeppelin/contracts/math/SafeMath.sol";
import "../library/AccessControl.sol";
import "../library/Pausable.sol";
import "../interface/IKlayYield.sol";
import "../interface/IKlayYieldFactory.sol";
import "../interface/kip/IKIP7.sol";
import "../interface/IYieldFactory.sol";
import "../interface/IYield.sol";
import "../interface/IKsdRefundManager.sol";
import "../interface/IYieldManager.sol";
import "../interface/IDistributor.sol";

/// @title Management of strategies to operate the deposited token.
/// @notice Each strategy can stake token.
/// @dev Independent strategies are called yield.
abstract contract BaseYieldFactory is IYieldFactory, AccessControl, Pausable {
    // --- data ---
    IKIP7 public ksd;
    IKsdRefundManager public ksdRefundManager;
    IDistributor public ksdRewardManager;
    address public override token;
    address public yield;
    IYieldManager public yieldManager;

    function __BaseYieldFactory_init() public initializer {
        __Pausable_init();
    }

    function COLLATERAL_TYPE() public view virtual returns (bytes32);

    // --- config ---
    function config(bytes32 what, address data) external onlyAdmin whenNotPaused {
        if (what == "ksd") ksd = IKIP7(data);
        else if (what == "ksdRefundManager") ksdRefundManager = IKsdRefundManager(data);
        else if (what == "ksdRewardManager") ksdRewardManager = IDistributor(data);
        else if (what == "token") token = data;
        else if (what == "yield") yield = data;
        else if (what == "yieldManager") yieldManager = IYieldManager(data);
        else revert("BaseYieldFactory::config: unrecognized-param");
    }

    function defaultYieldList() internal view returns (address[] memory) {
        address[] memory list = new address[](1);
        list[0] = address(yield);
        return list;
    }

    function sortedYieldList() public view returns (address[] memory) {
        address[] memory list = new address[](1);
        list[0] = address(yield);
        for (uint256 i = 0; i < list.length; i++) {
            uint256 apr_i = IKlayYield(list[i]).expectedApr();
            for (uint256 j = i + 1; j < list.length; j++) {
                uint256 apr_j = IKlayYield(list[j]).expectedApr();
                if (apr_i > apr_j) {
                    apr_i = apr_j;
                    address tmp = list[j];
                    list[j] = list[i];
                    list[i] = tmp;
                }
            }
        }
        return list;
    }

    function totalValue() public view override returns (uint256) {
        address[] memory yieldList = defaultYieldList();
        uint256 value = 0;
        for (uint256 i = 0; i < yieldList.length; i++) {
            value += IYield(yieldList[i]).totalValue();
        }
        return value;
    }

    function totalAmount() public view override returns (uint256) {
        address[] memory yieldList = defaultYieldList();
        uint256 amount = 0;
        for (uint256 i = 0; i < yieldList.length; i++) {
            amount += IYield(yieldList[i]).totalAmount();
        }
        return amount;
    }

    function stake(uint256 tokenAmount) external override onlyAdmin {
        IKIP7(token).transferFrom(msg.sender, address(this), tokenAmount);
        IKIP7(token).approve(yield, tokenAmount);
        address[] memory yieldList = sortedYieldList();
        address yield = yieldList[yieldList.length - 1];
        IYield(yield).stake(tokenAmount);
    }

    function unstake(uint256 tokenAmount) external override onlyAdmin {
        address[] memory yieldList = sortedYieldList();
        uint256 amountToUnstake = tokenAmount;
        for (uint256 i = 0; i < yieldList.length; i++) {
            if (amountToUnstake == 0) break;

            IYield yield = IYield(yieldList[i]);
            if (yield.totalAmount() >= amountToUnstake) {
                yield.unstake(amountToUnstake);
                amountToUnstake -= amountToUnstake;
            } else {
                uint256 _amount = yield.totalAmount();
                yield.unstake(_amount);
                amountToUnstake = amountToUnstake - _amount;
            }
        }
        require(amountToUnstake == 0, "BaseYieldFactory::unstake: fail");
    }

    function applyProfitAll() external override {
        address[] memory yieldList = defaultYieldList();
        uint256 earnedKsdAmount = 0;
        for (uint256 i = 0; i < yieldList.length; i++) {
            uint256 ksdAmount = IYield(yieldList[i]).applyProfit();
            ksd.transferFrom(yieldList[i], address(this), ksdAmount);
            earnedKsdAmount += ksdAmount;
        }

        bytes32 collateralType = COLLATERAL_TYPE();
        ksd.approve(address(yieldManager), earnedKsdAmount);
        yieldManager.manageProfit(collateralType, earnedKsdAmount);
    }
}
