pragma solidity 0.8.3;

import "../library/AccessControl.sol";
import "../library/Pausable.sol";
import "../library/WadRayMath.sol";
import "../library/openzeppelin/contracts/utils/Math.sol";
import "../interface/klayswap/IKlaytnExchangeImpl.sol";
import "../interface/IKlaySwapBridge.sol";
import "../interface/IYield.sol";

abstract contract JoblessYield is IYield, AccessControl, Pausable {
    IKIP7 public token;
    address public yieldFactory;
    uint256 public _totalAmount;
    address public receiver;

    function __JoblessYield_init() public initializer {
        __Pausable_init();
    }

    // --- config ---
    function config(bytes32 what, address data) external onlyAdmin {
        if (what == "token") token = IKIP7(data);
        else if (what == "yieldFactory") yieldFactory = data;
        else if (what == "receiver") receiver = data;
        else revert("JoblessYield::config:unrecognized-param");
    }

    function expectedApr() external view override returns (uint256) {
        return 1;
    }

    function toValue(uint256 tokenAmount) public view returns (uint256) {
        return tokenAmount;
    }

    function toAmount(uint256 value) public view returns (uint256) {
        return value;
    }

    function totalValue() external view override returns (uint256) {
        return _totalAmount;
    }

    function totalAmount() external view override returns (uint256) {
        return _totalAmount;
    }

    // --- functions ---
    /// @notice stake token to this contract
    /// @param tokenAmount Amount of token to stake.
    function stake(uint256 tokenAmount) external override onlyAdmin whenNotPaused {
        token.transferFrom(msg.sender, address(this), tokenAmount);
        _totalAmount += tokenAmount;
        emit Stake(msg.sender, tokenAmount);
    }

    /// @notice unstake token from this contract.
    /// @param tokenAmount Amount of token to unstake.
    function unstake(uint256 tokenAmount) public override onlyAdmin whenNotPaused {
        token.transfer(receiver, tokenAmount);
        _totalAmount -= tokenAmount;
        emit Unstake(msg.sender, tokenAmount);
    }

    /// @dev turn token into ksd
    function tokenToKsd(uint256 tokenAmount) internal virtual {}

    /// @notice Convert the generated reward to ksd and distribute it to the beneficiaries.
    function applyProfit() external override onlyAdmin returns (uint256 ksdAmount) {
        return 0;
    }
}
