/// @title KSD input/output contract
/// @notice A collection of functions related to KSD move
interface IKSDGateway {
    /// @notice ksd redemption & ksd burn
    function join(address usr, uint256 wad) external;

    /// @notice ksd mint & borrow ksd (loan)
    function exit(address usr, uint256 wad) external;

    /// @notice Ksd redemption & ksd burn.
    /// @dev Acting on behalf.
    function joinFrom(
        address ksdOwner,
        address usr,
        uint256 wad
    ) external;

    /// @notice Ksd mint & borrow ksd (loan)
    /// @dev Acting on behalf (Transaction through ksd owner)
    function exitFrom(
        address ksdOwner,
        address usr,
        uint256 wad
    ) external;
}
