pragma solidity 0.8.3;

import "../interface/kip/IKIP7.sol";
import "../interface/klayswap/IKlaytnExchangeImpl.sol";
import "../interface/klayswap/IKlaytnFactoryImpl.sol";
import "../interface/IAuctionHouse.sol";
import "../interface/IBKlay.sol";
import "../interface/IFlashBorrower.sol";
import "../interface/IKlayGateway.sol";
import "../interface/IKSDGateway.sol";
import "../interface/ILedger.sol";
import "../interface/IOtcFund.sol";
import "../library/AccessControl.sol";
import "../library/Pausable.sol";

contract KlayAuctionTaker is AccessControl, Pausable, IFlashBorrower {
    IBKlay public bKlay;
    IKIP7 public ksd;
    IKSDGateway public ksdGateway;
    IKlayGateway public klayGateway;
    ILedger public ledger;
    IAuctionHouse public auctionHouse;

    IOtcFund public otcFund;

    IKlaytnFactory public klayswap;
    IKlaytnExchange public klayKsdPool;

    uint256 public minProfitRate;

    uint256 constant RAY = 10**27;
    bytes32 constant KLAY_COLLATERAL_TYPE = bytes32("klay");

    function __Taker_init() public initializer {
        __Pausable_init();
    }

    function config(bytes32 what, address data) external onlyAdmin {
        if (what == "bKlay") bKlay = IBKlay(data);
        else if (what == "ksd") ksd = IKIP7(data);
        else if (what == "ksdGateway") ksdGateway = IKSDGateway(data);
        else if (what == "klayGateway") klayGateway = IKlayGateway(data);
        else if (what == "ledger") ledger = ILedger(data);
        else if (what == "auctionHouse") auctionHouse = IAuctionHouse(data);
        else if (what == "otcFund") otcFund = IOtcFund(data);
        else if (what == "klayswap") klayswap = IKlaytnFactory(data);
        else if (what == "klayKsdPool") klayKsdPool = IKlaytnExchange(data);
        else revert("Taker::config:unrecognized-param");
    }

    function config(bytes32 what, uint256 data) external onlyAdmin {
        if (what == "minProfitRate") minProfitRate = data;
        else revert("Taker::config:unrecognized-param");
    }

    function withdrawKlay(address to, uint256 amount) external onlyAdmin {
        if (amount == 0) {
            (bool success, ) = to.call{value: address(this).balance}("");
            require(success);
        } else {
            (bool success, ) = to.call{value: amount}("");
            require(success);
        }
    }

    function withdrawToken(
        IKIP7 token,
        address to,
        uint256 amount
    ) external onlyAdmin {
        if (amount == 0) {
            token.transfer(to, token.balanceOf(address(this)));
        } else {
            token.transfer(to, amount);
        }
    }

    function joinKsdGateway(uint256 amount) external onlyAdmin {
        ksdGateway.join(address(this), amount);
    }

    function exitKsdGateway(uint256 amount) external onlyAdmin {
        ksdGateway.exit(address(this), amount);
    }

    function stakeKlayGateway(uint256 amount) external onlyAdmin {
        klayGateway.stake{value: amount}();
    }

    function unstakeKlayGateway(uint256 amount) external onlyAdmin {
        klayGateway.unstakeInBKlay(amount);
    }

    function unstakeBKlay(uint256 amount) external onlyOperator {
        require(amount <= bKlay.balanceOf(address(this)));
        bKlay.unstakeKlay(address(this), amount);
    }

    function withdrawAKlayAndSwap(uint256 amount) external onlyOperator returns (uint256 klayAmount) {
        klayAmount = address(this).balance;
        require(amount > 1 && amount <= bKlay.balanceOf(address(this)));
        bKlay.withdrawKlay(address(this), amount);
        klayAmount = address(this).balance - klayAmount;
    }

    function pendingKlay() external view returns (uint256, uint256) {
        (uint256 completed, uint256 yet) = bKlay.pendingKlay(address(this));
        return (completed, yet);
    }

    function claimKlay() external {
        (uint256 completed, ) = bKlay.pendingKlay(address(this));
        if (completed > 0) {
            bKlay.claimKlay(address(this));
        }
    }

    function isProfitable(bytes32 colType, uint256 id) external view returns (bool) {
        require(colType == KLAY_COLLATERAL_TYPE);
        (, , uint256 bKlayAmount) = _amountsWithMaxProfit(id);
        return bKlayAmount != 0;
    }

    function debugAuction(uint256 id)
        external
        view
        returns (
            uint256 price,
            uint256 klayPriceMin,
            uint256 maxKsdAmount,
            uint256 klayAmount,
            uint256 ksdAmount,
            uint256 bKlayAmount
        )
    {
        // price: ray, sellAmount: wad, raiseAmount: rad
        (bool needsRedo, uint256 _price, uint256 sellAmount, uint256 raiseAmount) = auctionHouse.getStatus(id);
        price = _price;
        uint256 bound = auctionHouse.bound();
        if (needsRedo) {
            return (price, klayPriceMin, maxKsdAmount, klayAmount, ksdAmount, bKlayAmount);
        }

        // klay/ksd price in ray
        // ksd --(auction)-> bklay --(otc)-> klay
        // klay sell price must be higher than this
        klayPriceMin = (price * minProfitRate) / 1e9;
        uint256 fee = klayKsdPool.fee();
        klayPriceMin = (klayPriceMin * 10000) / (10000 - fee);

        (uint256 klayReserve, uint256 ksdReserve) = klayKsdPool.getCurrentPool();
        // swap until the spot price equals the minimum price
        uint256 ksdReserveAfterSwap = (sqrt(klayReserve) * sqrt(ksdReserve) * sqrt(klayPriceMin)) / sqrt(RAY);

        if (ksdReserveAfterSwap >= ksdReserve) {
            return (price, klayPriceMin, maxKsdAmount, klayAmount, ksdAmount, bKlayAmount);
        }

        ksdAmount = ksdReserve - ksdReserveAfterSwap;
        bKlayAmount = (ksdAmount * RAY) / price;
        maxKsdAmount = min(sellAmount * price, raiseAmount);
        if (ksdAmount * RAY >= maxKsdAmount) {
            ksdAmount = maxKsdAmount / RAY + 1;
            bKlayAmount = maxKsdAmount / price + 1;
        } else if (raiseAmount <= bound) {
            return (price, klayPriceMin, maxKsdAmount, klayAmount, ksdAmount, bKlayAmount);
        } else if (ksdAmount * RAY >= raiseAmount - bound) {
            ksdAmount = (raiseAmount - bound) / RAY + 1;
            bKlayAmount = (raiseAmount - bound) / price + 1;
        }
        klayAmount = klayKsdPool.estimateNeg(address(ksd), ksdAmount);
    }

    function _amountsWithMaxProfit(uint256 id)
        internal
        view
        returns (
            uint256 klayAmount,
            uint256 ksdAmount,
            uint256 bKlayAmount
        )
    {
        // TODO: consider otc balance
        // price: ray, sellAmount: wad, raiseAmount: rad
        (bool needsRedo, uint256 price, uint256 sellAmount, uint256 raiseAmount) = auctionHouse.getStatus(id);
        uint256 bound = auctionHouse.bound();
        if (needsRedo) {
            return (0, 0, 0);
        }

        // klay/ksd price in ray
        // ksd --(auction)-> bklay --(otc)-> klay
        // klay sell price must be higher than this
        uint256 klayPriceMin = (price * minProfitRate) / 1e9;
        uint256 fee = klayKsdPool.fee();
        klayPriceMin = (klayPriceMin * 10000) / (10000 - fee);

        (uint256 klayReserve, uint256 ksdReserve) = klayKsdPool.getCurrentPool();
        // swap until the spot price equals the minimum price
        uint256 ksdReserveAfterSwap = (sqrt(klayReserve) * sqrt(ksdReserve) * sqrt(klayPriceMin)) / sqrt(RAY);

        if (ksdReserveAfterSwap >= ksdReserve) {
            return (0, 0, 0);
        }

        ksdAmount = ksdReserve - ksdReserveAfterSwap;
        bKlayAmount = (ksdAmount * RAY) / price;
        uint256 maxKsdAmount = min(sellAmount * price, raiseAmount);
        if (ksdAmount * RAY >= maxKsdAmount) {
            ksdAmount = maxKsdAmount / RAY + 1;
            bKlayAmount = maxKsdAmount / price + 1;
        } else if (raiseAmount <= bound) {
            return (0, 0, 0);
        } else if (ksdAmount * RAY >= raiseAmount - bound) {
            ksdAmount = (raiseAmount - bound) / RAY + 1;
            bKlayAmount = (raiseAmount - bound) / price + 1;
        }
        klayAmount = klayKsdPool.estimateNeg(address(ksd), ksdAmount);
    }

    // flash loan klay -> take arbitrage -> repay bklay
    function _takeArbitrageWithFlashLoan(
        uint256 id,
        uint256 klayAmount,
        uint256 ksdAmount,
        uint256 bKlayAmount
    ) internal {
        bytes memory data = abi.encode(id, ksdAmount, bKlayAmount);
        otcFund.flashLoan(address(this), klayAmount, data);
    }

    function onFlashLoan(
        address account,
        uint256 klayAmount,
        uint256 fee,
        bytes calldata data
    ) external override returns (bool) {
        require(msg.sender == address(otcFund));
        (uint256 id, uint256 ksdAmount, uint256 bKlayAmount) = abi.decode(data, (uint256, uint256, uint256));
        _takeArbitrage(id, klayAmount, ksdAmount, bKlayAmount);
        bKlay.transfer(account, klayAmount + fee);
        return true;
    }

    // swap klay to ksd -> join ksd gateway -> take auction -> exit klay gateway
    function _takeArbitrage(
        uint256 id,
        uint256 klayAmount,
        uint256 ksdAmount,
        uint256 bKlayAmount
    ) internal {
        // swap to ksd
        klayswap.exchangeKlayNeg{value: klayAmount}(address(ksd), ksdAmount, new address[](0));

        // join ksd gateway
        ksdGateway.join(address(this), ksdAmount);

        // take auction
        auctionHouse.take(id, bKlayAmount, uint256(int256(-1)), address(this), "");

        // exit klay gateway
        klayGateway.unstakeInBKlay(ledger.token(KLAY_COLLATERAL_TYPE, address(this)));
    }

    function takeAndExchange(bytes32 colType, uint256 id) external onlyOperator {
        require(colType == KLAY_COLLATERAL_TYPE);
        (uint256 klayAmount, uint256 ksdAmount, uint256 bKlayAmount) = _amountsWithMaxProfit(id);
        if (klayAmount == 0) {
            return;
        }
        if (address(this).balance >= klayAmount) {
            // TODO: arbitrage amounts could be bigger without otc
            _takeArbitrage(id, klayAmount, ksdAmount, bKlayAmount);
        } else {
            _takeArbitrageWithFlashLoan(id, klayAmount, ksdAmount, bKlayAmount);
        }
    }

    function min(uint256 x, uint256 y) internal pure returns (uint256) {
        if (x < y) {
            return x;
        } else {
            return y;
        }
    }

    function sqrt(uint256 x) internal pure returns (uint256 y) {
        uint256 z = (x + 1) / 2;
        y = x;
        while (z < y) {
            y = z;
            z = (x / z + z) / 2;
        }
    }

    fallback() external payable {}

    receive() external payable {}
}
