pragma solidity 0.8.3;

import "../../library/openzeppelin/contracts/utils/SafeCast.sol";
import "../../library/AccessControl.sol";
import "../../interface/IKlayGateway.sol";
import "../../interface/IKSDGateway.sol";
import "../../interface/IBKlay.sol";
import "../../interface/ILedger.sol";
import "../../library/Pausable.sol";
import "../../library/WadRayMath.sol";

/// @title Klay input/output contract
/// @notice Functions to be called by user
contract KlayGateway is IKlayGateway, AccessControl, Pausable {
    using WadRayMath for uint256;

    function __KlayGateway_init() public initializer {
        __Pausable_init();
    }

    // --- data ---
    bytes32 public constant KLAY_COLLATERAL_TYPE = bytes32("klay"); // Collateral Type: lower-case
    ILedger public ledger; // ledger Engine
    IKSDGateway public ksdGateway;
    IBKlay public bKlay;
    IKIP7 public aklay;
    IKIP7 public skokoa;
    uint256 public skokoaLimit;
    mapping(address => bool) public isAllow; //unstakeInBKlay

    // --- config ---
    /// @notice Initialize interface variable.
    function config(bytes32 what, address data) external onlyAdmin whenNotPaused {
        if (what == "ledger") ledger = ILedger(data);
        else if (what == "ksdGateway") ksdGateway = IKSDGateway(data);
        else if (what == "bKlay") bKlay = IBKlay(data);
        else if (what == "aklay") aklay = IKIP7(data);
        else if (what == "skokoa") skokoa = IKIP7(data);
        else revert("KlayGateway::config:unrecognized-param");
    }

    /// @notice Initialize interface variable.
    function config(bytes32 what, uint256 data) external onlyAdmin whenNotPaused {
        if (what == "skokoaLimit") skokoaLimit = data;
        else revert("KlayGateway::config:unrecognized-param");
    }

    // --- function ---
    function setBKlayAccess(address account, bool bool_) external override onlyAdmin whenNotPaused {
        isAllow[account] = bool_;
    }

    /// @notice Send klay to bklay contract, record on ledge.
    /// @dev As much as the received klay, bklay is mint and treated as collateral.
    /// @dev Payable.
    function stake() public payable override whenNotPaused {
        ledger.addCollateral(KLAY_COLLATERAL_TYPE, msg.sender, SafeCast.toInt256(msg.value));
        bKlay.stakeKlay{value: msg.value}(address(this));
    }

    /// @notice Send aklay to bklay contract, record on ledge.
    /// @dev As much as the received aklay, bklay is mint and treated as collateral.
    /// @dev Payable.
    /// @param amount amount of aklay to stake
    function stakeAKlay(uint256 amount) public override whenNotPaused {
        ledger.addCollateral(KLAY_COLLATERAL_TYPE, msg.sender, SafeCast.toInt256(amount));
        aklay.transferFrom(msg.sender, address(this), amount);
        aklay.approve(address(bKlay), amount);
        bKlay.stakeAKlay(address(this), amount);
    }

    /// @notice Loan ksd with klay as collateral.
    /// @dev Currently, the only collateral is klay.
    /// @dev Ledger decides whether or not to verify the stability of a secured loan or not.
    /// @dev Loan history recorded on ledger.
    /// @param collateralAmount Amount of collateral to be collateralized.
    /// @param loanAmount Amount of ksd to borrow (loan).
    function borrow(uint256 collateralAmount, uint256 loanAmount) public override whenNotPaused {
        ledger.actionLoan(
            KLAY_COLLATERAL_TYPE,
            msg.sender,
            msg.sender,
            msg.sender,
            SafeCast.toInt256(collateralAmount),
            SafeCast.toInt256(loanAmount)
        );
        ksdGateway.exitFrom(msg.sender, msg.sender, loanAmount);
    }

    /// @notice Execute stake and borrow functions at the same time.
    /// @param amount Amount of ksd to borrow (loan).
    function stakeAndBorrow(uint256 amount) external payable override whenNotPaused {
        stake();
        borrow(msg.value, amount);
    }

    /// @notice Execute stake and borrow functions at the same time.
    /// @param aklayAmount Amount of aklay to stake.
    /// @param ksdAmount Amount of ksd to borrow (loan).
    function stakeAKlayAndBorrow(uint256 aklayAmount, uint256 ksdAmount) external override whenNotPaused {
        stakeAKlay(aklayAmount);
        borrow(aklayAmount, ksdAmount);
    }

    /// @notice Execute unstake to get the entrusted klay back.
    /// @dev Record on ledge.
    /// @dev Unstaking is carried out in the bklay contract.
    /// @param amount Amount of klay you want to return.
    function unstake(uint256 amount) public override whenNotPaused {
        if (ledger.token(KLAY_COLLATERAL_TYPE, msg.sender) < amount) {
            repay(amount - ledger.token(KLAY_COLLATERAL_TYPE, msg.sender), 0);
        }
        ledger.addCollateral(KLAY_COLLATERAL_TYPE, msg.sender, -SafeCast.toInt256(amount));
        bKlay.unstakeKlay(msg.sender, amount);
    }

    /// @notice Execute unstake to get BKlay back.
    /// @dev Record on ledge.
    /// @dev Unstaking is carried out in the bklay contract.
    /// @param amount Amount of klay you want to return.
    function unstakeInBKlay(uint256 amount) public override whenNotPaused {
        require(isAllow[msg.sender] || skokoa.balanceOf(msg.sender) >= skokoaLimit, "KlayGateway:: unstakeInBKlay: invalid msg.sender");
        if (ledger.token(KLAY_COLLATERAL_TYPE, msg.sender) < amount) {
            repay(amount - ledger.token(KLAY_COLLATERAL_TYPE, msg.sender), 0);
        }
        ledger.addCollateral(KLAY_COLLATERAL_TYPE, msg.sender, -SafeCast.toInt256(amount));
        bKlay.transfer(msg.sender, amount);
    }

    /// @notice Return a returnable klay to the user.
    /// @dev It takes 7 days for unstaked klay to be returned.
    /// @dev If you do not claim by 14 days after unstaking, it will be automatically staking again.
    /// @param usr Who will execute the claim.
    function claimKlay(address usr) external override {
        bKlay.claimKlay(usr);
    }

    /// @notice Return ksd and unlock collateral (klay).
    /// @dev Record on ledge.
    /// @param collateralAmount Amount of klay that was entrusted as collateral.
    /// @param loanAmount Amount of ksd borrowed (loan).
    function repay(uint256 collateralAmount, uint256 loanAmount) public override whenNotPaused {
        ksdGateway.joinFrom(msg.sender, msg.sender, loanAmount);
        ledger.actionLoan(
            KLAY_COLLATERAL_TYPE,
            msg.sender,
            msg.sender,
            msg.sender,
            -SafeCast.toInt256(collateralAmount),
            -SafeCast.toInt256(loanAmount)
        );
    }

    /// @notice Return ksd and unlock collateral (klay).
    /// @dev Record on ledge.
    /// @param collateralAmount Amount of klay that was entrusted as collateral.
    function repayAll(uint256 collateralAmount) public override whenNotPaused {
        (, , uint256 rate, , , ) = ledger.collateralInfo(KLAY_COLLATERAL_TYPE);
        (, uint256 rawLoanAmount) = ledger.accountInfo(KLAY_COLLATERAL_TYPE, msg.sender);
        uint256 loanAmount = rawLoanAmount.rmul(rate);
        ksdGateway.joinFrom(msg.sender, msg.sender, loanAmount);
        ledger.actionLoan(
            KLAY_COLLATERAL_TYPE,
            msg.sender,
            msg.sender,
            msg.sender,
            -SafeCast.toInt256(collateralAmount),
            -SafeCast.toInt256(loanAmount)
        );
    }

    /// @notice Execute repay and unstake functions at the same time.
    /// @dev Unstake is executed after repay is executed.
    /// @dev The claim function must be called to return the unstaked klay.
    /// @param collateralAmount Amount of klay that was entrusted as collateral.
    /// @param loanAmount Amount of ksd borrowed (loan).
    function repayAndUnstake(uint256 collateralAmount, uint256 loanAmount) external override whenNotPaused {
        repay(collateralAmount, loanAmount);
        unstake(collateralAmount);
    }

    /// @notice Execute repay and unstake functions at the same time.
    /// @dev Unstake is executed after repay is executed.
    /// @dev The claim function must be called to return the unstaked klay.
    /// @param collateralAmount Amount of klay that was entrusted as collateral.
    function repayAllAndUnstake(uint256 collateralAmount) external override whenNotPaused {
        repayAll(collateralAmount);
        unstake(collateralAmount);
    }
}
