        uint256 klay,
        uint256 klayForSwap,
        uint256 sklayBySwap
    ) external view returns (uint256);

    function _getPoolRate() external view returns (uint256 delegationPoolRate, uint256 swapPoolRate);

    function _comparePoolRate() external view returns (bool);

    function _getUserStat(address user)
        external
        view
        returns (
            address wallet,
            uint256 lp,
            uint256 klay,
            uint256 ksp,
            uint256 lastClaimedIndex,
            uint256 historyIndex,
            uint256 rewardKSPSum,
            uint256 lastKSPIndex
        );

    function addLiquidity(uint256 swapPoolRate) external payable;

