    function claimToken(address token) external;

    function claimCount(address usr) external view returns (uint256);

    function historyCount(address usr) external view returns (uint256);

    function history(address usr, uint256 hid)
        external
        view
        returns (
            address,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256,
            bool
        );
}
