pragma solidity 0.8.3;

import "../library/AccessControl.sol";
import "../interface/klayswap/IKlaytnExchangeImpl.sol";
import "../interface/klayswap/IKlaytnFactoryImpl.sol";
import "../interface/klayswap/IKlaySwapSupporterExtended.sol";
import "../interface/IKlaySwapBridge.sol";
import "../interface/IDKSD.sol";
import "../interface/IBKlay.sol";
import "../library/Pausable.sol";
import "../interface/IDistributor.sol";

/// @title Interaction contract with klaySwap.
/// @notice This contract utilizes the exchange functions of klaySwap.
/// @dev Klayswap is an AMM DEX protocol running on Klaytn
/*
    Some concept of exchages to understand in KlaySwap
    positive exchange
        - The amount {msg.sender} pays is fixed
        - The amount {msg.sender} receives is induced from it
            - {msg.sender} may set the minimum amount to receive in order to avoid unnecessary slippage losses
    negative exchange
        - The amount {msg.sender} receives is fixed
        - The amount {msg.sender} pays is induced from it
            - {msg.sender} may set the maximum amount to pay in order to avoid unnecessary slippage losses
*/
contract KlaySwapBridge is IKlaySwapBridge, AccessControl, Pausable {
    // --- data ---
    IKIP7 public ksd; // ksd token contract (stable token)
    IKlaytnFactory public factory; // klayswap factory contract (refer to klayswap/KlaytnFactory.impl.sol)
    IDistributor public ksdRewardManager; // ksdRewardManager token contract, distribute ksd reward

    function __KlaySwapBridge_init() public initializer {
        __Pausable_init();
    }

    // --- config ---
    /// @notice Initialize interface variables.
    function config(bytes32 what, address data) external onlyAdmin {
        if (what == "ksd") ksd = IKIP7(data);
        else if (what == "factory") factory = IKlaytnFactory(data);
        else if (what == "ksdRewardManager") ksdRewardManager = IDistributor(data);
        else revert("klaySwapBridge::config:unrecognized-param");
    }

    /// @notice Get pool(exchange) address from tokens.
    /// @param tokenA Address of tokenA.
    /// @param tokenB Address of tokenB.
    /// @return pool Address of the pool.
    function tokenToPool(address tokenA, address tokenB) public view override returns (address pool) {
        pool = factory.tokenToPool(tokenA, tokenB);
        require(pool != address(0), "KlaySwapBridge::tokenToPool: pool is not initialized");
    }

    /// @notice Exchange Klay for token.
    /// @dev "I want to pay {msg.value} amount of Klay in exchange for at least {amountB} amount of token"
    ///      e.g., suppose {msg.value} = 100 and amount = 200
    ///            if the pool is able to pay 250 tokens, the deal is valid. So msg.sender will pay 100 Klay and receive 250 tokenB
    ///            if the pool is not able to pay 250 tokens, the exchange does not take place
    /// @param token Address of token {msg.sender} wants to buy
    /// @dev msg.value: The exact amount of Klay that {msg.sender} wants to pay
    /// @param amount Minimum amount of token that account wants to receive
    ///                (i.e., if msg.sender cannot receive this amount of tokenB, it's a deal breaker)
    /// @param path Path to replace tokenA with tokenB in klayswap.
    function exchangeKlayPos(
        address token,
        uint256 amount,
        address[] calldata path
    ) external payable override {
        uint256 beforeBalance = IKIP7(token).balanceOf(address(this));
        factory.exchangeKlayPos{value: msg.value}(token, amount, path);
        uint256 afterBalance = IKIP7(token).balanceOf(address(this));
        IKIP7(token).transfer(msg.sender, afterBalance - beforeBalance);
    }

    /// @notice Exchange tokenA for tokenB.
    /// @dev "I want to pay {amountA} amount of tokenA in exchange for at least {amountB} amount of tokenB"
    ///      e.g., suppose amountA = 100 and amountB = 200
    ///            if the pool is able to pay 250 tokenB, the deal is valid. So msg.sender will pay 100 tokenA and receive 250 tokenB
    ///            if the pool is not able to pay 250 tokenB, the exchange does not take place
    /// @param tokenA The address of the token {msg.sender} wants to pay
    /// @param amountA The exact amount of tokenA msg.sender wants to pay
    /// @param tokenB TokenB address
    /// @param amountB Minimum amount of tokenB that {msg.sender} wants to receive
    ///                (i.e., if msg.sender cannot receive this amount of tokenB, it's a deal breaker)
    /// @param path Path to replace tokenA with tokenB in klayswap.
    function exchangeKctPos(
        address tokenA,
        uint256 amountA,
        address tokenB,
        uint256 amountB,
        address[] calldata path
    ) external override {
        IKIP7(tokenA).transferFrom(msg.sender, address(this), amountA);
        IKIP7(tokenA).approve(address(factory), amountA);
        uint256 beforeBBalance = IKIP7(tokenB).balanceOf(address(this));
        factory.exchangeKctPos(tokenA, amountA, tokenB, amountB, path);
        uint256 afterBBalance = IKIP7(tokenB).balanceOf(address(this));
        IKIP7(tokenB).transfer(msg.sender, afterBBalance - beforeBBalance);
    }

    /// @notice Exchange Klay for tokenB.
    /// @dev "I want to pay up to {msg.value} amount of Klay in exchange for {amount} amount of token"
    ///      e.g., suppose {msg.value} = 100 and amount = 200
    ///            If the pool is able to send 200 tokens in exchange for 98 Klay, the exchange will take place and the user will pay 98 Klay in exchange for 200 tokens.
    ///            if the pool demands {msg.sender} 101 Klay in exchange for 200 tokens, the exchange will not take place.
    /// @param token Address of token {msg.sender} wants to buy
    /// @param amount The exact amount of the token that {msg.sender} wants to receive
    ///              (i.e., If the pool demands {msg.sender} to pay more than {msg.value}, it is a deal breaker)
    /// {msg.sender} will pay the amount of Klay just enough to buy {amount} amount of the token, and the remaining Klay will be returned to {msg.sender}.
    /// @dev msg.value: The maximum amount of Klay that {msg.sender} is willing to pay
    /// @param path Path to replace tokenA with tokenB in klayswap.
    function exchangeKlayNeg(
        address token,
        uint256 amount,
        address[] calldata path
    ) external payable override {
        uint256 beforeBalance = address(this).balance;
        factory.exchangeKlayNeg{value: msg.value}(token, amount, path);
        uint256 afterBalance = address(this).balance;
        IKIP7(token).transfer(msg.sender, amount);

        uint256 refundAmount = msg.value - (beforeBalance - afterBalance);
        msg.sender.call{value: refundAmount}("");
    }

    // @notice Exchange tokenA for tokenB.
    /// @dev "I want to pay {amountA} amount of tokenA in exchange for at least {amountB} amount of tokenB"
    ///      e.g., suppose amountA = 100 and amountB = 200
    ///            If the pool is able to send 200 tokenB in exchange for 98 tokenA, the exchange will take place and the user will pay 98 tokenA in exchange for 200 tokenB.
    ///            if the pool demands {msg.sender} 101 tokenA in exchange for 200 tokenB, the exchange will not take place.
    /// @param tokenA The address of the token {msg.sender} wants to pay
    /// @param amountA The maximum amount of tokenA that {msg.sender} is willing to pay
    /// @param tokenB TokenB address
    /// @param amountB The exact amount of the tokenB that {msg.sender} wants to receive
    ///              (i.e., If the pool demands {msg.sender} to pay more than {msg.value} tokenA, it is a deal breaker)
    /// {msg.sender} will pay the amount of tokenA just enough to buy {amountB} amount of the tokenB, and the remaining tokenA will be returned to {msg.sender}.
    /// @param path Path to replace tokenA with tokenB in klayswap.
    function exchangeKctNeg(
        address tokenA,
        uint256 amountA,
        address tokenB,
        uint256 amountB,
        address[] calldata path
    ) external override {
        IKIP7(tokenA).transferFrom(msg.sender, address(this), amountA);
        IKIP7(tokenA).approve(address(factory), amountA);
        uint256 beforeABalance = IKIP7(tokenA).balanceOf(address(this));
        factory.exchangeKctNeg(tokenA, amountA, tokenB, amountB, path);
        uint256 afterABalance = IKIP7(tokenA).balanceOf(address(this));
        IKIP7(tokenB).transfer(msg.sender, amountB);

        uint256 refundAmount = amountA - (beforeABalance - afterABalance);
        IKIP7(tokenA).transfer(msg.sender, refundAmount);
    }

    /// @notice Fallback function.
    receive() external payable {}
}
