pragma solidity 0.8.3;

import "./IRewardPool.sol";
import "./kip/IKIP7.sol";

/// @title A token staking pool.
/// @dev KokoaRewardManager gives reward user who stakes token to this pool.
interface IStakingPool is IRewardPool {
    event Stake(address indexed from, address indexed to, uint256 amount);
    event Unstake(address indexed from, address indexed to, uint256 amount);

    function token() external view returns (IKIP7);

    /// @notice stake token
    function stake(address to, uint256 amount) external;

    /// @notice unstake token
    function unstake(address to, uint256 amount) external;
}
