pragma solidity ^0.8.3;

/**
 * @title RewardTokenVesting
 * @dev A token holder contract that can release its token balance to RewardManager contract.
 */
interface ITokenVesting {
    event TokensReleased(uint256 amount);

    /**
     * @return address of token contract.
     */
    function token() external view returns (address);

    /**
     * @return address of rewardManger who receives token.
     */
    function beneficiary() external view returns (address);

    /**
     * @return the start time of the token vesting.
     */
    function totalBalance() external view returns (uint256);

    /**
     * @return the start time of the token vesting.
     */
    function start() external view returns (uint256);

    /**
     * @return the duration of the token vesting.
     */
    function duration() external pure returns (uint256);

    /**
     * @return the amount of the token released.
     */
    function released() external view returns (uint256);

    /**
     * @return the amount of the token released.
     */
    function releasableAmount() external view returns (uint256);

    /**
     * @notice Transfers vested tokens to rewardManager.
     */
    function release() external;

    /**
     * @param from block timestamp
     * @param to block timestamp
     * @return the amount of the token release amount between `from` and `to`.
     */
    function releaseAmount(uint256 from, uint256 to) external view returns (uint256);
}
