pragma solidity 0.8.3;
pragma experimental ABIEncoderV2;

import "../library/openzeppelin/contracts/utils/SafeCast.sol";
import "../interface/IKokoaRewardManager.sol";
import "../interface/IRewardPool.sol";
import "../interface/ITokenVesting.sol";
import "../interface/IStakingPool.sol";
import "../library/AccessControl.sol";
import "../interface/kip/IKIP7.sol";
import "../library/Pausable.sol";

/// @title A manager for distributing kokoa reward.
/// @dev It manages all kokoa rewards. Its base code is from sushiswap.
/// Therefore, the way to distribute reward is very similar to sushiswap.
/// The main difference between sushiswap and KokoaRewardManager lies in staking.
/// Sushiswap forces to stake token, but KokoaRewardManager does not own token.
/// It just expect registered pool to call onDeposit/onWithdraw/onChanged, and
/// get balance and totalSupply of pool to calculate how to distribute reward.
/// Additionally, unlike SushiSwap where SUSHI tokens for liquidity mining are generated blockwise,
/// Kokoa Reward Manager mints the Kokoa tokens for reward in advance, and distributes them according to the vesting logic
contract KokoaRewardManager is IKokoaRewardManager, AccessControl, Pausable {
    uint256 public constant version = 1;

    IKIP7 public kokoa;
    ITokenVesting public rewardTokenVesting;

    mapping(address => PoolInfo) public override poolInfo;
    IRewardPool[] public override rewardPool;

    mapping(address => mapping(address => UserInfo)) public override userInfo;
    uint256 public override totalAllocPoint;

    uint256 private constant ACC_REWARD_PRECISION = 1e12;
    IStakingPool public skokoa;
    address public incinerator;

    modifier onlyRewardPool() {
        require(poolInfo[msg.sender].init, "KokoaRewardManager::onlyRewardPool: invalid reward pool");
        _;
    }

    function __KokoaRewardManager_init(address kokoa_, address rewardTokenVesting_) public initializer {
        __Pausable_init();
        kokoa = IKIP7(kokoa_);
        rewardTokenVesting = ITokenVesting(rewardTokenVesting_);
    }

    function config(bytes32 what, address data) external onlyAdmin whenNotPaused {
        if (what == "skokoa") {
            skokoa = IStakingPool(data);
        } else if (what == "incinerator") {
            incinerator = data;
        } else revert("KokoaRewardManager::config: unrecognized-param");
    }

    /// @notice number of registered pools
    function poolLength() external view override returns (uint256) {
        return rewardPool.length;
    }

    /// @notice all registered pools
    function rewardPools() external view override returns (address[] memory pools) {
        pools = new address[](rewardPool.length);
        for (uint256 i = 0; i < rewardPool.length; i++) {
            pools[i] = address(rewardPool[i]);
        }
    }

    /// @notice add pool to kokoaRewardManager
    /// @param allocPoint (allocPoint / totalAllocPoint) == kokoaRewardManager를 통해서 마이닝된 총 kokoa 양 중에 이 풀에 분배될 비율
    /// @param allocPoint a weight parameter for Kokoa distribution
    ////               i.e., (allocPoint / totalAllocPoint) == ratio of tokens allocated to this specific pool.
    ////                if allocPoint = 3 and totalAllocPoint = 10, it means that 30% of the reward tokens should be allocated to that pool.
    /// @param pool address of pool to register
    /// @dev it should be called by the governance (initially, admin EOA).
    function add(
        uint256 allocPoint,
        address pool,
        uint256 fee
    ) external override onlyAdmin {
        require(!poolInfo[pool].init, "KokoaRewardManager::add: pool is already added");
        require(fee >= 0 && fee <= 10000, "KokoaRewardManager::add: invalid fee ratio");
        totalAllocPoint = totalAllocPoint + allocPoint;
        rewardPool.push(IRewardPool(pool));

        poolInfo[pool] = PoolInfo({allocPoint: allocPoint, lastRewardTimestamp: block.timestamp, accRewardPerShare: 0, init: true, fee: fee});
        emit LogPoolAddition(rewardPool.length - 1, allocPoint, pool, fee);
    }

    /// @notice 등록된 풀이 받는 kokoa reward의 비율을 조절한다.
    /// @notice Adjust the reward ratio of the selected pool
    /// @param pool address of pool to register
    /// @param allocPoint (allocPoint / totalAllocPoint) == kokoaRewardManager를 통해서 마이닝된 총 kokoa 양 중에 이 풀에 분배될 비율
    /// @param allocPoint a weight parameter for Kokoa distribution
    /// @dev it should be called by the governance (initially, admin EOA).
    function set(
        address pool,
        uint256 allocPoint,
        uint256 fee
    ) external override onlyAdmin {
        require(poolInfo[pool].init, "KokoaRewardManager::set: pool is not initialized");
        require(fee >= 0 && fee <= 10000, "KokoaRewardManager::add: invalid fee ratio");
        totalAllocPoint = totalAllocPoint - poolInfo[pool].allocPoint + allocPoint;
        poolInfo[pool].allocPoint = allocPoint;
        poolInfo[pool].fee = fee;
        emit LogSetPool(pool, allocPoint, fee);
    }

    function pop() external onlyAdmin {
        address pool = address(rewardPool[rewardPool.length-1]);
        require(poolInfo[pool].init, "KokoaRewardManager::pop: pool is not initialized");
        totalAllocPoint = totalAllocPoint - poolInfo[pool].allocPoint;
        uint256 length = rewardPool.length;
        rewardPool.pop();
        delete poolInfo[pool];
        require(rewardPool.length == length-1,'KokoaRewardManager::pop:pop failure');
    }

    /// @notice returns amount of Kokoa reward a user is able to claim from a pool (i.e., 'claimable' reward)
    /// @param pool address of a reward pool
    /// @param usr address of user
    /// @param bonus calculate fee
    function claimableReward(
        address pool,
        address usr,
        bool bonus
    ) public view override whenNotPaused returns (uint256 claimable) {
        require(poolInfo[pool].init, "KokoaRewardManager::claimableReward: pool is not initialized");
        PoolInfo memory _poolInfo = poolInfo[pool];
        UserInfo storage user = userInfo[pool][usr];
        uint256 accRewardPerShare = _poolInfo.accRewardPerShare;
        uint256 tokenTotalSupply = IRewardPool(pool).totalSupply();
        if (block.timestamp > _poolInfo.lastRewardTimestamp && tokenTotalSupply != 0) {
            uint256 releasedReward = rewardTokenVesting.releaseAmount(_poolInfo.lastRewardTimestamp, block.timestamp);
            uint256 reward = (releasedReward * _poolInfo.allocPoint) / totalAllocPoint;
            accRewardPerShare = accRewardPerShare + ((reward * ACC_REWARD_PRECISION) / tokenTotalSupply);
        }
        uint256 userBalance = IRewardPool(pool).balanceOf(usr);
        claimable = SafeCast.toUint256(SafeCast.toInt256((userBalance * accRewardPerShare) / ACC_REWARD_PRECISION) - user.rewardDebt);
        if (!bonus) {
            claimable = (claimable * (10000 - _poolInfo.fee)) / 10000;
        }
    }

    function accRewardPrecision() external pure override returns (uint256) {
        return ACC_REWARD_PRECISION;
    }

    /// @notice 유저에게 분배될 reward 양을 업데이트 시킨다.
    /// @dev 내부적으로 _updatePool이 불리는데, 이 함수는 유저가 풀에 staking 양이 변화하거나, reward를 claim하기 전에 항상 불린다
    /// 따라서 이 함수를 밖에서 명시적으로 불릴 일은 거의 없을 것이다. 일단 sushiswap 에서 구현했기 때문에 가져왔다.
    /// (eng)
    /// @notice Update the amount of reward of a pool that should be distributed to its users
    /// @dev It calls _updatePool function.
    ///       _updatePool is a function that should be called before whenever a user's stake is changed or one claims reward
    ///       So updatePool will not be called in any other circumstances, but we still implemented this part because it's in SushiSwap code
    /// @param pool address of pool to update
    function updatePool(address pool) external override whenNotPaused {
        _updatePool(pool);
    }

    /// @notice 여러 풀들에 대해서 updatePool을 시켜준다.
    /// (eng)
    /// @notice Do updatePool for multiple pools
    /// @param pools address list of pools to update
    function massUpdatePools(address[] calldata pools) external override whenNotPaused {
        uint256 len = pools.length;
        for (uint256 i = 0; i < len; ++i) {
            _updatePool(pools[i]);
        }
    }

    function _updatePool(address pool) internal {
        require(poolInfo[pool].init, "KokoaRewardManager::_updatePool: pool is not initialized");
        PoolInfo memory _poolInfo = poolInfo[pool];
        if (block.timestamp > _poolInfo.lastRewardTimestamp) {
            uint256 lpSupply = IRewardPool(pool).totalSupply();
            if (lpSupply > 0) {
                rewardTokenVesting.release();
                uint256 releasedReward = rewardTokenVesting.releaseAmount(_poolInfo.lastRewardTimestamp, block.timestamp);
                uint256 reward = (releasedReward * _poolInfo.allocPoint) / totalAllocPoint;

                _poolInfo.accRewardPerShare = _poolInfo.accRewardPerShare + ((reward * ACC_REWARD_PRECISION) / lpSupply);
            }
            _poolInfo.lastRewardTimestamp = block.timestamp;
            poolInfo[pool] = _poolInfo;
            emit LogUpdatePool(pool, _poolInfo.lastRewardTimestamp, lpSupply, _poolInfo.accRewardPerShare);
        }
    }

    /// @notice 등록된 pool에서 유저가 staking한 양이 늘었을 때, 유저에게 분배할 reward 양을 계산하기 위해서 불린다.
    /// @dev 무조건 해당 풀에서만 불려야 한다. 즉 msg.sender == pool
    /// (eng)
    /// @notice When the amount a user staked in a pool is increased, this function should be called in order to keep track of the reward
    /// @dev Only Reward Pools should be able to call this function
    /// @param amount amount increased
    /// @param to address of user
    function onDeposit(uint256 amount, address to) public override onlyRewardPool whenNotPaused {
        _updatePool(msg.sender);
        PoolInfo memory pool = poolInfo[msg.sender];
        UserInfo storage user = userInfo[msg.sender][to];

        user.rewardDebt += SafeCast.toInt256((amount * pool.accRewardPerShare) / ACC_REWARD_PRECISION);

        emit Deposit(msg.sender, amount, to);
    }

    /// @notice 등록된 pool에서 유저가 staking한 양이 줄었을 때, 유저에게 분배할 reward 양을 계산하기 위해서 불린다.
    /// @dev 무조건 해당 풀에서만 불려야 한다. 즉 msg.sender == pool
    /// (eng)
    /// @notice When the amount a user staked in a pool is decreased, this function should be called in order to keep track of the reward
    /// @dev Only Reward Pools should be able to call this function
    /// @param amount amount decreased
    /// @param to address of user
    function onWithdraw(uint256 amount, address to) public override onlyRewardPool whenNotPaused {
        _updatePool(msg.sender);
        PoolInfo memory pool = poolInfo[msg.sender];
        UserInfo storage user = userInfo[msg.sender][to];

        user.rewardDebt -= SafeCast.toInt256((amount * pool.accRewardPerShare) / ACC_REWARD_PRECISION);
        emit Withdraw(msg.sender, amount, to);
    }

    /// @notice onDeposit과 onWithdraw의 wrapper
    /// @dev 무조건 해당 풀에서만 불려야 한다. 즉 msg.sender == pool
    /// (eng)
    /// @notice A wrapper function for onDeposit and onWithdraw
    /// @dev Only Reward Pools should be able to call this function
    /// @param amount amount changed
    /// @param to address of user
    function onChanged(int256 amount, address to) external override onlyRewardPool {
        if (amount > 0) {
            onDeposit(SafeCast.toUint256(amount), to);
        }
        if (amount < 0) {
            onWithdraw(SafeCast.toUint256(-amount), to);
        }
    }

    /// @notice 유저가 분배받은 kokoa reward를 claim 하는 function
    /// (eng)
    /// @notice A function that harvests the accumulated Kokoa reward of a user
    /// @param pool address of pool
    /// @param to receiver address of kokoa reward
    function _claimReward(
        address pool,
        address from,
        address to,
        uint256 fee
    ) internal returns (bool success) {
        _updatePool(pool);
        PoolInfo memory _poolInfo = poolInfo[pool];
        UserInfo storage user = userInfo[pool][from];
        uint256 userBalance = IRewardPool(pool).balanceOf(from);
        int256 accumulatedReward = SafeCast.toInt256((userBalance * _poolInfo.accRewardPerShare) / ACC_REWARD_PRECISION);
        uint256 _claimableReward = SafeCast.toUint256(accumulatedReward - user.rewardDebt);
        if (_claimableReward == 0) {
            success = false;
            return success;
        }

        // Effects
        user.rewardDebt = accumulatedReward;

        // Interactions
        kokoa.transfer(to, (_claimableReward * (10000 - fee)) / 10000);
        if (fee > 0) {
            kokoa.transfer(incinerator, (_claimableReward * fee) / 10000);
        }

        user.claimedReward = user.claimedReward + _claimableReward;
        emit ClaimReward(pool, from, _claimableReward);
        success = true;
    }

    function claimReward(address pool, address to) external override whenNotPaused returns (bool success) {
        PoolInfo memory _poolInfo = poolInfo[pool];
        return _claimReward(pool, msg.sender, to, _poolInfo.fee);
    }

    function claimRewardAll() public override whenNotPaused returns (bool success) {
        success = true;
        for (uint256 i = 0; i < rewardPool.length; i++) {
            address pool = address(rewardPool[i]);
            PoolInfo memory _poolInfo = poolInfo[pool];
            bool _success = _claimReward(pool, msg.sender, msg.sender, _poolInfo.fee);
            success = success && _success;
        }
    }

    function claimRewardByOther(address pool, address usr) external override whenNotPaused onlyAdmin returns (bool success) {
        PoolInfo memory _poolInfo = poolInfo[pool];
        return _claimReward(pool, usr, usr, _poolInfo.fee);
    }

    function claimRewardAllByOther(address usr) public override whenNotPaused onlyAdmin returns (bool success) {
        success = true;
        for (uint256 i = 0; i < rewardPool.length; i++) {
            address pool = address(rewardPool[i]);
            PoolInfo memory _poolInfo = poolInfo[pool];
            bool _success = _claimReward(pool, usr, usr, _poolInfo.fee);
            success = success && _success;
        }
    }

    function claimRewardAndStake(address pool) external override whenNotPaused returns (bool success) {
        uint256 claimable = claimableReward(pool, msg.sender, true);
        if (claimable == 0) {
            success = false;
            return success;
        }

        success = _claimReward(pool, msg.sender, address(this), 0);
        require(success, "KokoaRewardManager::claimRewardAndStake: claim failure");

        kokoa.approve(address(skokoa), claimable);
        skokoa.stake(msg.sender, claimable);
    }

    function claimRewardAndStakeByOther(address pool, address usr) external override whenNotPaused onlyAdmin returns (bool success) {
        uint256 claimable = claimableReward(pool, usr, true);
        if (claimable == 0) {
            success = false;
            return success;
        }

        success = _claimReward(pool, usr, address(this), 0);
        require(success, "KokoaRewardManager::claimRewardAndStake: claim failure");

        kokoa.approve(address(skokoa), claimable);
        skokoa.stake(usr, claimable);
    }

    function claimRewardAndStakeAll() external override whenNotPaused returns (bool success) {
        uint256 amount;
        uint256 claimable;
        success = true;

        for (uint256 i = 0; i < rewardPool.length; i++) {
            address pool = address(rewardPool[i]);
            claimable = claimableReward(pool, msg.sender, true);
            if (claimable == 0) {
                continue;
            }
            amount += claimable;

            bool _success = _claimReward(pool, msg.sender, address(this), 0);
            success = success && _success;
        }

        require(success, "KokoaRewardManager::claimRewardAndStakeAll: claim failure");

        if (amount > 0) {
            kokoa.approve(address(skokoa), amount);
            skokoa.stake(msg.sender, amount);
        }
    }
