pragma solidity 0.8.3;
pragma experimental ABIEncoderV2;

import "../library/openzeppelin/contracts/utils/SafeCast.sol";
import "../library/AccessControl.sol";
import "../interface/kip/IKIP7.sol";
import "../library/Pausable.sol";
import "../interface/ILedger.sol";
import "../interface/IKsdRefundManager.sol";
import "../interface/IStabilityFeeCollector.sol";

contract KsdRefundManager is IKsdRefundManager, AccessControl, Pausable {
    uint256 public constant version = 1;

    ILedger public ledger;
    IKIP7 public ksd;
    mapping(bytes32 => PoolInfo) public poolInfo;
    mapping(bytes32 => mapping(address => UserInfo)) public userInfo;
    uint256 private constant ACC_PRECISION = 1e12;

    function __KsdRefundManager_init() public initializer {
        __Pausable_init();
    }

    function config(bytes32 what, address data) external onlyAdmin whenNotPaused {
        if (what == "ledger") ledger = ILedger(data);
        else if (what == "ksd") ksd = IKIP7(data);
        else revert("KsdRefundManager::config: unrecognized-param");
    }

    // ksd refund manager
    function refund(bytes32 collateralType, uint256 refundAmount) external override whenNotPaused {
        ksd.transferFrom(msg.sender, address(this), refundAmount);
        PoolInfo memory _poolInfo = poolInfo[collateralType];
        (, uint256 totalLoan, , , , ) = ledger.collateralInfo(collateralType);
        if (totalLoan > 0) {
            _poolInfo.accAmountPerShare = _poolInfo.accAmountPerShare + ((refundAmount * ACC_PRECISION) / totalLoan);
            poolInfo[collateralType] = _poolInfo;
        }
        emit Refund(collateralType, block.timestamp, refundAmount, totalLoan, _poolInfo.accAmountPerShare);
    }

    function onDeposit(
        bytes32 collateralType,
        uint256 amount,
        address to
    ) public override onlyAdmin whenNotPaused {
        if (collateralType == bytes32("klay")) {
            return;
        }
        PoolInfo memory pool = poolInfo[collateralType];
        UserInfo storage user = userInfo[collateralType][to];
        user.debt += SafeCast.toInt256((amount * pool.accAmountPerShare) / ACC_PRECISION);
        emit Deposit(collateralType, amount, to);
    }

    function onWithdraw(
        bytes32 collateralType,
        uint256 amount,
        address to
    ) public override onlyAdmin whenNotPaused {
        if (collateralType == bytes32("klay")) {
            return;
        }
        PoolInfo memory pool = poolInfo[collateralType];
        UserInfo storage user = userInfo[collateralType][to];
        user.debt -= SafeCast.toInt256((amount * pool.accAmountPerShare) / ACC_PRECISION);
        emit Withdraw(collateralType, amount, to);
    }

    function onChanged(
        bytes32 collateralType,
        int256 amount,
        address to
    ) external override onlyAdmin {
        if (amount > 0) {
            onDeposit(collateralType, SafeCast.toUint256(amount), to);
        }
        if (amount < 0) {
            onWithdraw(collateralType, SafeCast.toUint256(-amount), to);
        }
    }

    function claimableAmount(bytes32 collateralType, address usr) public view override whenNotPaused returns (uint256) {
        PoolInfo memory _poolInfo = poolInfo[collateralType];
        UserInfo storage user = userInfo[collateralType][usr];
        uint256 accAmountPerShare = _poolInfo.accAmountPerShare;
        (, uint256 loan) = ledger.accountInfo(collateralType, usr);

        int256 amount = SafeCast.toInt256((loan * accAmountPerShare) / ACC_PRECISION) - user.debt;
        amount = amount > int256(0) ? amount : int256(0);
        return SafeCast.toUint256(amount);
    }

    function _claim(
        bytes32 collateralType,
        address from,
        address to
    ) internal {
        PoolInfo memory _poolInfo = poolInfo[collateralType];
        UserInfo storage user = userInfo[collateralType][from];
        (, uint256 loan) = ledger.accountInfo(collateralType, from);
        int256 accAmount = SafeCast.toInt256((loan * _poolInfo.accAmountPerShare) / ACC_PRECISION);
        uint256 _claimableAmount = SafeCast.toUint256(accAmount - user.debt);
        if (_claimableAmount == 0) {
            return;
        }

        user.debt = accAmount;

        // Interactions
        ksd.transfer(to, _claimableAmount);
        user.claimedAmount += _claimableAmount;
        emit Claim(collateralType, from, _claimableAmount);
    }

    function claim(bytes32 collateralType, address to) external override whenNotPaused {
        _claim(collateralType, msg.sender, to);
    }

    function claimByOther(bytes32 collateralType, address usr) external override whenNotPaused {
        _claim(collateralType, usr, usr);
    }
}
