pragma solidity 0.8.3;

import "../interface/IAuctionHouse.sol";
import "../interface/ILedger.sol";
import "../interface/IMinusBank.sol";
import "../interface/ILiquidator.sol";
import "../library/openzeppelin/contracts/utils/Math.sol";
import "../library/openzeppelin/contracts/utils/SafeCast.sol";
import "../library/Pausable.sol";
import "../library/AccessControl.sol";

/// @notice Most of the codes here are a subset of MakerDAO's contract
contract Liquidator is ILiquidator, AccessControl, Pausable {
    ILedger public ledger; // ledger Engine

    mapping(bytes32 => AuctionInfo) public override auctionInfos;

    IMinusBank public minusBank; // Debt Engine
    uint256 public totalMaxAmount; // Max KSD needed to cover debt+fees of active auctions [rad]
    uint256 public totalOnGoingAmount; // Amt KSD needed to cover debt+fees of active auctions [rad]

    // --- Init ---
    function __Liquidator_init(address ledger_) public initializer {
        __Pausable_init();
        ledger = ILedger(ledger_);
    }

    // --- Math ---
    uint256 constant WAD = 10**18;

    // --- Administration ---
    function config(bytes32 what, address data) external onlyAdmin {
        if (what == "minusBank") minusBank = IMinusBank(data);
        else revert("Liquidator::config:unrecognized-param");
        emit Config(what, data);
    }

    function config(bytes32 what, uint256 data) external onlyAdmin {
        if (what == "totalMaxAmount") totalMaxAmount = data;
        else revert("Liquidator::config:unrecognized-param");
        emit Config(what, data);
    }

    function config(
        bytes32 collateralType,
        bytes32 what,
        uint256 data
    ) external onlyAdmin {
        if (what == "penalty") {
            require(data >= WAD, "Liquidator::config:file-penalty-lt-WAD");
            auctionInfos[collateralType].penalty = data;
        } else if (what == "maxAmount") auctionInfos[collateralType].maxAmount = data;
        else revert("Liquidator::config:unrecognized-param");
        emit Config(collateralType, what, data);
    }

    function config(
        bytes32 collateralType,
        bytes32 what,
        address auctionHouse
    ) external onlyAdmin {
        if (what == "auctionHouse") {
            require(collateralType == IAuctionHouse(auctionHouse).collateralType(), "Liquidator::config:file-ilk-neq-auctionHouse.collateralType");
            auctionInfos[collateralType].auctionHouse = auctionHouse;
        } else revert("Liquidator::config:unrecognized-param");
        emit Config(collateralType, what, auctionHouse);
    }

    function penalty(bytes32 collateralType) external view override returns (uint256) {
        return auctionInfos[collateralType].penalty;
    }

    /// @notice liquidate a Vault and start a Dutch auction to sell its collateral for KSD.
    /// The entire Vault will be liquidated except when the target amount of KSD to be raised in
    /// the resulting auction (debt of Vault + liquidation penalty) causes either totalOnGoingAmount to exceed
    /// totalMaxAmount or collateral.onGoingAmount to exceed collateral.maxAmount by an economically significant amount. In that
    /// case, a partial liquidation is performed to respect the global and per-collateral limits on
    /// outstanding KSD target. The one exception is if the resulting auction would likely
    /// have too little collateral to be interesting to Keepers (debt taken from Vault < collateral.dust),
    /// in which case the function reverts. Please refer to the code and comments within if
    /// more detail is desired.
    /// @param collateralType type of collateral in vault to liquidate
    /// @param vaultAddr address of vault to liquidate
    /// @param keeper address that will receive incentives
    function liquidate(
        bytes32 collateralType,
        address vaultAddr,
        address keeper
    ) external override whenNotPaused returns (uint256 id) {
        (uint256 lockedCollateral, uint256 loan) = ledger.accountInfo(collateralType, vaultAddr);
        AuctionInfo memory auctionInfo = auctionInfos[collateralType];
        uint256 liquidateLoan;
        uint256 rate;
        uint256 dust;
        {
            uint256 spot;
            (, , rate, spot, , dust) = ledger.collateralInfo(collateralType);
            require(spot > 0 && lockedCollateral * spot < loan * rate, "Liquidator::liquidate:not-unsafe");

            // Get the minimum value between:
            // 1) Remaining space in the general totalMaxAmount
            // 2) Remaining space in the collateral hole
            require(
                totalMaxAmount > totalOnGoingAmount && auctionInfo.maxAmount > auctionInfo.onGoingAmount,
                "Liquidator::liquidate:liquidation-limit-hit"
            );
            uint256 room = Math.min(totalMaxAmount - totalOnGoingAmount, auctionInfo.maxAmount - auctionInfo.onGoingAmount);

            // uint256.max()/(RAD*WAD) = 115,792,089,237,316
            liquidateLoan = Math.min(loan, (room * WAD) / rate / auctionInfo.penalty);

            // Partial liquidation edge case logic
            if (loan > liquidateLoan) {
                if ((loan - liquidateLoan) * rate < dust) {
                    // If the leftover Vault would be dusty, just liquidate it entirely.
                    // This will result in at least one of onGoingAmount_i > hole_i or totalOnGoingAmount > totalMaxAmount becoming true.
                    // The amount of excess will be bounded above by ceiling(dust_i * penalty_i / WAD).
                    // This deviation is assumed to be small compared to both hole_i and totalMaxAmount, so that
                    // the extra amount of target KSD over the limits intended is not of economic concern.
                    liquidateLoan = loan;
                } else {
                    // In a partial liquidation, the resulting auction should also be non-dusty.
                    require(liquidateLoan * rate >= dust, "Liquidator::liquidate:dusty-auction-from-partial-liquidation");
                }
            }
        }

        uint256 liquidateCollateral = (lockedCollateral * liquidateLoan) / loan;

        require(liquidateCollateral > 0, "Liquidator::liquidate:null-auction");
        ledger.grab(
            collateralType,
            vaultAddr,
            auctionInfo.auctionHouse,
            address(minusBank),
            -SafeCast.toInt256(liquidateCollateral),
            -SafeCast.toInt256(liquidateLoan)
        );

        // -- Kokoa --
        uint256 fixedLiquidateLoan = liquidateLoan * rate;
        // minusBank.fess(due);

        {
            // Keeps the stack from staking too much
            // This calcuation will overflow if dart*rate exceeds ~10^14
            uint256 finalLiquidateLoan = (fixedLiquidateLoan * auctionInfo.penalty) / WAD;
            totalOnGoingAmount = totalOnGoingAmount + finalLiquidateLoan;
            auctionInfos[collateralType].onGoingAmount = auctionInfo.onGoingAmount + finalLiquidateLoan;

            id = IAuctionHouse(auctionInfo.auctionHouse).start({
                raiseAmount: finalLiquidateLoan,
                sellAmount: liquidateCollateral,
                vaultOwner: vaultAddr,
                keeper: keeper
            });
        }

        emit Liquidate(collateralType, vaultAddr, liquidateCollateral, liquidateLoan, fixedLiquidateLoan, auctionInfo.auctionHouse, id);
    }

    /// @notice when liquidation process is ended (including auction), endLiquidation is called to clear some data related the liquidation.
    /// @param collateralType collateralType
    /// @param rad liquidated ksd amount
    function endLiquidation(bytes32 collateralType, uint256 rad) external override onlyAdmin {
        totalOnGoingAmount = totalOnGoingAmount - rad;
        auctionInfos[collateralType].onGoingAmount = auctionInfos[collateralType].onGoingAmount - rad;
        emit EndLiquidation(collateralType, rad);
    }
}
