pragma solidity 0.8.3;

import "../interface/IFlashBorrower.sol";
import "../interface/IOtcFund.sol";
import "../interface/IBKlay.sol";
import "../library/openzeppelin/proxy/Initializable.sol";

contract MockFlashBorrower is IFlashBorrower, Initializable {
    IBKlay public bklay;
    IOtcFund public otcFund;

    function __MockFlashBorrower_init(address bklay_, address otcFund_) public initializer {
        bklay = IBKlay(bklay_);
        otcFund = IOtcFund(otcFund_);
    }

    function onFlashLoan(
        address account,
        uint256 klayAmount,
        uint256 fee,
        bytes calldata data
    ) external override returns (bool) {
        // due to compiler warning
        data;
        uint256 rewardAmount = klayAmount + fee;
        //bklay.mint(address(this), rewardAmount);
        //aklay.stakeKlay{value: msg.value}(address(this));
        bklay.stakeKlay{value: rewardAmount}(address(this));
        bklay.transfer(account, rewardAmount);
        return true;
    }

    function actionFlashLoan(uint256 amount) external returns (bool) {
        otcFund.flashLoan(address(this), amount, "");
        return true;
    }

    function getFlashFee(uint256 klayAmount) external view returns (uint256) {
        uint256 fee = otcFund.flashFee(klayAmount);
        return fee;
    }

    function transfer() external payable {}

    receive() external payable {}
}
