pragma solidity 0.8.3;
import "../library/Pausable.sol";
import "../library/AccessControl.sol";
import "../interface/ILedger.sol";
import "../interface/IFeed.sol";
import "../interface/IOracle.sol";
import "../library/WadRayMath.sol";

/// @title oracle contract
/// @notice get price from Feed contract and set the inverse of LTV (Loan-To-Value ratio)
/// @dev this oracle contract updates the reference price and LTV of ledger, therefore it should have permission to ledger.
contract Oracle is IOracle, AccessControl, Pausable {
    using WadRayMath for uint256;

    // --- Data ---
    struct CollateralInfo {
        IFeed feed; // Price Feed
        uint256 liquidRatio; // Liquidation ratio [ray]
    }
    mapping(bytes32 => CollateralInfo) public override collateralInfo;
    ILedger public ledger; // Ledger Engine
    uint256 public override unit = 10**27; // ref per KSD [ray]

    // --- Init ---
    function __Oracle_init(address ledger_) public initializer {
        __Pausable_init();
        ledger = ILedger(ledger_);
    }

    // --- Configuration ---
    /// @notice configuration
    function config(
        bytes32 collateralType,
        bytes32 what,
        address feed
    ) external onlyAdmin whenNotPaused {
        if (what == "feed") collateralInfo[collateralType].feed = IFeed(feed);
        else revert("Oracle::config:unrecognized-param");
    }

    /// @notice configuration
    /// It is not necessary because unit is just constant for now. (this config is for just in case)
    function config(bytes32 what, uint256 data) external onlyAdmin whenNotPaused {
        if (what == "unit") unit = data;
        else revert("Oracle::config:unrecognized-param");
    }

    /// @notice configuration
    function config(
        bytes32 collateralType,
        bytes32 what,
        uint256 data
    ) external onlyAdmin whenNotPaused {
        if (what == "liquidRatio") collateralInfo[collateralType].liquidRatio = data;
        else revert("Oracle::config:unrecognized-param");
    }

    /// @notice update price (actually price * LTV) to ledger
    /// @param collateralType type of collateral
    function updatePrice(bytes32 collateralType) external override {
        (uint256 price, bool has) = collateralInfo[collateralType].feed.getPrice();
        uint256 ltvPrice = has ? (price * 10**9).rdiv(unit).rdiv(collateralInfo[collateralType].liquidRatio) : 0;
        ledger.config(collateralType, "spot", ltvPrice);
        emit UpdatePrice(collateralType, price, ltvPrice);
    }
}
