pragma solidity 0.8.3;

import "../library/kip/KIP7Extended.sol";
import "../library/WadRayMath.sol";
import "../library/openzeppelin/contracts/math/SafeMath.sol";
import "../interface/IBKlay.sol";
import "../interface/IFlashBorrower.sol";
import "../interface/IOtcFund.sol";
import "../interface/klayswap/IKlaytnFactoryImpl.sol";
import "../interface/klayswap/IKlaytnExchangeImpl.sol";
import "../interface/kip/IKIP7Detailed.sol";

/// @notice bklay <-> klay swap
contract OtcFund is KIP7Extended, IOtcFund {
    using SafeMath for uint256;
    using WadRayMath for uint256;

    // --- event ---
    event DepositKlay(address caller, uint256 amount);
    event WithdrawKlay(address caller, uint256 amount);
    event FlashLoan(address caller, address borrower, uint256 klayAmount, uint256 fee);
    event UnstakeBklay(address caller, uint256 bklayAmount);
    event WithdrawTokenAndSwap(uint256 bklayAmount, uint256 klayAmount);

    // --- data ---
    IBKlay public bklay;
    uint256 public exchangeRatio; // 1056000000 bklay = 1000000000 klay
    uint256 public liquidityIndex;
    uint256 public rawTotalSupply;
    mapping(address => bool) public otcBorrower; // kokoa team
    mapping(address => bool) public otcLender; // hashed, a41, ...
    mapping(address => uint256) public rawBalance; // balance of
    uint256 public constant precision = 1e12;
    IKlaytnFactory public factory; // klaytnFactory contract
    address public aklay;
    address public aklayYield;
    uint256 public klayAKlayRatio; //975 of 1000
    uint256 public toleranceRatio; //950 of 1000
    address public klayAKlayLp;

    // --- modifier ---
    modifier onlyOtcBorrower() {
        require(otcBorrower[msg.sender], "OtcFund:: not auth borrower");
        _;
    }

    modifier onlyOtcLender() {
        require(otcLender[msg.sender], "OtcFund:: not auth lender");
        _;
    }

    // --- init & config functions  ---
    function __OtcFund_init(address bklay_) public initializer {
        __KIP7Extended_init("OTC Fund Liquidity Provider", "OTCLP", 18);
        bklay = IBKlay(bklay_);
        liquidityIndex = WadRayMath.ray();
    }

    function config(bytes32 what, uint256 data) external onlyOwner {
        if (what == "ratio") exchangeRatio = data;
        else if (what == "index") liquidityIndex = data;
        else if (what == "klayAKlayRatio") klayAKlayRatio = data;
        else if (what == "toleranceRatio") toleranceRatio = data;
        else revert("OtcFund::config:unrecognized-param");
    }

    function config(bytes32 what, address data) external onlyOwner {
        if (what == "bklay") bklay = IBKlay(data);
        else if (what == "factory") factory = IKlaytnFactory(data);
        else if (what == "aklay") aklay = data;
        else if (what == "aklayYield") aklayYield = data;
        else if (what == "klayAKlayLp") klayAKlayLp = data;
        else revert("OtcFund::config:unrecognized-param");
    }

    function setOtcBorrower(address account, bool auth) external onlyOwner {
        otcBorrower[account] = auth;
    }

    function setOtcLender(address account, bool auth) external onlyOwner {
        otcLender[account] = auth;
    }

    function unstakeBKlay(uint256 amount) external onlyOwner {
        // unstake bklay
        require(amount <= bklay.balanceOf(address(this)), "OtcFund::unstakeBKlay:overflow");
        bklay.unstakeKlay(address(this), amount);
        emit UnstakeBklay(msg.sender, amount);
    }

    function withdrawAKlayAndSwap(uint256 amount) external onlyOwner {
        uint256 klayAmount = address(this).balance;
        // withdraw withdrawKlay
        require(amount > 1 && amount <= bklay.balanceOf(address(this)), "OtcFund::withdrawAKlayAndSwap:overflow");
        bklay.withdrawKlay(address(this), amount);
        klayAmount = address(this).balance - klayAmount;
        emit WithdrawTokenAndSwap(amount, klayAmount);

        // update liquidity index
        uint256 bklayAmount = bklay.balanceOf(address(this));
        (uint256 completed, uint256 yet) = bklay.pendingKlay(address(this));
        liquidityIndex = (address(this).balance + bklayAmount + completed + yet) * precision.rayDiv(rawTotalSupply);
    }

    function withdrawAKlayAndSwapWithLimit() external onlyOperator {
        // check action condition
        (uint256 klayAmount, uint256 aklayAmount) = IKlaytnExchange(klayAKlayLp).getCurrentPool();
        if ((1000 * klayAmount) / aklayAmount > klayAKlayRatio) {
            // get limit amount
            uint256 amountMax = SafeMath.sqrt((1000 * klayAmount * aklayAmount) / toleranceRatio) - aklayAmount;
            uint256 klayAmount = address(this).balance;
            uint256 amount = SafeMath.min(amountMax, bklay.balanceOf(address(this)));

            // action swap
            bklay.withdrawKlay(address(this), amount);
            klayAmount = address(this).balance - klayAmount;
            emit WithdrawTokenAndSwap(amount, klayAmount);

            // update liquidityIndex
            uint256 bklayAmount = bklay.balanceOf(address(this));
            (uint256 completed, uint256 yet) = bklay.pendingKlay(address(this));
            liquidityIndex = (address(this).balance + bklayAmount + completed + yet) * precision.rayDiv(rawTotalSupply);
        }
    }

    // --- functions for lender  ---
    function depositKlay() public payable override onlyOtcLender {
        require(msg.value > 0, "OtcFund::depositKlay:overflow");
        rawBalance[msg.sender] = rawBalance[msg.sender] + (msg.value * precision).rayDiv(liquidityIndex);
        rawTotalSupply = rawTotalSupply + (msg.value * precision).rayDiv(liquidityIndex);

        emit DepositKlay(msg.sender, msg.value);
    }

    function withdrawKlay(uint256 amount) public override onlyOtcLender {
        _claimKlay();
        require(amount <= withdrawableKlay(msg.sender), "OtcFund::withdrawKlay:invalid amount");
        rawBalance[msg.sender] = rawBalance[msg.sender] - (amount * precision).rayDiv(liquidityIndex);
        rawTotalSupply = rawTotalSupply - (amount * precision).rayDiv(liquidityIndex);

        (bool success, ) = msg.sender.call{value: amount}("");
        require(success, "OtcFund:: withdrawKlay: transfer overflow");

        emit WithdrawKlay(msg.sender, amount);
    }

    function balanceOf(address account) public view override returns (uint256) {
        return rawBalance[account].rayMul(liquidityIndex) / precision;
    }

    function totalSupply() public view override returns (uint256) {
        return rawTotalSupply.rayMul(liquidityIndex) / precision;
    }

    /// @notice withdrawable klay
    function withdrawableKlay(address account) public view onlyOtcLender returns (uint256) {
        return SafeMath.min(address(this).balance, balanceOf(account));
    }

    function pendingKlay() external view returns (uint256, uint256) {
        (uint256 completed, uint256 yet) = bklay.pendingKlay(address(this));
        return (completed, yet);
    }

    function claimKlay() external {
        bool success = _claimKlay();
        require(success, "OtcFund::claimKlay:claimklay failed");
    }

    function _claimKlay() internal returns (bool) {
        uint256 klayBalance = address(this).balance;
        (uint256 completed, ) = bklay.pendingKlay(address(this));
        if (completed > 0) {
            bklay.claimKlay(address(this));
        }
        require(address(this).balance == klayBalance + completed, "OtcFund::_claimKlay:invalid klay balance");
        return true;
    }

    // --- functions for borrower  ---
    function flashLoan(
        address account,
        uint256 klayAmount,
        bytes calldata data
    ) external override onlyOtcBorrower returns (bool) {
        uint256 fee = _flashFee(klayAmount);
        uint256 expectedKlayAmount = address(this).balance - klayAmount;
        uint256 expectedBklayAmount = bklay.balanceOf(address(this)) + klayAmount + fee;

        // start flashloan
        (bool success, ) = msg.sender.call{value: klayAmount}("");
        require(success, "OtcFund::flashLoan: Transfer failed");
        require(IFlashBorrower(account).onFlashLoan(address(this), klayAmount, fee, data), "OtcFund::flashLoan: Callback failed");
        require(address(this).balance == expectedKlayAmount, "OtcFund::flashLoan: not expected klay balance");
        require(bklay.balanceOf(address(this)) >= expectedBklayAmount, "OtcFund::flashLoan: not expected bklay balance");
        emit FlashLoan(msg.sender, account, klayAmount, fee);

        // update liquidity index
        uint256 bklayAmount = bklay.balanceOf(address(this));
        (uint256 completed, uint256 yet) = bklay.pendingKlay(address(this));
        liquidityIndex = (address(this).balance + bklayAmount + completed + yet) * precision.rayDiv(rawTotalSupply);

        return true;
    }

    function flashFee(uint256 klayAmount) external view override onlyOtcBorrower returns (uint256) {
        return _flashFee(klayAmount);
    }

    function _flashFee(uint256 klayAmount) internal view returns (uint256) {
        require(address(this).balance >= klayAmount, "OtcFund::_flashFee:insufficient fund klay");
        require(exchangeRatio > 1000000000, "OtcFund::_flashFee:invalid exchangeRatio");
        return (klayAmount * exchangeRatio) / 1000000000 - klayAmount;
    }

    function __transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal {
        require(otcLender[sender], "OtcFund:: sender is not auth lender");
        require(otcLender[recipient], "OtcFund:: recipient is not auth lender");

        uint256 amountScaled = (amount * precision).rayDiv(liquidityIndex);
        rawBalance[sender] = rawBalance[sender] - amountScaled;
        rawBalance[recipient] = rawBalance[recipient] + amountScaled;
        emit Transfer(sender, recipient, amount);
    }

    function transfer(address recipient, uint256 amount) public override whenNotPaused returns (bool success) {
        __transfer(msg.sender, recipient, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override whenNotPaused returns (bool) {
        __transfer(sender, recipient, amount);
        _approve(sender, msg.sender, _allowances[sender][msg.sender] - amount);
        return true;
    }

    function approve(address spender, uint256 value) public virtual override whenNotPaused returns (bool) {
        require(otcLender[msg.sender], "OtcFund:: msg.sender is not auth lender");
        require(otcLender[spender], "OtcFund:: spender is not auth lender");
        return super.approve(spender, value);
    }

    receive() external payable {
        if (msg.sender == address(bklay) || msg.sender == address(factory) || msg.sender == address(aklayYield)) {
            return;
        }
        if (msg.value == 0) {
            _claimKlay();
            uint256 amount = withdrawableKlay(msg.sender);
            withdrawKlay(amount);
        } else {
            depositKlay();
        }
    }
}
