pragma solidity 0.8.3;

import "./IRewardPool.sol";

/// @title A manager about kokoa rewards.
/// @dev It manages all kokoa rewards. It's base code is from sushiswap.
/// Therefore, the way to distribute reward is very similar to sushiswap.
/// The main difference between sushiswap and KokoaRewardManager is staking.
/// Sushiswap forces to stake token, but KokoaRewardManager does not own token.
/// It just expect registered pool to call onDeposit/onWithdraw/onChanged, and
/// get balance and totalSupply of pool to calculate how to distribute reward.
/// 또한, 기존 sushiswap 에서는 블록당 minting이 되어 리워드를 생산했지만,
/// 이 변형된 코드에서는 vesting 로직에 따라서 vesting된 양을 등록된 풀에게 분배한다.
interface IKokoaRewardManager {
    struct PoolInfo {
        uint256 accRewardPerShare;
        uint256 lastRewardTimestamp;
        uint256 allocPoint;
        bool init;
        uint256 fee;
    }

    struct UserInfo {
        int256 rewardDebt;
        uint256 claimedReward; // it is used in referral contract.
    }

    /// @notice count of registered pools
    function poolLength() external view returns (uint256);

    /// @notice user info.
    function userInfo(address, address) external view returns (int256, uint256);

    /// @notice registered pool
    function rewardPool(uint256 index) external view returns (IRewardPool);

    /// @notice all registered pools
    function rewardPools() external view returns (address[] memory);

    /// @notice pool info
    function poolInfo(address pool)
        external
        view
        returns (
            uint256,
            uint256,
            uint256,
            bool,
            uint256
        );

    /// @notice total allocation point
    function totalAllocPoint() external view returns (uint256);

    /// @notice add pool to kokoaRewardManager
    /// @param allocPoint (allocPoint / totalAllocPoint) == kokoaRewardManager를 통해서 마이닝된 총 kokoa 양 중에 이 풀에 분배될 비율
    /// @param pool address of pool to register
    /// @dev 이 함수는 거버넌스에 의해서만 불려야 한다.
    function add(
        uint256 allocPoint,
        address pool,
        uint256 fee
    ) external;

    /// @notice 등록된 풀이 받는 kokoa reward의 비율을 조절한다.
    /// @param pool address of pool to register
    /// @param allocPoint (allocPoint / totalAllocPoint) == kokoaRewardManager를 통해서 마이닝된 총 kokoa 양 중에 이 풀에 분배될 비율
    /// @dev 이 함수는 거버넌스에 의해서만 불려야 한다.
    function set(
        address pool,
        uint256 allocPoint,
        uint256 fee
    ) external;

    /// @notice amount of kokoa reward to distribute, but not claimed yet
    /// @param pool address of pool
    /// @param usr address of user who is distributed kokoa reward
    function claimableReward(
        address pool,
        address usr,
        bool bonus
    ) external view returns (uint256 pending);

    /// @notice 유저에게 분배될 reward 양을 업데이트 시킨다.
    /// @param pool address of pool to update
    /// @dev 내부적으로 _updatePool이 불리는데, 이 함수는 유저가 풀에 staking 양이 변화하거나, reward를 claim하기 전에 항상 불린다
    /// 따라서 이 함수를 밖에서 명시적으로 불릴 일은 거의 없을 것이다. 일단 sushiswap 에서 구현했기 때문에 가져왔다.
    function updatePool(address pool) external;

    /// @notice 여러 풀들에 대해서 updatePool을 시켜준다.
    /// @param pools address list of pools to update
    function massUpdatePools(address[] calldata pools) external;

    /// @notice reward 분배양을 계산할 때 나눗셈을 하는데, 이때 얼마만큼의 유효숫자로 나눗셈을 할 것인지 결정한다.
    function accRewardPrecision() external pure returns (uint256);

    /// @notice 등록된 pool에서 유저가 staking한 양이 늘었을 때, 유저에게 분배할 reward 양을 계산하기 위해서 불린다.
    /// @param amount amount of increasing user's balance
    /// @param to address of user
    /// @dev 무조건 해당 풀에서만 불려야 한다. 즉 msg.sender == pool
    function onDeposit(uint256 amount, address to) external;

    /// @notice 등록된 pool에서 유저가 staking한 양이 줄었을 때, 유저에게 분배할 reward 양을 계산하기 위해서 불린다.
    /// @param amount amount of increasing user's balance
    /// @param to address of user
    /// @dev 무조건 해당 풀에서만 불려야 한다. 즉 msg.sender == pool
    function onWithdraw(uint256 amount, address to) external;

    /// @notice onDeposit과 onWithdraw의 wrapper
    /// @param amount amount of increasing user's balance
    /// @param to address of user
    /// @dev 무조건 해당 풀에서만 불려야 한다. 즉 msg.sender == pool
    function onChanged(int256 amount, address to) external;

    /// @notice 유저가 분배받은 kokoa reward를 claim 하는 function
    /// @param pool address of pool
    /// @param to receiver address of kokoa reward
    function claimReward(address pool, address to) external returns (bool success);

    function claimRewardAll() external returns (bool success);

    function claimRewardByOther(address pool, address usr) external returns (bool success);

    function claimRewardAllByOther(address usr) external returns (bool success);

    function claimRewardAndStake(address pool) external returns (bool success);

    function claimRewardAndStakeByOther(address pool, address usr) external returns (bool success);

    function claimRewardAndStakeAll() external returns (bool success);

    // --- Event ---

    event Deposit(address indexed pool, uint256 amount, address indexed to);
    event Withdraw(address indexed pool, uint256 amount, address indexed to);
    event ClaimReward(address indexed pool, address indexed user, uint256 amount);
    event LogPoolAddition(uint256 indexed pid, uint256 allocPoint, address indexed lpToken, uint256 fee);
    event LogSetPool(address indexed pool, uint256 allocPoint, uint256 fee);
    event LogUpdatePool(address indexed pool, uint256 lastRewardTimestamp, uint256 lpSupply, uint256 accRewardPerShare);
    event LogInit();
}
