pragma solidity 0.8.3;

/// @notice This contract gives incentive to invitor who invites invitee into this service
/// The amount of incentive is proportional to reward that invitee receive
interface IReferral {
    event Registered(address invitor, address invitee);

    /// @notice get invitor address by invitee address
    /// @param invitee address of user
    function invitorOf(address invitee) external view returns (address);

    /// @notice total amount of reward that invitee received
    /// @param invitee address of user
    function receivedReward(address invitee) external view returns (uint256);

    /// @notice multiplicative factor. It is used for calculating referral reward.
    function multiplicativeFactor() external view returns (uint256);

    /// @notice record relation between invitor and invitee
    /// @param invitor address of invitor
    /// @param invitee address of invitee
    function register(address invitor, address invitee) external;

    /// @notice amount of reward created from invitee activity.
    /// @param invitee address of invitee
    function claimableRewardFrom(address invitee) external view returns (uint256);

    /// @notice harvest reward which is created from invitee activity
    /// @param invitee address of invitee
    function claimRewardFrom(address invitee) external;
}
