pragma solidity 0.8.3;

import "../interface/IReferral.sol";
import "../interface/IKokoaRewardManager.sol";
import "../library/AccessControl.sol";
import "../library/Pausable.sol";
import "../interface/kip/IKIP7.sol";
import "../library/WadRayMath.sol";
import "../interface/IEcosystemBeneficiary.sol";

/// @notice This contract gives incentive to users who invites other users to Kokoa
/// The amount of incentive is proportional to the liquidity mining reward that invitee earns
/// This contract manages the relationship between invitors and invitees
/// and periodically calcualtes the amount of reward that each invitors should receive
/// The soure of fund that this contract distributes as reward is the 'beneficiary' (initially, an EOA)
/// And the source of fund of the beneficiary is the Ecosystem Fund
/// The beneficiary must approve Referral to spend its Kokoa

/// ecosystem fund -> beneficiary EOA <- Referral contract grabs Kokoa from beneficiary
contract Referral is IReferral, AccessControl, Pausable {
    using WadRayMath for uint256;

    IKIP7 public kokoa; // address of KokoaToken contract
    address public ecosystemBeneficiary; /// may be beneficiary of ecosystem vesting
    IKokoaRewardManager public kokoaRewardManager; /// address of KokoaRewardManager contract

    mapping(address => address) public override invitorOf; /// get invitor address by invitee address
    mapping(address => uint256) public override receivedReward; /// total amount of reward that invitee received

    uint256 public override multiplicativeFactor; /// invitee 가 받은 reward의 양에 얼마만큼 비례해서 kokoa를 줄지 결정하는 설정값

    function __Referral_init(address kokoa_, address kokoaRewardManager_) public initializer {
        __Pausable_init();
        kokoa = IKIP7(kokoa_);
        kokoaRewardManager = IKokoaRewardManager(kokoaRewardManager_);
    }

    function config(bytes32 what, uint256 data) external onlyOwner {
        if (what == "multiplicativeFactor") multiplicativeFactor = data;
        else revert("Referral::config: unrecognized-param");
    }

    function config(bytes32 what, address data) external onlyOwner {
        if (what == "ecosystemBeneficiary") ecosystemBeneficiary = data;
        else revert("Referral::config: unrecognized-param");
    }

    /// @dev kokoaRewardManager를 통해서 invitee가 받은 kokoa reward의 양을 계산하는 function이다.
    /// For loop exists, but the number of pool may be limited upto 5 or 6.
    /// Therefore, I think it is okay.
    function _receivedReward(address usr) internal view returns (uint256) {
        uint256 reward = 0;
        uint256 poolLength = kokoaRewardManager.poolLength();
        for (uint256 i = 0; i < poolLength; i++) {
            address pool = address(kokoaRewardManager.rewardPool(i));
            (, uint256 claimedReward) = (kokoaRewardManager.userInfo(pool, usr));
            uint256 claimableReward = kokoaRewardManager.claimableReward(pool, usr, true);
            reward = reward + claimedReward + claimableReward;
        }
        return reward;
    }

    /// @notice record relation between invitor and invitee
    /// @param invitor address of invitor
    /// @param invitee address of invitee
    /// 오직 화이트리스팅된 operator에 의해서만 등록되어야 한다.
    /// invitor != invitee <- need to check
    function register(address invitor, address invitee) external override onlyOperator whenNotPaused {
        require(invitorOf[msg.sender] == address(0), "Referral::invited: already invited");
        invitorOf[invitee] = invitor;
        receivedReward[invitee] = _receivedReward(invitee);
        emit Registered(invitor, invitee);
    }

    //    function invited(address invitor) external {
    //        require(invitorOf[msg.sender] == address(0), "Referral::invited: already invited");
    //    }

    /// @notice amount of reward created from invitee activity(received kokoa reward)
    /// @param invitee address of invitee
    function claimableRewardFrom(address invitee) public view override returns (uint256) {
        require(invitorOf[invitee] != address(0), "Referral::claimableRewardFrom: invalid invitee");
        uint256 nowReceivedReward = _receivedReward(invitee);
        uint256 lastReceivedReward = receivedReward[invitee];
        return (nowReceivedReward - lastReceivedReward).rmul(multiplicativeFactor);
    }

    /// @notice claim reward created from invitee activity(received kokoa reward)
    /// @param invitee address of invitee
    function claimRewardFrom(address invitee) external override whenNotPaused {
        require(invitorOf[invitee] != address(0), "Referral::claimRewardFrom: invalid invitee");
        uint256 nowReceivedReward = _receivedReward(invitee);
        uint256 lastReceivedReward = receivedReward[invitee];
        uint256 claimableReward = (nowReceivedReward - lastReceivedReward).rmul(multiplicativeFactor);
        receivedReward[invitee] = nowReceivedReward;

        address invitor = invitorOf[invitee];
        IEcosystemBeneficiary(ecosystemBeneficiary).claim(address(this), claimableReward);
        kokoa.transfer(invitor, claimableReward);
    }
}
