pragma solidity 0.8.3;
pragma experimental ABIEncoderV2;

import "../library/kip/KIP7Extended.sol";
import "../library/openzeppelin/contracts/utils/SafeCast.sol";
import "../library/openzeppelin/contracts/utils/Math.sol";
import "../interface/ISKokoaToken.sol";
import "../interface/IKSDRewardManager.sol";
import "../interface/IKokoaRewardManager.sol";

/// @title staked kokoa token
/// @notice it is staked governance token.
/// spec (kor)
/// 1. kokoa를 staking 하면 staking 한 양 만큼 skokoa를 준다.
/// 2. unstaking을 하면 그 양 만큼 kokoa를 되돌려 주지만, 30일 뒤에 claim 할 수 있다.
/// 3. 이 토큰을 소유한 유저에게 yield 수익의 일부를 ksd 의 형태로 나누어준다.
/// 4. 이 토큰을 소유한 유저에게 kokoa reward를 나누어 준다.
/// 5. sKokoa를 transfer하더라도 기존에 받은 reward는 원소유주가 claim할 수 있게 남아있다

/// spec (eng)
/// 1. By staking Kokoa, a user will receive the same amount of sKokoa
/// 2. When a user burns sKokoa, he will be able to claim the same amount of Kokoa after 30 days.
/// 3. Any address that possess sKokoa will be able to claim the protocol profit (KSD) - which is generated by Yield module
/// 4. Any address that possess sKokoa will be able to claim the mining reward (Kokoa) - which comes from our liquidity mining program
/// 5. When a user transfers sKokoa to others, the remaining unclaimed reward stays intact - he can still claim that reward.

/// @dev There will be no actual on-chain governance system in the beginning.
/// This is the foundation of future on-chain governance that will be implemented later.
/// Nevertheless, users will still be able to earn governance reward by staking Kokoa (KSD by protocol profit + Kokoa by liquidity mining)
/// The on-chain governance will utilize delegated staking.
/// Only the whitelisted members are able to directly participate in the governance, and other users will have to delegate their votes to them.

/// The code base here is based on Compound's governance
/// And this contract will be added to rewardManagers as one of the pools to receive reward
contract SKokoaToken is KIP7Extended, ISKokoaToken {
    IKIP7 public Kokoa;
    /// @dev A record of each accounts' delegate - i.e., select the node to delegate their voting power
    /// Only the whitelisted addresses can be delegatees
    mapping(address => address) public override delegates;

    /// @dev A record of votes checkpoints for each account, by index
    mapping(address => mapping(uint32 => Checkpoint)) internal _checkpoints;

    /// @dev The number of checkpoints for each account
    mapping(address => uint32) public override numCheckpoints;

    /// @notice The EIP-712 typehash for the contract's domain
    bytes32 public constant DOMAIN_TYPEHASH = keccak256("EIP712Domain(string name,uint256 chainId,address verifyingContract)");

    /// @notice The EIP-712 typehash for the delegation struct used by the contract
    bytes32 public constant DELEGATION_TYPEHASH = keccak256("Delegation(address delegatee,uint256 nonce,uint256 expiry)");

    uint256 public chainId;

    /// @dev A record of states for signing / validating signatures
    mapping(address => uint256) public override nonces;

    struct UnstakeRecord {
        uint256 block;
        uint256 amount;
    }

    // for tracking amount of staking/unstaking/pending
    mapping(address => uint256) public override unstakeCount;
    mapping(address => uint256) public override claimCount;
    mapping(address => mapping(uint256 => UnstakeRecord)) public override unstakeRecord;

    uint256 public override lockUpPeriod;

    IKSDRewardManager private _ksdRewardManager;
    IKokoaRewardManager private _kokoaRewardManager;

    function __SKokoaToken_init(uint256 chainId_, address kokoa_) public initializer {
        __KIP7Extended_init("Staked Kokoa", "sKOKOA", 18);
        Kokoa = IKIP7(kokoa_);
        chainId = chainId_;
    }

    function config(bytes32 what, address data) external onlyOwner {
        if (what == "ksdRewardManager") _ksdRewardManager = IKSDRewardManager(data);
        else if (what == "kokoaRewardManager") _kokoaRewardManager = IKokoaRewardManager(data);
        else revert("SKokoaToken::config: unrecognized-param");
    }

    function config(bytes32 what, uint256 data) external onlyOwner {
        if (what == "lockUpPeriod") lockUpPeriod = data;
        else revert("SKokoaToken::config: unrecognized-param");
    }

    function checkpoints(address account, uint32 index) external view override returns (Checkpoint memory checkpoint) {
        checkpoint = _checkpoints[account][index];
    }

    /// @notice transfer
    /// @dev it should notice to rewardManager that the amount of balance is changed
    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _ksdRewardManager.onChanged(-SafeCast.toInt256(amount), msg.sender);
        _ksdRewardManager.onChanged(SafeCast.toInt256(amount), recipient);
        _kokoaRewardManager.onChanged(-SafeCast.toInt256(amount), msg.sender);
        _kokoaRewardManager.onChanged(SafeCast.toInt256(amount), recipient);
        super.transfer(recipient, amount);

        _moveDelegates(delegates[msg.sender], delegates[recipient], amount);
        return true;
    }

    /// @notice transferFrom
    /// @dev it should notice to rewardManager that the amount of balance is changed
    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _ksdRewardManager.onChanged(-SafeCast.toInt256(amount), sender);
        _ksdRewardManager.onChanged(SafeCast.toInt256(amount), recipient);
        _kokoaRewardManager.onChanged(-SafeCast.toInt256(amount), sender);
        _kokoaRewardManager.onChanged(SafeCast.toInt256(amount), recipient);
        super.transferFrom(sender, recipient, amount);

        _moveDelegates(delegates[sender], delegates[recipient], amount);
        return true;
    }

    /// @notice stake kokoa and receive sKokoa
    /// @param amount amount of kokoa to stake
    function stake(address user, uint256 amount) external {
        _ksdRewardManager.onChanged(SafeCast.toInt256(amount), user);
        _kokoaRewardManager.onChanged(SafeCast.toInt256(amount), user);

        Kokoa.transferFrom(msg.sender, address(this), amount);
        _mint(user, amount);
        _moveDelegates(address(0), delegates[user], amount);

        emit Stake(msg.sender, user, amount);
    }

    /// @notice burn sKokoa and unstake kokoa. It takes time to receive kokoa.
    /// @param amount amount of kokoa to unstake
    function unstake(address user, uint256 amount) external {
        _ksdRewardManager.onChanged(-SafeCast.toInt256(amount), msg.sender);
        _kokoaRewardManager.onChanged(-SafeCast.toInt256(amount), msg.sender);

        _burn(msg.sender, amount);
        _moveDelegates(delegates[msg.sender], address(0), amount);

        uint256 count = unstakeCount[user];
        if (count > 0 && unstakeRecord[user][count - 1].block == block.timestamp) {
            unstakeRecord[user][count - 1].amount += amount;
        } else {
            unstakeRecord[user][count] = UnstakeRecord({block: block.timestamp, amount: amount});
            unstakeCount[user] += 1;
        }

        emit Unstake(msg.sender, user, amount);
    }

    /// @notice receive kokoa which is after 30 days from unstaking.
    function claim(address user) external {
        require(unstakeCount[user] > 0, "SKokoaToken::claim: should unstake first");
        uint256 claimAmount = 0;

        uint256 safeCount = Math.min(claimCount[user] + 10, unstakeCount[user]);
        for (uint256 i = claimCount[user]; i < safeCount; i++) {
            if (block.timestamp - unstakeRecord[user][i].block >= lockUpPeriod) {
                claimAmount = claimAmount + unstakeRecord[user][i].amount;
                claimCount[user] += 1;
            } else {
                break;
            }
        }
        Kokoa.transfer(user, claimAmount);
        emit Claim(user, claimAmount);
    }

    /**
     * @notice Delegate votes from `msg.sender` to `delegatee`
     * @param delegatee The address to delegate votes to
     */
    function delegate(address delegatee) public override {
        return _delegate(msg.sender, delegatee);
    }

    /**
     * @notice Delegates votes from signatory to `delegatee`
     * @param delegatee The address to delegate votes to
     * @param nonce The contract state required to match the signature
     * @param expiry The time at which to expire the signature
     * @param v The recovery byte of the signature
     * @param r Half of the ECDSA signature pair
     * @param s Half of the ECDSA signature pair
     */
    function delegateBySig(
        address delegatee,
        uint256 nonce,
        uint256 expiry,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external override {
        bytes32 domainSeparator = keccak256(abi.encode(DOMAIN_TYPEHASH, keccak256(bytes(name())), chainId, address(this)));

        bytes32 structHash = keccak256(abi.encode(DELEGATION_TYPEHASH, delegatee, nonce, expiry));

        bytes32 digest = keccak256(abi.encodePacked("\x19\x01", domainSeparator, structHash));

        address signatory = ecrecover(digest, v, r, s);
        require(signatory != address(0), "SKokoa::delegateBySig: invalid signature");
        require(nonce == nonces[signatory]++, "SKokoa::delegateBySig: invalid nonce");
        require(block.timestamp <= expiry, "SKokoa::delegateBySig: signature expired");
        return _delegate(signatory, delegatee);
    }

    /**
     * @notice Gets the current votes balance for `account`
     * @param account The address to get votes balance
     * @return The number of current votes for `account`
     */
    function getCurrentVotes(address account) external view override returns (uint96) {
        uint32 nCheckpoints = numCheckpoints[account];
        return nCheckpoints > 0 ? _checkpoints[account][nCheckpoints - 1].votes : 0;
    }

    /**
     * @notice Determine the prior number of votes for an account as of a block number
     * @dev Block number must be a finalized block or else this function will revert to prevent misinformation.
     * @param account The address of the account to check
     * @param blockNumber The block number to get the vote balance at
     * @return The number of votes the account had as of the given block
     */
    function getPriorVotes(address account, uint256 blockNumber) external view override returns (uint96) {
        require(blockNumber < block.number, "SKokoa::getPriorVotes: not yet determined");

        uint32 nCheckpoints = numCheckpoints[account];
        if (nCheckpoints == 0) {
            return 0;
        }

        // First check most recent balance
        if (_checkpoints[account][nCheckpoints - 1].fromBlock <= blockNumber) {
            return _checkpoints[account][nCheckpoints - 1].votes;
        }

        // Next check implicit zero balance
        if (_checkpoints[account][0].fromBlock > blockNumber) {
            return 0;
        }

        uint32 lower = 0;
        uint32 upper = nCheckpoints - 1;
        while (upper > lower) {
            uint32 center = upper - (upper - lower) / 2; // ceil, avoiding overflow
            Checkpoint memory cp = _checkpoints[account][center];
            if (cp.fromBlock == blockNumber) {
                return cp.votes;
            } else if (cp.fromBlock < blockNumber) {
                lower = center;
            } else {
                upper = center - 1;
            }
        }
        return _checkpoints[account][lower].votes;
    }

    function _delegate(address delegator, address delegatee) internal {
        address currentDelegate = delegates[delegator];
        uint256 delegatorBalance = _balances[delegator]; // balance of underlying sKOKOAs (not scaled);
        delegates[delegator] = delegatee;

        emit DelegateChanged(delegator, currentDelegate, delegatee);

        _moveDelegates(currentDelegate, delegatee, delegatorBalance);
    }

    function _moveDelegates(
        address srcRep,
        address dstRep,
        uint256 amount
    ) internal {
        if (srcRep != dstRep && amount > 0) {
            if (srcRep != address(0)) {
                // decrease old representative
                uint32 srcRepNum = numCheckpoints[srcRep];
                uint96 srcRepOld = srcRepNum > 0 ? _checkpoints[srcRep][srcRepNum - 1].votes : 0;
                uint96 srcRepNew = srcRepOld - SafeCast.toUint96(amount);
                _writeCheckpoint(srcRep, srcRepNum, srcRepOld, srcRepNew);
            }

            if (dstRep != address(0)) {
                // increase new representative
                uint32 dstRepNum = numCheckpoints[dstRep];
                uint96 dstRepOld = dstRepNum > 0 ? _checkpoints[dstRep][dstRepNum - 1].votes : 0;
                uint96 dstRepNew = dstRepOld + SafeCast.toUint96(amount);
                _writeCheckpoint(dstRep, dstRepNum, dstRepOld, dstRepNew);
            }
        }
    }

    function _writeCheckpoint(
        address delegatee,
        uint32 nCheckpoints,
        uint96 oldVotes,
        uint96 newVotes
    ) internal {
        uint32 blockNumber = safe32(block.number, "SKokoa::_writeCheckpoint: block number exceeds 32 bits");

        if (nCheckpoints > 0 && _checkpoints[delegatee][nCheckpoints - 1].fromBlock == blockNumber) {
            _checkpoints[delegatee][nCheckpoints - 1].votes = newVotes;
        } else {
            _checkpoints[delegatee][nCheckpoints] = Checkpoint(blockNumber, newVotes);
            numCheckpoints[delegatee] = nCheckpoints + 1;
        }

        emit DelegateVotesChanged(delegatee, oldVotes, newVotes);
    }

    function safe32(uint256 n, string memory errorMessage) internal pure returns (uint32) {
        require(n < 2**32, errorMessage);
        return uint32(n);
    }
}
