pragma solidity 0.8.3;

import "./IRewardPool.sol";
import "./IDistributor.sol";

/// @title A manager about ksd rewards.
/// @dev It manages all ksd rewards. It's base code is sushiswap.
/// Therefore, the way to distribute reward is very similar to sushiswap.
/// The main difference between sushiswap and KSDRewardManager is staking.
/// Sushiswap forces to stake token. But, KSDRewardManager does not own token.
/// It just expect registered pool to call onDeposit/onWithdraw/onChanged.
/// 또한, 기존 sushiswap 에서는 블록당 minting이 되어 리워드를 생산했지만,
/// 이 변형된 코드에서는 yield에서 생산된 이익을 이 컨트렉트의 distribute 함수를 통해서 넣어준 것만 분배한다.
interface IKSDRewardManager is IDistributor {
    struct PoolInfo {
        uint256 accRewardPerShare;
        uint256 ksdReward;
        uint256 allocPoint;
        bool init;
    }

    struct UserInfo {
        int256 rewardDebt;
        uint256 claimedReward;
    }

    struct EarnHistory {
        uint256 timestamp;
        uint256 amount;
    }

    function range() external view returns (uint256);

    function targetDksdApr() external view returns (uint256);

    function earnHistoriesLength() external view returns (uint256);

    function earnHistories(uint256 index) external view returns (uint256 timestamp, uint256 amount);

    function earnCursor() external view returns (uint256);

    function earnSumInRange() external view returns (uint256);

    /// @notice count of registered pools
    function poolLength() external view returns (uint256);

    /// @notice user info.
    function userInfo(address, address) external view returns (int256, uint256);

    /// @notice registered pool
    function rewardPool(uint256 index) external view returns (IRewardPool);

    /// @notice pool info
    function poolInfo(address pool)
        external
        view
        returns (
            uint256,
            uint256,
            uint256,
            bool
        );

    /// @notice all registered pools
    function rewardPools() external view returns (address[] memory);

    function totalAllocPoint() external view returns (uint256);

    function add(uint256 allocPoint, address token) external;

    function set(address pool, uint256 allocPoint) external;

    function claimableReward(address pool, address user) external view returns (uint256 pending);

    function massUpdatePools(address[] calldata pools) external;

    function accRewardPrecision() external pure returns (uint256);

    function updatePool(address pool) external;

    function onDeposit(uint256 amount, address to) external;

    function onWithdraw(uint256 amount, address to) external;

    function onChanged(int256 amount, address to) external;

    function claimReward(address pool, address to) external returns (bool success);

    function claimRewardByOther(address pool, address usr) external returns (bool success);

    function claimRewardAllByOther(address usr) external returns (bool success);

    /// distribute 함수는 IDistributor.sol에 있다.

    event Deposit(address indexed pool, uint256 amount, address indexed to);
    event Withdraw(address indexed pool, uint256 amount, address indexed to);
    event ClaimReward(address indexed pool, address indexed user, uint256 amount);
    event LogPoolAddition(uint256 indexed pid, uint256 allocPoint, address indexed lpToken);
    event LogSetPool(address indexed pool, uint256 allocPoint);
    event LogUpdatePool(address indexed pool, uint256 now, uint256 lpSupply, uint256 accRewardPerShare);
    event LogInit();
}
