pragma solidity ^0.8.3;

import "./openzeppelin/proxy/Initializable.sol";

contract AccessControl is Initializable {
    struct RoleData {
        mapping(address => bool) members;
        bytes32 adminRole;
    }

    mapping(bytes32 => RoleData) private _roles;

    bytes32 public constant DEFAULT_ADMIN_ROLE = 0x00;

    event RoleAdminChanged(bytes32 indexed role, bytes32 indexed previousAdminRole, bytes32 indexed newAdminRole);
    event RoleGranted(bytes32 indexed role, address indexed account, address indexed sender);
    event RoleRevoked(bytes32 indexed role, address indexed account, address indexed sender);
    modifier onlyRole(bytes32 role) {
        _checkRole(role, msg.sender);
        _;
    }

    modifier onlyOwner() {
        _checkRole(bytes32("owner"), msg.sender);
        _;
    }

    modifier onlyAdmin() {
        _checkRole(bytes32("admin"), msg.sender);
        _;
    }

    modifier onlyOperator() {
        _checkRole(bytes32("operator"), msg.sender);
        _;
    }

    function __AccessControl_init() internal initializer {
        __AccessControl_init_unchained();
    }

    function __AccessControl_init_unchained() private initializer {
        _setupRole(bytes32("owner"), msg.sender);
        _setRoleAdmin(bytes32("admin"), bytes32("owner"));
        _setRoleAdmin(bytes32("operator"), bytes32("owner"));
        _setupRole(bytes32("admin"), msg.sender);
        _setupRole(bytes32("operator"), msg.sender);
    }

    function hasRole(bytes32 role, address account) public view returns (bool) {
        return _roles[role].members[account];
    }

    function _checkRole(bytes32 role, address account) internal view {
        if (!hasRole(role, account)) {
            revert("AccessControl:_checkRole:invalid role");
        }
    }

    function getRoleAdmin(bytes32 role) public view returns (bytes32) {
        return _roles[role].adminRole;
    }

    function grantRole(bytes32 role, address account) public onlyRole(getRoleAdmin(role)) {
        _grantRole(role, account);
    }

    function revokeRole(bytes32 role, address account) public onlyRole(getRoleAdmin(role)) {
        _revokeRole(role, account);
    }

    function renounceRole(bytes32 role, address account) public {
        require(account == msg.sender, "AccessControl: can only renounce roles for self");

        _revokeRole(role, account);
    }

    function _setupRole(bytes32 role, address account) internal {
        _grantRole(role, account);
    }

    function _setRoleAdmin(bytes32 role, bytes32 adminRole) internal {
        bytes32 previousAdminRole = getRoleAdmin(role);
        _roles[role].adminRole = adminRole;
        emit RoleAdminChanged(role, previousAdminRole, adminRole);
    }

    function _grantRole(bytes32 role, address account) private {
        if (!hasRole(role, account)) {
            _roles[role].members[account] = true;
            emit RoleGranted(role, account, msg.sender);
        }
    }

    function _revokeRole(bytes32 role, address account) private {
        if (hasRole(role, account)) {
            _roles[role].members[account] = false;
            emit RoleRevoked(role, account, msg.sender);
        }
    }

    // --- ownable --
    function transferOwnership(address newOwner) public onlyOwner {
        _grantRole(bytes32("owner"), newOwner);
        _revokeRole(bytes32("owner"), msg.sender);
    }
}
","keccak256":"0x16d2c3697b545ce0f8f6508d64eef0522b972736cbe77fc7c2e7e50a348f94b8","license":"MIT"},"contracts/library/Pausable.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "./AccessControl.sol";

abstract contract Pausable is AccessControl {
    event Paused(address account);
    event Unpaused(address account);

    bool private _paused;

    function __Pausable_init() internal initializer {
        __AccessControl_init();
        _setRoleAdmin(bytes32("pauser"), bytes32("owner"));
        _setupRole(bytes32("pauser"), msg.sender);
    }

    function paused() public view virtual returns (bool) {
        return _paused;
    }

    modifier whenNotPaused() virtual {
        require(!paused(), "Pausable: paused");
        _;
    }

    modifier whenPaused() {
        require(paused(), "Pausable: not paused");
        _;
    }

    function pause() public whenNotPaused onlyRole("pauser") {
        _paused = true;
        emit Paused(msg.sender);
    }

    function unpause() public whenPaused onlyRole("pauser") {
        _paused = false;
        emit Unpaused(msg.sender);
    }
}
