pragma solidity 0.8.3;

import "./IPausable.sol";

/// @notice manages auctions. It is used in liquidator.
interface IAuctionHouse {
    struct Sale {
        uint256 index; // Index in active array
        uint256 raiseAmount; // KSD to raise       [rad]
        uint256 sellAmount; // collateral to sell [wad]
        address vaultOwner; // Liquidated vault
        uint96 startTime; // Auction start time
        uint256 startPrice; // Starting price     [ray]
    }

    // --- Events ---
    event Config(bytes32 indexed what, uint256 data);
    event Config(bytes32 indexed what, address data);

    event Start(
        uint256 indexed id,
        uint256 startPrice,
        uint256 raiseAmount,
        uint256 sellAmount,
        address indexed vaultOwner,
        address indexed keeper,
        uint256 coin
    );
    event Take(uint256 indexed id, uint256 max, uint256 price, uint256 owe, uint256 raiseAmount, uint256 sellAmount, address indexed vaultOwner);
    event Redo(
        uint256 indexed id,
        uint256 startPrice,
        uint256 raiseAmount,
        uint256 sellAmount,
        address indexed vaultOwner,
        address indexed keeper,
        uint256 coin
    );

    event Cancel(uint256 id);

    function collateralType() external view returns (bytes32);

    function buf() external view returns (uint256);

    function timeLimit() external view returns (uint256);

    function dropLimit() external view returns (uint256);

    function ratioIncentive() external view returns (uint64);

    function constIncentive() external view returns (uint192);

    function bound() external view returns (uint256);

    function cnt() external view returns (uint256);

    function active(uint256 id) external view returns (uint256);

    function sales(uint256 id)
        external
        view
        returns (
            uint256,
            uint256,
            uint256,
            address,
            uint96,
            uint256
        );

    /// @notice starts an auction
    /// trusts the caller to transfer collateral to the contract
    /// The `startPrice` is obtained as follows:
    ///
    ///     startPrice = feedPrice * buf / par
    ///
    /// Where `feedPrice` is the collateral's unitary value in USD, `buf` is a
    /// multiplicative factor to increase the starting price, and `par` is a
    /// reference per KSD.
    /// @param raiseAmount ksd amount to raise through auction. [rad]
    /// @param sellAmount collateral amount to sell through auction. [rad]
    /// @param vaultOwner address will receive any leftover collateral
    /// @param keeper address that will receive incentives
    function start(
        uint256 raiseAmount,
        uint256 sellAmount,
        address vaultOwner,
        address keeper
    ) external returns (uint256 id);

    /// @notice cancel an auction during EmergencyStop or via governance action.
    /// @param id sale id
    function cancel(uint256 id) external;

    function take(
        uint256 id,
        uint256 amt,
        uint256 max,
        address to,
        bytes calldata data
    ) external;

    function getStatus(uint256 id)
        external
        view
        returns (
            bool needsRedo,
            uint256 price,
            uint256 sellAmount, // collateral amount
            uint256 raiseAmount // raise amount
        );
}
