pragma solidity 0.8.3;
import "./kip/IKIP7Extended.sol";

/// @title Bonded Collateral Contract
/// @notice Mint/Burn logic of Bond
interface IBond is IKIP7Extended {
    event Stake(address from, address to, uint256 tokenAmount, uint256 bondAmount);
    event Unstake(address from, address to, uint256 tokenAmount, uint256 bondAmount);

    /// @notice Calculate bond amount corresponding to the given token amount
    function fromTokenAmount(uint256 tokenAmount) external view returns (uint256 bondAmount);

    /// @notice Calculate token amount corresponding to the given bond amount
    function toTokenAmount(uint256 bondAmount) external view returns (uint256 tokenAmount);

    /// @notice Stake token to this contract and return bond.
    function stake(address to, uint256 tokenAmount) external returns (uint256 bondAmount);

    /// @notice Untake to return staked token.
    function unstake(address to, uint256 bondAmount) external returns (uint256 tokenAmount);
}
