pragma solidity 0.8.3;

import "../interface/kip/IKIP7.sol";
import "../interface/klayswap/IKlaytnExchangeImpl.sol";
import "../interface/klayswap/IKlaytnFactoryImpl.sol";
import "../interface/IAuctionHouse.sol";
import "../interface/IAuctionTakerCallee.sol";
import "../interface/IBond.sol";
import "../interface/IGateway.sol";
import "../interface/IKSDGateway.sol";
import "../interface/ILedger.sol";
import "../library/AccessControl.sol";
import "../library/Pausable.sol";
import "../interface/IAddressBook.sol";

contract SingleCollateralAuctionTaker is AccessControl, Pausable, IAuctionTakerCallee {
    struct TakerInfo {
        // collateral -> ksd path
        address[] swapPath;
    }

    IAddressBook public addressBook;
    mapping(bytes32 => TakerInfo) internal takerInfos;

    mapping(bytes32 => uint256) public minProfitRate; // min profit rate * 1e9

    uint256 constant RAY = 10**27;

    function __SingleCollateralAuctionTaker_init() public initializer {
        __Pausable_init();
    }

    function config(bytes32 what, address data) external onlyAdmin {
        if (what == "addressBook") addressBook = IAddressBook(data);
        else revert("Taker::config:unrecognized-param");
    }

    function config(
        bytes32 what,
        bytes32 colType,
        address[] calldata data
    ) external onlyAdmin {
        if (what == "swapPath") takerInfos[colType].swapPath = data;
        else revert("Taker::config:unrecognized-param");
    }

    function config(
        bytes32 what,
        bytes32 colType,
        uint256 data
    ) external onlyAdmin {
        if (what == "minProfitRate") minProfitRate[colType] = data;
        else revert("Taker::config:unrecognized-param");
    }

    function withdrawKlay(address to, uint256 amount) external onlyAdmin {
        if (amount == 0) {
            (bool success, ) = to.call{value: address(this).balance}("");
            require(success);
        } else {
            (bool success, ) = to.call{value: amount}("");
            require(success);
        }
    }

    function withdrawToken(
        IKIP7 token,
        address to,
        uint256 amount
    ) external onlyAdmin {
        if (amount == 0) {
            token.transfer(to, token.balanceOf(address(this)));
        } else {
            token.transfer(to, amount);
        }
    }

    function joinKsdGateway(uint256 amount) external onlyAdmin {
        IKSDGateway(addressBook.ksdGateway()).join(address(this), amount);
    }

    function exitKsdGateway(uint256 amount) external onlyAdmin {
        IKSDGateway(addressBook.ksdGateway()).exit(address(this), amount);
    }

    function stakeCollateralGateway(bytes32 colType, uint256 amount) external onlyAdmin {
        IGateway(addressBook.getCollateralContract(colType, bytes32("gateway"))).stake(amount);
    }

    function unstakeCollateralGateway(bytes32 colType, uint256 amount) external onlyAdmin {
        IGateway(addressBook.getCollateralContract(colType, bytes32("gateway"))).unstake(amount);
    }

    function isProfitable(bytes32 colType, uint256 id) external view returns (bool) {
        uint256 bondAmount = bondAmountWithMaxProfit(colType, id);
        return bondAmount != 0;
    }

    // take auction -> sell collateral -> repay ksd
    function takeAndExchange(bytes32 colType, uint256 id) external onlyOperator {
        uint256 bondAmount = bondAmountWithMaxProfit(colType, id);
        if (bondAmount == 0) {
            return;
        }
        bytes memory data = abi.encode(colType);
        address auctionHouse = addressBook.getCollateralContract(colType, bytes32("bondAuctionHouse"));
        IAuctionHouse(auctionHouse).take(id, bondAmount, uint256(int256(-1)), address(this), data);
    }

    function bondAmountWithMaxProfit(bytes32 colType, uint256 id) public view returns (uint256 bondAmount) {
        // price: ray, sellAmount: wad, raiseAmount: rad
        address auctionHouse = addressBook.getCollateralContract(colType, bytes32("bondAuctionHouse"));
        address bond = addressBook.getCollateralContract(colType, bytes32("bond"));
        (bool needsRedo, uint256 price, uint256 sellAmount, uint256 raiseAmount) = IAuctionHouse(auctionHouse).getStatus(id);
        if (needsRedo) {
            return 0;
        }

        // collateral price in ray
        // ksd --(auction)-> collateral --(klayswap)-> ksd
        // collateral sell price must be higher than this

        // collateral token sell amount
        uint256 maxSwapColAmount = maxSwap(colType, IBond(bond).toTokenAmount(sellAmount), (price * minProfitRate[colType]) / 1e9);

        bondAmount = IBond(bond).fromTokenAmount(maxSwapColAmount);
        uint256 maxBondAmount = min(sellAmount, raiseAmount / price + 1);
        if (bondAmount >= maxBondAmount) {
            bondAmount = maxBondAmount;
        } else if (raiseAmount <= IAuctionHouse(auctionHouse).bound()) {
            // no partial take for small auctions
            return 0;
        }
    }

    function takerCall(
        address taker,
        uint256 owe,
        uint256 slice,
        bytes calldata data
    ) external override {
        bytes32 colType = abi.decode(data, (bytes32));
        TakerInfo memory takerInfo = takerInfos[colType];
        address token = addressBook.getCollateralContract(colType, bytes32("token"));

        // called only when this contract takes an auction from the auction house
        require(msg.sender == addressBook.getCollateralContract(colType, bytes32("bondAuctionHouse")));
        require(taker == address(this));

        // exit collateral gateway
        uint256 tokenBefore = IKIP7(token).balanceOf(address(this));
        IGateway(addressBook.getCollateralContract(colType, bytes32("gateway"))).unstake(slice);
        uint256 tokenAmount = IKIP7(token).balanceOf(address(this)) - tokenBefore;

        // swap to ksd
        {
            address ksp = addressBook.ksp();
            address ksd = addressBook.ksd();
            IKIP7(token).approve(ksp, tokenAmount);
            IKlaytnFactory(ksp).exchangeKctPos(token, tokenAmount, ksd, 1, takerInfo.swapPath);
        }

        // join ksd gateway
        IKSDGateway(addressBook.ksdGateway()).join(address(this), owe / RAY);
    }

    // returns amountIn of the maximum swap
    function maxSwapView(bytes32 colType, uint256 amountIn_) external view returns (uint256) {
        address tokenIn = addressBook.getCollateralContract(colType, bytes32("token"));
        address tokenOut = addressBook.ksd();
        address[] memory path = takerInfos[colType].swapPath;
        uint256 n = path.length;

        // Caching
        uint256[] memory cachePoolIn = new uint256[](n + 1);
        uint256[] memory cachePoolOut = new uint256[](n + 1);
        uint256[] memory cacheFee = new uint256[](n + 1);

        {
            address ksp = addressBook.ksp();
            address nowToken = tokenIn;
            address pool;
            address tokenA;
            uint256 poolA;
            uint256 poolB;
            uint256 fee;
            for (uint256 i = 0; i < n; i++) {
                pool = IKlaytnFactory(ksp).tokenToPool(nowToken, path[i]);
                tokenA = IKlaytnExchange(pool).tokenA();
                (poolA, poolB) = IKlaytnExchange(pool).getCurrentPool();
                fee = IKlaytnExchange(pool).fee();
                if (tokenA == path[i]) {
                    cachePoolIn[i] = poolB;
                    cachePoolOut[i] = poolA;
                } else {
                    cachePoolIn[i] = poolA;
                    cachePoolOut[i] = poolB;
                }
                cacheFee[i] = fee;
                nowToken = path[i];
            }
            pool = IKlaytnFactory(ksp).tokenToPool(nowToken, tokenOut);
            tokenA = IKlaytnExchange(pool).tokenA();
            (poolA, poolB) = IKlaytnExchange(pool).getCurrentPool();
            fee = IKlaytnExchange(pool).fee();
            if (tokenA == tokenOut) {
                cachePoolIn[n] = poolB;
                cachePoolOut[n] = poolA;
            } else {
                cachePoolIn[n] = poolA;
                cachePoolOut[n] = poolB;
            }
            cacheFee[n] = fee;
        }

        uint256 amountIn = amountIn_;
        uint256 priceAfter = RAY;
        for (uint256 i = 0; i < n + 1; i++) {
            uint256 poolIn = cachePoolIn[i];
            uint256 poolOut = cachePoolOut[i];
            uint256 fee = cacheFee[i];

            uint256 num = poolOut * amountIn * (10000 - fee);
            uint256 den = poolIn * 10000 + amountIn * (10000 - fee);
            uint256 amountOut = num / den;

            // XXX: for whitelisted pools, use `amountIn * (10000 - fee)`
            poolIn = poolIn + amountIn;
            poolOut = poolOut - amountOut;

            // update
            amountIn = amountOut;
            priceAfter = (priceAfter * poolOut * (10000 - fee)) / poolIn / 10000;
        }
        return priceAfter;
    }

    // returns amountIn of the maximum swap
    function maxSwap(
        bytes32 colType,
        uint256 maxAmountIn,
        uint256 minPrice // ray
    ) internal view returns (uint256) {
        address tokenIn = addressBook.getCollateralContract(colType, bytes32("token"));
        address tokenOut = addressBook.ksd();
        address[] memory path = takerInfos[colType].swapPath;
        uint256 n = path.length;

        // Caching
        uint256[] memory cachePoolIn = new uint256[](n + 1);
        uint256[] memory cachePoolOut = new uint256[](n + 1);
        uint256[] memory cacheFee = new uint256[](n + 1);

        {
            address ksp = addressBook.ksp();
            address nowToken = tokenIn;
            address pool;
            address tokenA;
            uint256 poolA;
            uint256 poolB;
            uint256 fee;
            for (uint256 i = 0; i < n; i++) {
                pool = IKlaytnFactory(ksp).tokenToPool(nowToken, path[i]);
                tokenA = IKlaytnExchange(pool).tokenA();
                (poolA, poolB) = IKlaytnExchange(pool).getCurrentPool();
                fee = IKlaytnExchange(pool).fee();
                if (tokenA == path[i]) {
                    cachePoolIn[i] = poolB;
                    cachePoolOut[i] = poolA;
                } else {
                    cachePoolIn[i] = poolA;
                    cachePoolOut[i] = poolB;
                }
                cacheFee[i] = fee;
                nowToken = path[i];
            }
            pool = IKlaytnFactory(ksp).tokenToPool(nowToken, tokenOut);
            tokenA = IKlaytnExchange(pool).tokenA();
            (poolA, poolB) = IKlaytnExchange(pool).getCurrentPool();
            fee = IKlaytnExchange(pool).fee();
            if (tokenA == tokenOut) {
                cachePoolIn[n] = poolB;
                cachePoolOut[n] = poolA;
            } else {
                cachePoolIn[n] = poolA;
                cachePoolOut[n] = poolB;
            }
            cacheFee[n] = fee;
        }

        // Binary search
        uint256 high = maxAmountIn;
        uint256 low = 0;
        while (high > low) {
            uint256 mid = (high + low) / 2;
            // Get price after swap
            uint256 amountIn = mid;
            uint256 priceAfter = RAY;
            for (uint256 i = 0; i < n + 1; i++) {
                uint256 poolIn = cachePoolIn[i];
                uint256 poolOut = cachePoolOut[i];
                uint256 fee = cacheFee[i];

                uint256 num = poolOut * amountIn * (10000 - fee);
                uint256 den = poolIn * 10000 + amountIn * (10000 - fee);
                uint256 amountOut = num / den;

                // XXX: for whitelisted pools, use `amountIn * (10000 - fee)`
                poolIn = poolIn + amountIn;
                poolOut = poolOut - amountOut;

                // update
                amountIn = amountOut;
                priceAfter = ((priceAfter * poolOut) / poolIn / 10000) * (10000 - fee);
            }
            if (priceAfter > minPrice) {
                low = mid + 1;
            } else {
                high = mid;
            }
        }

        return high;
    }

    function min(uint256 x, uint256 y) internal pure returns (uint256) {
        if (x < y) {
            return x;
        } else {
            return y;
        }
    }

    function sqrt(uint256 x) internal pure returns (uint256 y) {
        uint256 z = (x + 1) / 2;
        y = x;
        while (z < y) {
            y = z;
            z = (x / z + z) / 2;
        }
    }

    fallback() external payable {}

    receive() external payable {}
}
