pragma solidity 0.8.3;

interface ILedger {
    // --- Events ---
    event CollateralChanged(bytes32 collateralType, address usr, int256 amount);
    event KSDChanged(address usr, int256 amount);
    event LoanChanged(bytes32 collateralType, address debtor, address collateralVault, address ksdVault, int256 collateralAmount, int256 loanAmount);
    event AccountChanged(bytes32 collateralType, address vault, uint256 lockedCollateral, uint256 loan);

    /// @notice msg.sender가 usr에게 자신의 vault를 조작할 수 있는 권한을 준다.
    /// @notice authorize usr to manipulate msg.sender's vault
    function allow(address usr) external;

    /// @notice msg.sender가 usr에게 자신의 vault를 조작할 수 있는 권한을 뻇는.
    /// @notice Deauthorize usr from manipulating msg.sender's vault
    function disallow(address usr) external;

    // --- Data view ---
    /// @notice 담보물 관련 정보
    /// @param collateralType 담보물 타입.
    /// @dev 리턴 값은 순서대로
    /// 1. totalLockedCollateral (전체 locked된 담보물 양)
    /// 2. totalLoan (발행된(빌린) KSD양)
    /// 3. rate (시간에 따라 점차 증가하고, accountInfo.loan 에 곱해져서 사용자가 빌린 ksd의 양을 늘리는 용도로 사용됨. stability fee)
    /// 4. spot (== price * LTV)
    /// 5. line (담보 타입에서 빌릴 수 있는 총 KSD)
    /// 6. dust (하나의 vault에서 빌릴 수 있는 최저의 ksd 양)
    /// (eng)
    /// @notice Collateral Iinfo
    /// @param collateralType Type of collateral - initially, there's only one (Klay)
    /// @dev the value that it returns are
    /// 1. totalLockedCollateral
    /// 2. totalLoan
    /// 3. rate (It gradually increases over time and is multiplied by accountInfo.loan to increase the amount of ksd borrowed by the user.. stability fee)
    /// 4. spot (== price * LTV)
    /// 5. line (Maximum KSD that can be minted from given collateral type)
    /// 6. dust (Minimum KSD that should be borrowed from a CDP)
    function collateralInfo(bytes32 collateralType)
        external
        view
        returns (
            uint256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256
        );

    /// @notice 담보물 관련 정보들 조회
    /// @param collateralTypes 담보물 타입 list
    /// @dev collateralInfo 의 값들을 array 로 return
    function collateralInfos(bytes32[] calldata collateralTypes)
        external
        view
        returns (
            uint256[] memory,
            uint256[] memory,
            uint256[] memory,
            uint256[] memory,
            uint256[] memory,
            uint256[] memory
        );

    /// @notice usr의 vault 정보
    /// @param collateralType 담보 종류
    /// @param usr 유저의 address
    /// @dev 리턴 값은 순서대로
    /// 1. lockedCollateral (vault에 잠긴 담보물 양)
    /// 2. loan (vault에서 빌린 ksd 양)
    /// (eng)
    /// @notice Information of a user's vault
    /// @param collateralType type of collateral
    /// @param usr Address of the user
    /// @dev returns
    /// 1. lockedCollateral (amount of collateral locked in the user's vault)
    /// 2. loan (normalized amount of KSD minted from that vault)
    function accountInfo(bytes32 collateralType, address usr) external view returns (uint256, uint256);

    /// @notice This function returns the index of a user of given collateral type
    ///         The index
    /// @param collateralType type of collateral
    /// @param usr address of user
    /// @dev 담보물 타입마다 cdp 연 유저를 tracking 하기 위한 것으로, 각 유저가 몇 번째로 cdp를 열었는지를 기록.
    /// @dev This function is designed to track the list of users who have opened a CDP (for each collateral type)
    ///       Since CDPs are never deleted once created, this index should not be update at any circumstances
    function accountIndex(bytes32 collateralType, address usr) external view returns (uint256);

    /// @notice access address of an account by index
    /// @param collateralType type of collateral
    /// @param index index of user
    /// @dev index 번째로 들어온 유저의 주소를 리턴
    /// @dev returns the address of {index}-th CDP opener
    function accountByIndex(bytes32 collateralType, uint256 index) external view returns (address);

    /// @notice Total number of user who have opened a CDP (given collateral type) - always increasing
    /// @param collateralType type of collateral
    /// @dev 지금까지 cdp를 연 유저의 수
    function accountLength(bytes32 collateralType) external view returns (uint256);

    /// @notice locked 되지 않은 담보물의 양. 단, 실제 담보물은 gateway 컨트렉트가 관리하고 있음.
    /// @param collateralType type of collateral
    /// @param usr address of user
    /// @notice The amount of collateral that are not locked.
    function token(bytes32 collateralType, address usr) external view returns (uint256);

    /// @notice 빌리거나, ksdGateway를 통해서 들어온 ksd의 양.
    /// @param usr address of user
    /// @notice The amount of KSD lent to, or depositted by (through KSDGateway) the {usr}
    function ksd(address usr) external view returns (uint256);

    /// @notice total KSD Issued [rad]
    function totalIssued() external view returns (uint256);

    /// @notice Total Unbacked KSD [rad]
    function vice() external view returns (uint256);

    /// @notice Total Debt Ceiling [rad]
    function line() external view returns (uint256);

    /// @notice unbacked KSD [rad]
    function sin(address usr) external view returns (uint256);

    /// @notice initialize collateral
    /// @param collateralType type of collateral to initialize.
    /// 오직 거버넌스에 의해서만 불려야 한다
    /// Should only be called by the governance
    function init(bytes32 collateralType) external;

    /// @notice config something
    /// 오직 거버넌스와 오라클 의해서만 불려야 한다
    /// Should only be called by the governance and oracle
    function config(
        bytes32 collateralType,
        bytes32 what,
        uint256 data
    ) external;

    /// @notice token(lock 되지 않은 collateral)의 양을 변화시킨다.
    ///          장부의 기록된 token의 양을 변화시기키 때문에 무조건 허가된 contract에서만 불려야 한다.
    /// (eng)
    /// @notice Increase the amount of 'unlocked collateral'
    ///          (which means that the token is deposited into CDP but not yet considered as 'locked' as collateral)
    ///         This function changes the amount of 'unlocked collateral' of the CDP, so it should only be called by authorized contracts.
    function addCollateral(
        bytes32 collateralType,
        address usr,
        int256 amount
    ) external;

    /// @notice token(lock 되지 않은 collateral)을 다른 account에 보내는 function.
    function sendCollateral(
        bytes32 collateralType,
        address src,
        address dst,
        uint256 amoount
    ) external;

    /// @notice ksd를 다른 account에 보내는 function. (실제 ksd token을 보내는 것이 아니라, 장부 상의 기록만 바뀜)
    function sendKsd(
        address src,
        address dst,
        uint256 amount
    ) external;

    /// @notice token, ksd 양을 통해서 vault의 값을 변화시킴.
    /// @param collateralType type of collateral
    /// @param debtor owner of vault
    /// @param collateralVault (네이밍이 햇갈리기는 한데, 위 token의 owner) 담보물을 가져오거나 vault에서 unlock 시킨 담보물을 넣을 주소
    /// @param ksdVault (네이밍이 햇갈리기는 한데, 위 ksd의 owner) ksd를 vault에 넣어주거나, vault에서 빌린 ksd를 넣어줄 곳
    /// @param collateralAmount amount of collateral to change
    /// @param loanAmount amount of KSD to change
    /// @dev vault에 ksd를 갚거나/빌리거나, 담보물을 더 lock 시키거나/빼거나
    /// 단, vault는 항상 safe 해야하고, dust 이상의 ksd를 유지해야 한다.
    /// 상식적인 선에서 권한 관리가 필요하다. 예를들어, 허락받지 않은 사람이 locked되지 않은 남의 collateral를 자신의 vault로 lock 시키지 못해야 한다.
    function actionLoan(
        bytes32 collateralType,
        address debtor,
        address collateralVault,
        address ksdVault,
        int256 collateralAmount,
        int256 loanAmount
    ) external;

    /// @notice vault에 잠긴 collateral양이나 ksd 부채를 이동시킨다.
    /// @param collateralType type of collateral
    /// @param src address of vault (accountInfo)
    /// @param dst address of vault (accountInfo)
    /// @param collateralAmount amount of collateral to change
    /// @param loanAmount amount of KSD to change
    /// @dev 상식적인 선에서 권한 관리가 필요하다.
    function fork(
        bytes32 collateralType,
        address src,
        address dst,
        int256 collateralAmount,
        int256 loanAmount
    ) external;

    /// @notice 담보물 몰수 (confiscation)
    /// @param collateralType type of collateral
    /// @param debtor 청산될 vault 주소
    /// @param collateralVault vault에서 locked된 담보물을 몰수해갈 주체. (이 서비스에서는 항상 auctionHouse. auction에서 팔아야 하기 때문)
    /// @param ksdVault vault에서 빌려간 ksd는 담보물이 auction에 들어간 순간 backing되지 않은 ksd이기 때문에, 이것을 맡아줄 주체가 필요. 이 서비스에서는 minusBank가 맡아준다.
    /// @param collateralAmount amount of collateral to change
    /// @param loanAmount amount of KSD to change
    /// @dev liquidator의 liquidate에서만 불림
    function grab(
        bytes32 collateralType,
        address debtor,
        address collateralVault,
        address ksdVault,
        int256 collateralAmount,
        int256 loanAmount
    ) external;

    /// @notice backing되지 않은 ksd를 상쇄하는 function. msg.sender의 ksd를 소모한다.
    function heal(uint256 amount) external;

    /// @notice backing되지 않은 ksd를 만들어서 msg.sender에게 준다.
    /// @dev minusBank에서 keeper들에게 incentive를 주기 위해서, 미리 ksd를 대출받는 역할로 쓰인다.
    /// 따라서, 오직 minusBank에서만 불려야 한다.
    function suck(
        address u,
        address v,
        uint256 amount
    ) external;

    /// @notice stability fee를 ksdReceiver에게 준다.
    /// @dev StabilityFeeCollector에서만 불려야 한다.
    function collectStabilityFee(
        bytes32 collateralType,
        address ksdReceiver,
        int256 rateDiff
    ) external;
}
